﻿using Aura.Common.Extensions;
using Aura.Database.Context.Stock;
using Microsoft.EntityFrameworkCore;
using System.Linq;

namespace Aura.Manager.Stock.Reports
{
    internal class ActualUsageCalculator
    {
        internal static async Task<ActualStockItemUsage> GetActualUsageForItemAsync(
            ReadOnlyStockContext readOnlyStockContext, 
            StockTake fromStockTake,
            StockTake toStockTake,
            Item item, 
            decimal? openingBalance, 
            decimal? closingBalance, 
            decimal wastagePercentage, 
            FifoCalculator fifoCalculator)
        {
            var actualUsage = new ActualStockItemUsage()
            {
                ItemId = item.Id,
//                    GrossProfitReportId = report.Id,
                ValuesUpdatedAt = DateTime.UtcNow,
            };

            var grvs = await readOnlyStockContext.Grvs
                .Include(x => x.GrvItems)
                .ThenInclude(gi => gi.StockMovements)
                .OrderBy(x => x.StockMovementAt)
                .Where(x =>
                    x.StoreId == fromStockTake.StoreId &&
                    x.GrvItems.Any(i => i.ItemId == item.Id) &&
                    x.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized &&
                    (x.StockMovementAt >= fromStockTake.DateTime && x.StockMovementAt <= toStockTake.DateTime))
                .ToListAsync();

            var balance = openingBalance ?? 0M;
            var purchases = 0M;

            foreach (var grv in grvs)
            {
                var grvItem = grv.GrvItems.First(x => x.ItemId == item.Id);

                var actualReceivedQuantity = grvItem.StockMovements.Sum(sm => sm.Quantity);
                var actualCost = grvItem.TotalCostExcl.ProRata(grvItem.QuantityGrv, actualReceivedQuantity);

                var quantityWithWastage = actualReceivedQuantity * (100M - wastagePercentage) / 100M;

                if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
                {
                    balance += quantityWithWastage;
                    purchases += actualCost;
                }
                else
                {
                    balance -= quantityWithWastage;
                    purchases -= actualCost;
                }
            }

            if (openingBalance.HasValue && closingBalance.HasValue)
            {
                actualUsage.ActualUsage = balance - closingBalance.Value;

                actualUsage.Fifocost =
                    (await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, openingBalance.Value, fromStockTake, grvs, null, wastagePercentage) ?? 0M) +
                    purchases -
                    (await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, closingBalance.Value, toStockTake, grvs, null, wastagePercentage) ?? 0M);
            }

            return actualUsage;
        }
    }
}

