    21→        public async Task<GrossProfitReportResponse> GetGrossProfitReportAsync(string storeId, int fromStockTakeId, int toStockTakeId, bool useModel, int[]? stockListIds)
    22→        {
    23→            if (fromStockTakeId == toStockTakeId)
    24→            {
    25→                throw new ApiException(System.Net.HttpStatusCode.BadRequest, "You cannot specify the same stock take for both the start and end of the report.");
    26→            }
