    31→            if (openingStockTake == null || openingStockTake.StoreId.ToUpperInvariant() != storeId)
    32→            {
    33→                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Opening stock take {fromStockTakeId} was not found for store id {storeId}");
    34→            }
