    50→            await stockContext.SaveChangesAsync();
    51→            stockContext.Entry(report).State = EntityState.Detached;
    52→            readOnlyStockContext.GrossProfitReports.Attach(report);
    53→            report.FromStockTake = openingStockTake;
    54→            report.ToStockTake = closingStockTake;
    55→            readOnlyStockContext.Entry(report).State = EntityState.Unchanged;
    56→
    57→            var itemIds = openingStockTake
    58→                .StockTakeItems.Select(x => x.ItemId)
    59→                .Union(closingStockTake.StockTakeItems.Select(x => x.ItemId))
    60→                .Distinct()
    61→                .ToList();
    62→
    63→            if (stockListIds.OrEmptyIfNull().Any())
