    66→            }
    67→
    68→            await Parallel.ForEachAsync(itemIds, async (itemId, cancel) =>
    69→            {
    70→                var scope = scopeFactory.CreateScope();
    71→                var readOnlyStockContext = scope.ServiceProvider.GetRequiredService<ReadOnlyStockContext>();
    72→                var stockContext = scope.ServiceProvider.GetRequiredService<StockContext>();
    73→
    74→                var item = await GetItemAsync(itemId);
    75→
    76→                var storeWastagePercentage = await GetStoreWastagePercentageAsync(itemId, storeId);
    77→
