    79→                var hasClosing = closingStockTake.StockTakeItems.Any(x => x.ItemId == item.Id);
    80→
    81→                if (hasOpening && hasClosing)
    82→                {
    83→                    var openingBulk = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
    84→                    var openingPrepped = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
