   551→                var grvUnits = actualReceivedQuantity * ((100M - wastagePercent) / 100M);
   552→                var unitCost = grvUnits > 0 ? actualTotalCost / grvUnits : 0;
   553→                
   554→                if (receivedUnits + grvUnits > unitsRequired)
   555→                {
   556→                    grvUnits = unitsRequired - receivedUnits;
   557→                }
   558→
   559→                if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
   560→                {
   561→                    receivedUnits += grvUnits;
   562→                }
   563→                else // This will include Returns and Short Delivered
   564→                {
   565→                    receivedUnits -= grvUnits;
   566→                }
   567→
   568→                grvBalance = new StockTakeWindowGrvBalance()
   569→                {
   570→                    StockTakeWindowId = window.Id,
   571→                    GrvId = grv.Id,
   572→                    GrvItemId = grvItem.Id,
   573→                    OpeningUnitQuantity = grvUnits * (grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv ? -1M : 1M),
   574→                    UnitCost = unitCost,
   575→                    RemainingUnitQuantity = grvUnits * (grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv ? -1M : 1M),
   576→                };
   577→                await stockContext.StockTakeWindowGrvBalances.AddAsync(grvBalance);
   578→                grvBalances.Add(grvBalance);
   579→
   580→                if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
   581→                {
   582→                    var pendingReturns = grvBalances
   583→                        .Where(x => x.RemainingUnitQuantity < 0 && x.Id < grvBalance.Id);
   584→
   585→                    foreach (var pendingReturn in pendingReturns)
   586→                    {
   587→                        var availableGrvs = grvBalances
   588→                            .Where(x => x.RemainingUnitQuantity > 0 && x.Id > pendingReturn.Id);
   589→
   590→                        var required = -pendingReturn.RemainingUnitQuantity;
   591→
   592→                        foreach (var availableGrv in availableGrvs)
   593→                        {
   594→                            var amount = Math.Min(required, availableGrv.RemainingUnitQuantity);
   595→                            availableGrv.RemainingUnitQuantity -= amount;
   596→                            pendingReturn.RemainingUnitQuantity += amount;
   597→                            required -= amount;
   598→
   599→                            if (required == 0)
   600→                            {
   601→                                break;
   602→                            }
   603→                        }
   604→
   605→                        await stockContext.SaveChangesAsync();
   606→                    }
   607→                }
   608→
   609→                if (receivedUnits >= unitsRequired)
   610→                {
   611→                    var excess = receivedUnits - unitsRequired;
   612→                    grvBalance.RemainingUnitQuantity -= excess;
   613→                    grvBalance.OpeningUnitQuantity -= excess;
   614→
   615→                    await stockContext.SaveChangesAsync();
   616→
   617→                    break;
   618→                }
   619→            }
   620→
   621→            if (grvBalance != null)
   622→            {
   623→                grvBalance.OpeningUnitQuantity += unitsRequired - receivedUnits;
   624→                grvBalance.RemainingUnitQuantity += unitsRequired - receivedUnits;
   625→
   626→                await stockContext.SaveChangesAsync();
   627→            }
