﻿using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.Extensions;
using Aura.Database.Context.Stock;
using AuraCloudInterface.Stock;
using AuraCloudInterface.Stock.GrossProfitReport;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.DependencyInjection;
using System.Collections.Concurrent;

namespace Aura.Manager.Stock.Reports
{
    public class GrossProfitReportManager(
            StockContext stockContext,
            ReadOnlyStockContext readOnlyStockContext,
            StoreManager storeManager,
            TheoreticalSalesReportManager theoreticalSalesReportManager,
            FifoCalculator fifoCalculator,
            IServiceScopeFactory scopeFactory)
    {
        public async Task<GrossProfitReportResponse> GetGrossProfitReportAsync(string storeId, int fromStockTakeId, int toStockTakeId, bool useModel, int[]? stockListIds)
        {
            if (fromStockTakeId == toStockTakeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.BadRequest, "You cannot specify the same stock take for both the start and end of the report.");
            }

            storeId = storeId.ToUpperInvariant();

            var openingStockTake = await readOnlyStockContext.StockTakes.AsNoTracking().Include(x => x.StockTakeItems).ThenInclude(x => x.StockTakeItemSubCounts).FirstOrDefaultAsync(x => x.Id == fromStockTakeId);
            if (openingStockTake == null || openingStockTake.StoreId.ToUpperInvariant() != storeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Opening stock take {fromStockTakeId} was not found for store id {storeId}");
            }
            var closingStockTake = await readOnlyStockContext.StockTakes.AsNoTracking().Include(x => x.StockTakeItems).ThenInclude(x => x.StockTakeItemSubCounts).FirstOrDefaultAsync(x => x.Id == toStockTakeId);
            if (closingStockTake == null || closingStockTake.StoreId.ToUpperInvariant() != storeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Closing stock take {toStockTakeId} was not found for store id {storeId}");
            }

            var store = await storeManager.ViewStoreSummaryAsync(storeId);

            var report = new GrossProfitReport()
            {
                StoreId = storeId,
                FromStockTakeId = fromStockTakeId,
                ToStockTakeId = toStockTakeId,
            };
            await stockContext.GrossProfitReports.AddAsync(report);
            await stockContext.SaveChangesAsync();
            stockContext.Entry(report).State = EntityState.Detached;
            readOnlyStockContext.GrossProfitReports.Attach(report);
            report.FromStockTake = openingStockTake;
            report.ToStockTake = closingStockTake;
            readOnlyStockContext.Entry(report).State = EntityState.Unchanged;

            var itemIds = openingStockTake
                .StockTakeItems.Select(x => x.ItemId)
                .Union(closingStockTake.StockTakeItems.Select(x => x.ItemId))
                .Distinct()
                .ToList();

            if (stockListIds.OrEmptyIfNull().Any())
            {
                itemIds = itemIds.Where(x => readOnlyStockContext.StockListItems.Where(x => stockListIds.Contains(x.StockListId) && !x.StockList.IsArchived).Select(x => x.ItemId).Contains(x)).ToList();
            }

            await Parallel.ForEachAsync(itemIds, async (itemId, cancel) =>
            {
                var scope = scopeFactory.CreateScope();
                var readOnlyStockContext = scope.ServiceProvider.GetRequiredService<ReadOnlyStockContext>();
                var stockContext = scope.ServiceProvider.GetRequiredService<StockContext>();

                var item = await GetItemAsync(itemId);

                var storeWastagePercentage = await GetStoreWastagePercentageAsync(itemId, storeId);

                var hasOpening = openingStockTake.StockTakeItems.Any(x => x.ItemId == item.Id);
                var hasClosing = closingStockTake.StockTakeItems.Any(x => x.ItemId == item.Id);

                if (hasOpening && hasClosing)
                {
                    var openingBulk = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                    var openingPrepped = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                    var openingStockOnHand = openingPrepped + openingBulk * (100 - storeWastagePercentage) / 100M;

                    var openingWindow = await GetStockTakeWindowAsync(stockContext, readOnlyStockContext, report.Id, openingStockTake, item, openingStockOnHand, storeWastagePercentage);

                    var closingBulk = Convert.ToDecimal(closingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                    var closingPrepped = Convert.ToDecimal(closingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                    var closingStockOnHand = closingPrepped + closingBulk * (100 - storeWastagePercentage) / 100M;

                    var closingWindow = await GetStockTakeWindowAsync(stockContext, readOnlyStockContext, report.Id, closingStockTake, item, closingStockOnHand, storeWastagePercentage);

                    var actualUsage = await ActualUsageCalculator.GetActualUsageForItemAsync(readOnlyStockContext, report.FromStockTake, report.ToStockTake, item, openingStockOnHand, closingStockOnHand, storeWastagePercentage, fifoCalculator);
                    actualUsage.GrossProfitReportId = report.Id;

                    var openingUnits = openingPrepped + openingBulk * (100M - storeWastagePercentage) / 100M;
                    var closingUnits = closingPrepped + closingBulk * (100M - storeWastagePercentage) / 100M;

                    actualUsage.OpeningUnits = openingUnits;
                    actualUsage.ClosingUnits = closingUnits;

                    await stockContext.ActualStockItemUsages.AddAsync(actualUsage);
                    await stockContext.SaveChangesAsync();
                }
                else
                {
                    if (hasOpening)
                    {
                        var openingBulk = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                        var openingPrepped = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                        var openingStockOnHand = openingPrepped + openingBulk * (100 - storeWastagePercentage) / 100M;
                        var openingWindow = await GetStockTakeWindowAsync(stockContext, readOnlyStockContext, report.Id, openingStockTake, item, openingStockOnHand, storeWastagePercentage);
                        var openingUnits = openingPrepped + openingBulk * (100M - storeWastagePercentage) / 100M;

                        var actualUsage = await ActualUsageCalculator.GetActualUsageForItemAsync(readOnlyStockContext, report.FromStockTake, report.ToStockTake, item, openingStockOnHand, null, storeWastagePercentage, fifoCalculator);

                        actualUsage.GrossProfitReportId = report.Id;
                        actualUsage.OpeningUnits = openingUnits;

                        await stockContext.ActualStockItemUsages.AddAsync(actualUsage);
                        await stockContext.SaveChangesAsync();
                    }
                    else if (hasClosing)
                    {
                        var closingBulk = Convert.ToDecimal(closingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                        var closingPrepped = Convert.ToDecimal(closingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                        var closingStockOnHand = closingPrepped + closingBulk * (100 - storeWastagePercentage) / 100M;
                        var closingWindow = await GetStockTakeWindowAsync(stockContext, readOnlyStockContext, report.Id, closingStockTake, item, closingStockOnHand, storeWastagePercentage);
                        var closingUnits = closingPrepped + closingBulk * (100M - storeWastagePercentage) / 100M;

                        var actualUsage = await ActualUsageCalculator.GetActualUsageForItemAsync(readOnlyStockContext, report.FromStockTake, report.ToStockTake, item, null, closingStockOnHand, storeWastagePercentage, fifoCalculator);

                        actualUsage.GrossProfitReportId = report.Id;
                        actualUsage.ClosingUnits = closingUnits;

                        await stockContext.ActualStockItemUsages.AddAsync(actualUsage);
                        await stockContext.SaveChangesAsync();
                    }
                }
            });

            if (stockListIds.OrEmptyIfNull().Any())
            {
                itemIds = itemIds.Where(x => readOnlyStockContext.StockListItems.Where(x => stockListIds.Contains(x.StockListId) && !x.StockList.IsArchived).Select(x => x.ItemId).Contains(x)).ToList();
            }

            var theoreticalWindow = await theoreticalSalesReportManager.GetTheoreticalUsageAsync(store.BrandId, storeId, openingStockTake, closingStockTake.DateTime, itemIds, useModel);

            return await GenerateGrossProfitResponseAsync(storeId, openingStockTake, closingStockTake, report, theoreticalWindow, itemIds, useModel);
        }

        Dictionary<int, decimal> storeWastageCache = new Dictionary<int, decimal>();
        Dictionary<int, decimal> modelWastageCache = new Dictionary<int, decimal>();

        async Task<decimal> GetStoreWastagePercentageAsync(int itemId, string storeId)
        {
            if (storeWastageCache.TryGetValue(itemId, out var wastagePercentage))
            {
                return wastagePercentage;
            }

            wastagePercentage = await GetModelWastagePercentageAsync(itemId);

            var readOnlyStockContext = scopeFactory.CreateScope().ServiceProvider.GetRequiredService<ReadOnlyStockContext>();
            var storeOverride = readOnlyStockContext.ItemDetailStoreOverrides.FirstOrDefault(x => x.StoreId == storeId && x.ItemId == itemId);
            if (storeOverride?.WastagePercentage.HasValue ?? false)
            {
                wastagePercentage = storeOverride.WastagePercentage.Value;
            }

            storeWastageCache[itemId] = wastagePercentage;

            return wastagePercentage;
        }

        async Task<decimal> GetModelWastagePercentageAsync(int itemId)
        {
            if (modelWastageCache.TryGetValue(itemId, out var wastagePercentage))
            {
                return wastagePercentage;
            }

            var item = await GetItemAsync(itemId);
            wastagePercentage = item.WastagePercentage;

            modelWastageCache[itemId] = wastagePercentage;

            return wastagePercentage;
        }

        Dictionary<int, Item> itemCache = new Dictionary<int, Item>();
        async Task<Item> GetItemAsync(int itemId)
        {
            if (!itemCache.TryGetValue(itemId, out var item))
            {
                var readOnlyStockContext = scopeFactory.CreateScope().ServiceProvider.GetRequiredService<ReadOnlyStockContext>();
                item = await readOnlyStockContext.Items.FindAsync(itemId);
                itemCache[itemId] = item;
            }

            return item;
        }

        private async Task<GrossProfitReportResponse> GenerateGrossProfitResponseAsync(
            string storeId, 
            StockTake openingStockTake, 
            StockTake closingStockTake, 
            GrossProfitReport report, 
            TheoreticalUsageInfo? theoreticalWindow, 
            List<int> itemIds, 
            bool useModelWastage)
        {
            var inventoryByStockItem = new ConcurrentDictionary<int, Inventory>();

            var actualUsages = await stockContext.ActualStockItemUsages
                .Include(x => x.Item)
                .ThenInclude(x => x.Category)
                .Include(x => x.Item)
                .ThenInclude(x => x.ItemDetailStoreOverrides)
                .Include(x => x.Item)
                .ThenInclude(x => x.StockType)
                .Include(x => x.Item)
                .ThenInclude(x => x.UnitOfMeasure)
                .Where(x => x.GrossProfitReport.FromStockTakeId == openingStockTake.Id && x.GrossProfitReport.ToStockTakeId == closingStockTake.Id && itemIds.Contains(x.ItemId))
                .ToListAsync();

            var storeReceives = await readOnlyStockContext.GrvItems
                .Include(x => x.Grv)
                .Include(x => x.StockMovements)
                .Where(x =>
                    x.Grv.StoreId == storeId &&
                    x.ItemId.HasValue &&
                    itemIds.Contains(x.ItemId.Value) &&
                    x.Grv.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized &&
                    (x.Grv.StockMovementAt >= openingStockTake.DateTime && x.Grv.StockMovementAt < closingStockTake.DateTime))
                .ToListAsync();

            await Parallel.ForEachAsync(actualUsages, async (actual, cancel) =>
            {
                var scope = scopeFactory.CreateScope();
                var stockContext = scope.ServiceProvider.GetRequiredService<StockContext>();
                var readOnlyStockContext = scope.ServiceProvider.GetRequiredService<ReadOnlyStockContext>();

                var storeWastagePercentage = await GetStoreWastagePercentageAsync(actual.ItemId, storeId);
                var receives = storeReceives.Where(x => x.ItemId == actual.ItemId);

                var purchases = receives.Where(x => x.Grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv);
                var returns = receives.Where(x => x.Grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv);

                var totalPurchases = purchases.Sum(x => x.TotalCostExcl.ProRata(x.QuantityGrv, x.StockMovements.Sum(sm => sm.Quantity)));
                var totalReturns = returns.Sum(x => x.TotalCostExcl.ProRata(x.QuantityGrv, x.StockMovements.Sum(sm => sm.Quantity)));

                var hasActual = actual.ClosingUnits.HasValue && actual.OpeningUnits.HasValue;
                var purchasesUnits = purchases.Sum(x => x.StockMovements.Sum(sm => sm.Quantity));

                inventoryByStockItem[actual.ItemId] = new Inventory()
                {
                    ItemId = actual.ItemId,
                    CategoryName = actual.Item.Category?.Name,
                    ItemName = actual.Item.Name,
                    ItemCode = actual.Item.Code,
                    StockType = actual.Item.StockType?.Name ?? "Not set",
                    ItemStatus = actual.Item.IsArchived ? "Inactive" : "Active",
                    ItemDescription = actual.Item.Description,
                    UnitOfMeasure = actual.Item.UnitOfMeasure?.Name,
                    OpeningUnits = actual.OpeningUnits,
                    PurchasesUnits = purchasesUnits * (100M - storeWastagePercentage) / 100M,
                    ClosingUnits = actual.ClosingUnits,
                    ActualUnits = hasActual ? actual.ActualUsage : null,
                    OpeningValue = actual.OpeningUnits.HasValue ? await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, actual.Item, actual.OpeningUnits.Value, openingStockTake, [], report, storeWastagePercentage) : null,
                    ClosingValue = actual.ClosingUnits.HasValue ? await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, actual.Item, actual.ClosingUnits.Value, closingStockTake, receives.Select(x => x.Grv).Distinct().ToList(), report, storeWastagePercentage) : null,
                    ActualCostOfSalesValue = hasActual ? actual.Fifocost : null,
                    PurchasesValue = totalPurchases - totalReturns,
                    ModelWastagePercentage = actual.Item.WastagePercentage,
                    StoreWastagePercentage = storeWastagePercentage,
                };
            });

            if (theoreticalWindow != null)
            {
                foreach (var theoreticalUsagePeriod in theoreticalWindow.Periods)
                {
                    foreach (var stockUsage in theoreticalUsagePeriod.Value.UsageByStockItemId)
                    {
                        if (itemIds.Any())
                        {
                            if (!itemIds.Contains(stockUsage.Key))
                            {
                                continue;
                            }
                        }

                        var itemUsage = stockUsage.Value;

                        if (!inventoryByStockItem.TryGetValue(itemUsage.ItemId, out var inventory))
                        {
                            var item = await GetItemAsync(itemUsage.ItemId);
                            var storeWastagePercentage = await GetStoreWastagePercentageAsync(itemUsage.ItemId, storeId);
                            var modelWastagePercentage = item.WastagePercentage;

                            inventory = new Inventory()
                            {
                                ItemId = itemUsage.ItemId,
                                CategoryName = item.Category?.Name,
                                ItemName = item.Name,
                                ItemCode = item.Code,
                                StockType = item.StockType?.Name ?? "Not set",
                                ItemStatus = item.IsArchived ? "Inactive" : "Active",
                                ItemDescription = item.Description,
                                UnitOfMeasure = item.UnitOfMeasure?.Name,
                                TheoreticalCostOfSalesValue = itemUsage.Fifocost,
                                TheoreticalUnits = itemUsage.UnitQuantityUsed,
                                ModelWastagePercentage = item.WastagePercentage,
                                StoreWastagePercentage = storeWastagePercentage,
                            };
                        }
                        else
                        {
                            if (inventory.TheoreticalCostOfSalesValue == null)
                            {
                                inventory.TheoreticalCostOfSalesValue = itemUsage.Fifocost;
                            }
                            else
                            {
                                inventory.TheoreticalCostOfSalesValue += itemUsage.Fifocost;
                            }
                            if (inventory.TheoreticalUnits == null)
                            {
                                inventory.TheoreticalUnits = itemUsage.UnitQuantityUsed;
                            }
                            else
                            {
                                inventory.TheoreticalUnits += itemUsage.UnitQuantityUsed;
                            }
                        }
                        inventoryByStockItem[itemUsage.ItemId] = inventory;
                    }
                }

                var theoreticalItemIds = new List<int>();

                foreach (var theoreticalUsagePeriod in theoreticalWindow.Periods)
                {
                    foreach (var stockUsage in theoreticalUsagePeriod.Value.SalesByMenuItemId)
                    {
                        var menuItemUsage = stockUsage.Value;

                        var stockItemUsage = await GetRecipeForMenuItemAsync(theoreticalUsagePeriod.Value.RecipeRevisionId, menuItemUsage.MenuItemId, MenuItemRecipeType.EatIn);

                        foreach (var stockItemId in stockItemUsage.Keys)
                        {
                            if (itemIds.Any())
                            {
                                if (!itemIds.Contains(stockItemId))
                                {
                                    continue;
                                }
                            }

                            var quantityUsed = stockItemUsage[stockItemId] * menuItemUsage.EatInQuantitySold;
                            var quantityWasted = stockItemUsage[stockItemId] * menuItemUsage.EatInQuantityWasted;

                            await UpdateUsageAsync(stockItemId, storeReceives, quantityUsed, quantityWasted, inventoryByStockItem, storeId, openingStockTake, theoreticalItemIds, useModelWastage);
                        }

                        stockItemUsage = await GetRecipeForMenuItemAsync(theoreticalUsagePeriod.Value.RecipeRevisionId, menuItemUsage.MenuItemId, MenuItemRecipeType.EatOut);

                        foreach (var stockItemId in stockItemUsage.Keys)
                        {
                            if (itemIds.Any())
                            {
                                if (!itemIds.Contains(stockItemId))
                                {
                                    continue;
                                }
                            }

                            var quantityUsed = stockItemUsage[stockItemId] * menuItemUsage.TakeAwayQuantitySold;
                            var quantityWasted = stockItemUsage[stockItemId] * menuItemUsage.TakeAwayQuantityWasted;

                            await UpdateUsageAsync(stockItemId, storeReceives, quantityUsed, quantityWasted, inventoryByStockItem, storeId, openingStockTake, theoreticalItemIds, useModelWastage);
                        }
                    }
                }
            }

            foreach (var inventory in inventoryByStockItem.Values)
            {
                var item = await GetItemAsync(inventory.ItemId);
                var wastagePercentage = await GetStoreWastagePercentageAsync(inventory.ItemId, storeId);

                foreach (var discard in readOnlyStockContext.ItemDiscards.Where(x => x.ItemId == inventory.ItemId && x.StoreId == storeId && x.Timestamp >= openingStockTake.DateTime && x.Timestamp < closingStockTake.DateTime))
                {
                    inventory.SpoilageUnits += (discard.PreppedQuantity ?? 0) + ((discard.BulkQuantity ?? 0) * (100 - wastagePercentage) / 100M);                    
                }

                var receives = storeReceives.Where(x => x.ItemId == inventory.ItemId).Select(x => x.Grv).Distinct().ToList();

                inventory.DifferenceUnits =  inventory.ActualUnits - inventory.TheoreticalUnits;
                inventory.DifferenceValue = inventory.ActualCostOfSalesValue - inventory.TheoreticalCostOfSalesValue;
                inventory.SpoilageValue = (await fifoCalculator.GetFifoCostForItemAsync(
                    readOnlyStockContext,
                    item,
                    inventory.SpoilageUnits,
                    openingStockTake,
                    receives,
                    null,
                    wastagePercentage)) ?? 0M;
                inventory.VoidsValue = (await fifoCalculator.GetFifoCostForItemAsync(
                    readOnlyStockContext, 
                    item, 
                    inventory.VoidsUnits, 
                    openingStockTake, 
                    receives,
                    null, 
                    wastagePercentage)) ?? 0M;

                inventory.StockLists = await readOnlyStockContext.StockLists.Where(x => x.StockListItems.Any(i => i.ItemId == inventory.ItemId)).Select(x => new InventoryStockList(x.Id, x.Name)).ToListAsync();
            }

            var response = new GrossProfitReportResponse()
            {
                Inventory = inventoryByStockItem.Values,
            };

            return response;
        }

        private async Task UpdateUsageAsync(
            int stockItemId, 
            List<GrvItem> storeReceives, 
            decimal quantityUsed, 
            decimal quantityWasted, 
            ConcurrentDictionary<int, Inventory> inventoryByStockItem, 
            string storeId, 
            StockTake openingStockTake, 
            List<int> theoreticalItemIds,
            bool useModelWastage)
        {
            var receives = storeReceives.Where(x => x.ItemId == stockItemId);
            if (!inventoryByStockItem.TryGetValue(stockItemId, out var inventory))
            {
                var item = await GetItemAsync(stockItemId);
                var storeWastagePercentage = await GetStoreWastagePercentageAsync(stockItemId, storeId);

                inventory = new Inventory()
                {
                    ItemId = stockItemId,
                    CategoryName = item.Category?.Name,
                    ItemName = item.Name,
                    ItemCode = item.Code,
                    StockType = item.StockType?.Name ?? "Not set",
                    ItemStatus = item.IsArchived ? "Inactive" : "Active",
                    ItemDescription = item.Description,
                    UnitOfMeasure = item.UnitOfMeasure?.Name,
                    TheoreticalUnits = quantityUsed + quantityWasted,
                    TheoreticalCostOfSalesValue = await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, quantityUsed + quantityWasted, openingStockTake, receives.Select(x => x.Grv).Distinct().ToList(), null, useModelWastage ? item.WastagePercentage : storeWastagePercentage),
                    ModelWastagePercentage = item.WastagePercentage,
                    StoreWastagePercentage = storeWastagePercentage,
                };
                inventoryByStockItem[stockItemId] = inventory;
                theoreticalItemIds.Add(stockItemId);
            }
            else
            {
                if (theoreticalItemIds.Contains(stockItemId))
                {
                    var item = await GetItemAsync(stockItemId);
                    var storeWastagePercentage = await GetStoreWastagePercentageAsync(stockItemId, storeId);

                    inventory.TheoreticalUnits += quantityUsed + quantityWasted;
                    inventory.TheoreticalCostOfSalesValue += await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, quantityUsed + quantityWasted, openingStockTake, receives.Select(x => x.Grv).Distinct().ToList(), null, useModelWastage ? item.WastagePercentage : storeWastagePercentage);
                }
            }

            inventory.VoidsUnits += quantityWasted;
        }

        async Task<Dictionary<int, decimal>> GetRecipeForMenuItemAsync(int recipeRevisionId, int menuItemId, MenuItemRecipeType recipeType)
        {
            var stockItemUsage = await readOnlyStockContext
                .MenuItemStockItemMaps
                .Where(x => x.MenuItemId == menuItemId && x.RecipeRevisionId == recipeRevisionId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)recipeType))
                .GroupBy(x => x.StockItemId)
                .ToDictionaryAsync(x => x.Key, x => x.Sum(q => (q.Quantity == null ? 0M : q.Quantity.Value)));

            foreach (var recipeItemUsage in await readOnlyStockContext
                .MenuItemRecipeMaps
                .Where(x => x.MenuItemId == menuItemId && x.RecipeRevisionId == recipeRevisionId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)recipeType))
                .Select(x => x.Recipe.RecipeItems)
                .SelectMany(x => x)
                .GroupBy(x => x.ItemId)
                .ToDictionaryAsync(x => x.Key, x => x.Sum(q => (q.Quantity == null ? 0M : q.Quantity.Value))))
            {
                if (stockItemUsage.ContainsKey(recipeItemUsage.Key))
                {
                    stockItemUsage[recipeItemUsage.Key] += recipeItemUsage.Value;
                }
                else
                {
                    stockItemUsage[recipeItemUsage.Key] = recipeItemUsage.Value;
                }
            }

            return stockItemUsage;
        }

        private static async Task<List<StockTakeWindowGrvBalance>> GetStockTakeWindowAsync(
            StockContext stockContext, 
            ReadOnlyStockContext readOnlyStockContext, 
            int grossProfitReportId, StockTake stockTake, 
            Item item, 
            decimal unitsRequired, 
            decimal wastagePercent)
        {
            var window = new StockTakeWindow()
            {
                GrossProfitReportId = grossProfitReportId,
                StockTakeId = stockTake.Id,
                ItemId = item.Id,
                LastUpdatedAt = DateTime.UtcNow,
            };
            await stockContext.StockTakeWindows.AddAsync(window);
            await stockContext.SaveChangesAsync();

            var receivedUnits = 0M;

            var grvs = readOnlyStockContext.Grvs
                .Include(x => x.GrvItems)
                .ThenInclude(gi => gi.StockMovements)
                .Where(x =>
                    x.StoreId == stockTake.StoreId &&
                    x.GrvItems.Any(x => x.ItemId == item.Id) &&
                    (x.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized) &&
                    x.StockMovementAt < stockTake.DateTime)
                .OrderByDescending(x => x.StockMovementAt)
                .ThenByDescending(x => x.Id);

            List<StockTakeWindowGrvBalance> grvBalances = new();

            StockTakeWindowGrvBalance grvBalance = null;

            foreach (var grv in grvs)
            {
                var grvItem = grv.GrvItems.First(x => x.ItemId == item.Id);

                var actualReceivedQuantity = grvItem.StockMovements.Sum(sm => sm.Quantity);
                var actualTotalCost = grvItem.TotalCostExcl.ProRata(grvItem.QuantityGrv, actualReceivedQuantity);
                var grvUnits = actualReceivedQuantity * ((100M - wastagePercent) / 100M);
                var unitCost = grvUnits > 0 ? actualTotalCost / grvUnits : 0;

                if (receivedUnits + grvUnits > unitsRequired)
                {
                    grvUnits = unitsRequired - receivedUnits;
                }

                if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
                {
                    receivedUnits += grvUnits;
                }
                else // This will include Returns and Short Delivered
                {
                    receivedUnits -= grvUnits;
                }

                grvBalance = new StockTakeWindowGrvBalance()
                {
                    StockTakeWindowId = window.Id,
                    GrvId = grv.Id,
                    GrvItemId = grvItem.Id,
                    OpeningUnitQuantity = grvUnits * (grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv ? -1M : 1M),
                    UnitCost = unitCost,
                    RemainingUnitQuantity = grvUnits * (grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv ? -1M : 1M),
                };
                await stockContext.StockTakeWindowGrvBalances.AddAsync(grvBalance);
                grvBalances.Add(grvBalance);

                if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
                {
                    var pendingReturns = grvBalances
                        .Where(x => x.RemainingUnitQuantity < 0 && x.Id < grvBalance.Id);

                    foreach (var pendingReturn in pendingReturns)
                    {
                        var availableGrvs = grvBalances
                            .Where(x => x.RemainingUnitQuantity > 0 && x.Id > pendingReturn.Id);

                        var required = -pendingReturn.RemainingUnitQuantity;

                        foreach (var availableGrv in availableGrvs)
                        {
                            var amount = Math.Min(required, availableGrv.RemainingUnitQuantity);
                            availableGrv.RemainingUnitQuantity -= amount;
                            pendingReturn.RemainingUnitQuantity += amount;
                            required -= amount;

                            if (required == 0)
                            {
                                break;
                            }
                        }

                        await stockContext.SaveChangesAsync();
                    }
                }

                if (receivedUnits >= unitsRequired)
                {
                    var excess = receivedUnits - unitsRequired;
                    grvBalance.RemainingUnitQuantity -= excess;
                    grvBalance.OpeningUnitQuantity -= excess;

                    await stockContext.SaveChangesAsync();

                    break;
                }
            }

            if (grvBalance != null)
            {
                grvBalance.OpeningUnitQuantity += unitsRequired - receivedUnits;
                grvBalance.RemainingUnitQuantity += unitsRequired - receivedUnits;

                await stockContext.SaveChangesAsync();
            }

            window.Value = grvBalances.Sum(x => x.UnitCost * x.OpeningUnitQuantity);

            await stockContext.SaveChangesAsync();

            return grvBalances;
        }
    }
}

