using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Database.Context.Stock;
using AuraCloudInterface.Stock;
using AuraCloudInterface.Stock.StockLossReport;
using Microsoft.EntityFrameworkCore;

namespace Aura.Manager.Stock.Reports
{
    public class StockLossReportManager
    {
        private ReadOnlyStockContext readOnlyStockContext;
        private StoreManager storeManager;
        private TheoreticalSalesReportManager theoreticalSalesReportManager;
        private FifoCalculator fifoCalculator;

        public StockLossReportManager(
            StockContext stockContext,
            ReadOnlyStockContext readOnlyStockContext,
            StoreManager storeManager,
            StoreTransactionManager storeTransactionManager,
            TheoreticalSalesReportManager theoreticalSalesReportManager,
            FifoCalculator fifoCalculator)
        {
            this.readOnlyStockContext = readOnlyStockContext;
            this.storeManager = storeManager;
            this.theoreticalSalesReportManager = theoreticalSalesReportManager;
            this.fifoCalculator = fifoCalculator;
        }

        public async Task<StockLossReportResponse> GetStockLossReportAsync(string storeId, int fromStockTakeId, int toStockTakeId)
        {
            if (fromStockTakeId == toStockTakeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.BadRequest, "You cannot specify the same stock take for both the start and end of the report.");
            }
            storeId = storeId.ToUpperInvariant();

            var openingStockTake = await readOnlyStockContext.StockTakes.AsNoTracking().FirstOrDefaultAsync(x => x.Id == fromStockTakeId);
            if (openingStockTake == null || openingStockTake.StoreId.ToUpperInvariant() != storeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Opening stock take {fromStockTakeId} was not found for store id {storeId}");
            }
            var closingStockTake = await readOnlyStockContext.StockTakes.AsNoTracking().FirstOrDefaultAsync(x => x.Id == toStockTakeId);
            if (closingStockTake == null || closingStockTake.StoreId.ToUpperInvariant() != storeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Closing stock take {toStockTakeId} was not found for store id {storeId}");
            }

            var store = await storeManager.ViewStoreSummaryAsync(storeId);

            var usageData = await theoreticalSalesReportManager.GetTheoreticalUsageAsync(store.BrandId, storeId, openingStockTake, closingStockTake.DateTime, await readOnlyStockContext.Items.Where(x => x.BrandId == store.BrandId).Select(x => x.Id).ToListAsync(), false);

            return await GenerateStockLossReportAsync(storeId, openingStockTake, closingStockTake, usageData);
        }

        private async Task<StockLossReportResponse> GenerateStockLossReportAsync(string storeId, StockTake openingStockTake, StockTake closingStockTake, TheoreticalUsageInfo usageInfo)
        {
            var report = new StockLossReportResponse()
            {
                Losses = new List<Loss>(),
            };

            await Task.Delay(100);

            var losses = new Dictionary<int, Loss>();
            var items = new Dictionary<int, Item>();

            foreach (var period in usageInfo.Periods)
            {
                var usages = period.Value.UsageByStockItemId;
                foreach (var stockUsageInfo in usages)
                {
                    var usage = stockUsageInfo.Value;

                    if (!losses.TryGetValue(usage.ItemId, out var loss))
                    {
                        var item = await readOnlyStockContext.Items.FindAsync(usage.ItemId);

                        loss = new Loss()
                        {
                            ItemId = usage.ItemId,
                            ItemName = item.Name,
                            ItemCode = item.Code,
                            ItemDescription = item.Description,
                            ItemStatus = item.IsArchived ? "Inactive" : "Active",
                            CategoryName = item.Category?.Name,
                            StockType = item.StockType?.Name,
                            UnitOfMeasure = item.UnitOfMeasure?.Name,
                        };
                        losses[usage.ItemId] = loss;
                    }

                    loss.TheoreticalUnits += usage.UnitQuantityUsed;
                    loss.TheoreticalCostOfSalesValue += usage.Fifocost ?? 0M;
                }
            }

            foreach (var period in usageInfo.Periods)
            {
                var periodWindow = await readOnlyStockContext.TheoreticalUsagePeriods.FindAsync(period.Key);
                var sales = period.Value.SalesByMenuItemId;
                foreach (var saleInfo in sales)
                {
                    var sale = saleInfo.Value;
                    if (sale.EatInQuantityWasted > 0)
                    {
                        var stockItems = readOnlyStockContext.MenuItemStockItemMaps.AsNoTracking().Where(x => x.RecipeRevisionId == periodWindow.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatIn));

                        foreach (var item in stockItems)
                        {
                            if (!losses.TryGetValue(item.StockItemId, out var loss))
                            {
                                loss = new Loss()
                                {
                                    ItemId = item.StockItemId,
                                    ItemName = item.StockItem.Name,
                                    ItemCode = item.StockItem.Code,
                                    ItemDescription = item.StockItem.Description,
                                    ItemStatus = item.StockItem.IsArchived ? "Inactive" : "Active",
                                    CategoryName = item.StockItem.Category?.Name,
                                    StockType = item.StockItem.StockType?.Name,
                                    UnitOfMeasure = item.StockItem.UnitOfMeasure?.Name,
                                };
                                losses[item.StockItemId] = loss;
                            }

                            loss.VoidsUnits += item.Quantity * sale.EatInQuantityWasted ?? 0M;
                        }

                        var recipes = readOnlyStockContext.MenuItemRecipeMaps.AsNoTracking().Where(x => x.RecipeRevisionId == periodWindow.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatIn));
                        foreach (var recipe in recipes)
                        {
                            foreach (var item in recipe.Recipe.RecipeItems.Where(x => x.RecipeRevisionId == periodWindow.RecipeRevisionId))
                            {
                                if (!losses.TryGetValue(item.ItemId, out var loss))
                                {
                                    loss = new Loss()
                                    {
                                        ItemId = item.ItemId,
                                        ItemName = item.Item.Name,
                                        ItemCode = item.Item.Code,
                                        ItemDescription = item.Item.Description,
                                        ItemStatus = item.Item.IsArchived ? "Inactive" : "Active",
                                        CategoryName = item.Item.Category?.Name,
                                        StockType = item.Item.StockType?.Name,
                                        UnitOfMeasure = item.Item.UnitOfMeasure?.Name,
                                    };
                                    losses[item.ItemId] = loss;
                                }

                                loss.VoidsUnits += item.Quantity * sale.EatInQuantityWasted ?? 0M;
                            }
                        }
                    }

                    if (sale.TakeAwayQuantityWasted > 0)
                    {
                        var stockItems = readOnlyStockContext.MenuItemStockItemMaps.AsNoTracking().Where(x => x.RecipeRevisionId == periodWindow.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatOut));

                        foreach (var item in stockItems)
                        {
                            if (!losses.TryGetValue(item.StockItemId, out var loss))
                            {
                                loss = new Loss()
                                {
                                    ItemId = item.StockItemId,
                                    ItemName = item.StockItem.Name,
                                    ItemCode = item.StockItem.Code,
                                    ItemDescription = item.StockItem.Description,
                                    ItemStatus = item.StockItem.IsArchived ? "Inactive" : "Active",
                                    CategoryName = item.StockItem.Category?.Name,
                                    StockType = item.StockItem.StockType?.Name,
                                    UnitOfMeasure = item.StockItem.UnitOfMeasure?.Name,
                                };
                                losses[item.StockItemId] = loss;
                            }

                            loss.VoidsUnits += item.Quantity * sale.TakeAwayQuantityWasted ?? 0M;
                        }

                        var recipes = readOnlyStockContext.MenuItemRecipeMaps.AsNoTracking().Where(x => x.RecipeRevisionId == periodWindow.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatOut));
                        foreach (var recipe in recipes)
                        {
                            foreach (var item in recipe.Recipe.RecipeItems.Where(x => x.RecipeRevisionId == periodWindow.RecipeRevisionId))
                            {
                                if (!losses.TryGetValue(item.ItemId, out var loss))
                                {
                                    loss = new Loss()
                                    {
                                        ItemId = item.ItemId,
                                        ItemName = item.Item.Name,
                                        ItemCode = item.Item.Code,
                                        ItemDescription = item.Item.Description,
                                        ItemStatus = item.Item.IsArchived ? "Inactive" : "Active",
                                        CategoryName = item.Item.Category?.Name,
                                        StockType = item.Item.StockType?.Name,
                                        UnitOfMeasure = item.Item.UnitOfMeasure?.Name,
                                    };
                                    losses[item.ItemId] = loss;
                                }

                                loss.VoidsUnits += item.Quantity * sale.TakeAwayQuantityWasted ?? 0M;
                            }
                        }
                    }
                }
            }

            foreach (var discard in readOnlyStockContext.ItemDiscards.Where(x => x.StoreId == storeId && x.Timestamp > openingStockTake.DateTime && x.Timestamp < closingStockTake.DateTime))
            {
                if (!items.TryGetValue(discard.ItemId, out var item))
                {
                    item = await readOnlyStockContext.Items.FindAsync(discard.ItemId);
                    items[discard.ItemId] = item;
                }

                var storeOverride = item.ItemDetailStoreOverrides.FirstOrDefault(x => x.StoreId == storeId);
                var wastagePercentage = storeOverride?.WastagePercentage ?? item.WastagePercentage;

                if (!losses.TryGetValue(item.Id, out var loss))
                {
                    loss = new Loss()
                    {
                        ItemId = item.Id,
                        ItemName = item.Name,
                        ItemCode = item.Code,
                        ItemDescription = item.Description,
                        ItemStatus = item.IsArchived ? "Inactive" : "Active",
                        CategoryName = item.Category?.Name,
                        StockType = item.StockType?.Name,
                        UnitOfMeasure = item.UnitOfMeasure?.Name,
                    };
                    losses[item.Id] = loss;
                }

                loss.SpoilageUnits += (discard.PreppedQuantity ?? 0) + ((discard.BulkQuantity ?? 0) * (100 - wastagePercentage) / 100M);
            }

            foreach (var loss in losses.Values)
            {
                if (!items.TryGetValue(loss.ItemId, out var item))
                {
                    item = await readOnlyStockContext.Items.FindAsync(loss.ItemId);
                    items[loss.ItemId] = item;
                }

                var storeOverride = item.ItemDetailStoreOverrides.FirstOrDefault(x => x.StoreId == storeId);
                var wastagePercentage = storeOverride?.WastagePercentage ?? item.WastagePercentage;

                var openingUnits = 0M;
                var closingUnits = 0M;

                if (openingStockTake.StockTakeItems.Any(x => x.ItemId == item.Id))
                {
                    var openingBulk = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                    var openingPrepped = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                    openingUnits = openingPrepped + openingBulk * (100 - wastagePercentage) / 100M;
                }

                if (closingStockTake.StockTakeItems.Any(x => x.ItemId == item.Id))
                {
                    var closingBulk = Convert.ToDecimal(closingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                    var closingPrepped = Convert.ToDecimal(closingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                    closingUnits = closingPrepped + closingBulk * (100 - wastagePercentage) / 100M;
                }

                var grvs = await readOnlyStockContext.Grvs
                    .OrderBy(x => x.StockMovementAt)
                    .Where(x =>
                        x.StoreId == storeId &&
                        x.GrvItems.Any(i => i.ItemId == item.Id) &&
                        x.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized &&
                        (x.StockMovementAt >= openingStockTake.DateTime && x.StockMovementAt <= closingStockTake.DateTime))
                    .ToListAsync();

                loss.VoidsValue = await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, loss.VoidsUnits, openingStockTake, grvs, null, wastagePercentage) ?? 0M;
                loss.SpoilageValue = await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, loss.SpoilageUnits, openingStockTake, grvs, null, wastagePercentage) ?? 0M;

                var actualUsage = await ActualUsageCalculator.GetActualUsageForItemAsync(readOnlyStockContext, openingStockTake, closingStockTake, item, openingUnits, closingUnits, wastagePercentage, fifoCalculator);

                loss.DifferenceUnits = actualUsage.ActualUsage - loss.TheoreticalUnits;
                loss.DifferenceValue = actualUsage.Fifocost - loss.TheoreticalCostOfSalesValue;
            }

            report.Losses.AddRange(losses.Values);

            return report;
        }
    }
