   226→        internal async Task<TheoreticalUsageInfo> GetTheoreticalUsageAsync(int brandId, string storeId, StockTake openingStockTake, DateTime toTimestamp, ICollection<int> itemIds, bool useModel)
   227→        {
   228→            var window = new TheoreticalUsage()
   229→            {
   230→                StoreId = storeId,
   231→                FromStockTakeId = openingStockTake.Id,
   232→                ToTimestamp = toTimestamp,
   233→            };
   234→            await stockContext.TheoreticalUsages.AddAsync(window);
   235→            await stockContext.SaveChangesAsync();
   236→            stockContext.TheoreticalUsages.Entry(window).State = EntityState.Detached;
   237→            readOnlyStockContext.TheoreticalUsages.Attach(window);
   238→
   239→            var windowPeriods = new List<TheoreticalUsagePeriod>();
   240→
   241→            var recipeRevisions = readOnlyStockContext.RecipeRevisions.OrderByDescending(x => x.EffectiveDate).Where(x => x.BrandId == brandId && x.ApprovedAt.HasValue);
   242→            if (!await recipeRevisions.AnyAsync())
   243→            {
   244→                // no approved recipe revision
   245→                var revision = await readOnlyStockContext.RecipeRevisions.Where(x => x.BrandId == brandId).OrderByDescending(x => x.Id).FirstOrDefaultAsync();
   246→                if (revision == null)
   247→                {
   248→                    // no recipe revisions, so can't calculate theoretical usage
   249→                    return null;
   250→                }
   251→                windowPeriods.Add(new TheoreticalUsagePeriod()
   252→                {
   253→                    TheoreticalUsageId = window.Id,
   254→                    RecipeRevisionId = revision.Id,
   255→                    FromTimestamp = openingStockTake.DateTime,
   256→                    ToTimestamp = toTimestamp,
   257→                });
   258→            }
   259→            else
   260→            {
   261→                TheoreticalUsagePeriod? period = null;
   262→
   263→                var openingRevision = await recipeRevisions.FirstOrDefaultAsync(x => x.EffectiveDate < openingStockTake.DateTime);
   264→                if (openingRevision != null)
   265→                {
   266→                    period = new TheoreticalUsagePeriod()
   267→                    {
   268→                        TheoreticalUsageId = window.Id,
   269→                        RecipeRevisionId = openingRevision.Id,
   270→                        FromTimestamp = openingStockTake.DateTime,
   271→                    };
   272→                    windowPeriods.Add(period);
   273→
   274→                    recipeRevisions = readOnlyStockContext.RecipeRevisions
   275→                        .OrderBy(x => x.EffectiveDate)
   276→                        .Where(x => x.BrandId == brandId && x.ApprovedAt.HasValue && x.EffectiveDate > openingRevision.EffectiveDate);
   277→                }
   278→                else
   279→                {
   280→                    recipeRevisions = readOnlyStockContext.RecipeRevisions
   281→                        .OrderBy(x => x.EffectiveDate)
   282→                        .Where(x => x.BrandId == brandId && x.ApprovedAt.HasValue && x.EffectiveDate > openingStockTake.DateTime);
   283→                }
   284→
   285→                foreach (var revision in recipeRevisions)
   286→                {
   287→                    if (revision.EffectiveDate > toTimestamp && windowPeriods.Any())
   288→                    {
   289→                        break;
   290→                    }
   291→
   292→                    if (period != null)
   293→                    {
   294→                        period.ToTimestamp = revision.EffectiveDate;
   295→                    }
   296→
   297→                    period = new TheoreticalUsagePeriod()
   298→                    {
   299→                        TheoreticalUsageId = window.Id,
   300→                        RecipeRevisionId = revision.Id,
   301→                        FromTimestamp = period == null ? openingStockTake.DateTime : revision.EffectiveDate,
   302→                    };
   303→                    windowPeriods.Add(period);
   304→                }
   305→
   306→                if (period == null)
   307→                {
   308→                    // no recipe revisions, so can't calculate theoretical usage
   309→                    return null;
   310→                }
   311→
   312→                // last period ends at the close of the reporting period.
   313→                period.ToTimestamp = toTimestamp;
   314→            }
   315→
   316→            await stockContext.TheoreticalUsagePeriods.AddRangeAsync(windowPeriods);
   317→            await stockContext.SaveChangesAsync();
   318→
   319→            var info = new TheoreticalUsageInfo()
   320→            {
   321→                Id = window.Id,
   322→            };
   323→
   324→            await Parallel.ForEachAsync(windowPeriods, async (period, cancel) =>
   325→            {
   326→                var scope = scopeFactory.CreateScope();
   327→                var stockContext = scope.ServiceProvider.GetRequiredService<StockContext>();
   328→                var readOnlyStockContext = scope.ServiceProvider.GetRequiredService<ReadOnlyStockContext>();
   329→                var fifoCalculator = scope.ServiceProvider.GetRequiredService<FifoCalculator>();
   330→                var storeTransactionManager = scope.ServiceProvider.GetRequiredService<StoreTransactionManager>();
   331→
   332→                var periodInfo = await CalculateTheoreticalUsageForWindowPeriodAsync(
   333→                    stockContext, 
   334→                    readOnlyStockContext, 
   335→                    fifoCalculator, 
   336→                    storeTransactionManager, 
   337→                    brandId, 
   338→                    storeId, 
   339→                    period, 
   340→                    openingStockTake, 
   341→                    itemIds, 
   342→                    period.RecipeRevisionId, 
   343→                    useModel);
   344→                periodInfo.PeriodId = period.Id;
   345→                info.Periods[period.Id] = periodInfo;
   346→            });
   347→
   348→            return info;
   349→        }
