   351→        private static async Task<TheoreticalUsagePeriodInfo> CalculateTheoreticalUsageForWindowPeriodAsync(
   352→            StockContext stockContext, 
   353→            ReadOnlyStockContext readOnlyStockContext,
   354→            FifoCalculator fifoCalculator,
   355→            StoreTransactionManager storeTransactionManager,
   356→            int brandId, 
   357→            string storeId, 
   358→            TheoreticalUsagePeriod period, 
   359→            StockTake openingStockTake, 
   360→            ICollection<int> itemIds, 
   361→            int recipeRevisionId,
