﻿using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Database.Context.Stock;
using AuraCloudInterface.Menus;
using AuraCloudInterface.Stock;
using AuraCloudInterface.Stock.TheoreticalSalesReport;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.DependencyInjection;

namespace Aura.Manager.Stock.Reports
{
    public class TheoreticalSalesReportManager
    {
        private StockContext stockContext;
        private ReadOnlyStockContext readOnlyStockContext;
        private StoreManager storeManager;
        private StoreTransactionManager storeTransactionManager;
        private FifoCalculator fifoCalculator;
        private MenuManager menuManager;
        private IServiceScopeFactory scopeFactory;

        public TheoreticalSalesReportManager(
            StockContext stockContext,
            ReadOnlyStockContext readOnlyStockContext,
            StoreManager storeManager, 
            StoreTransactionManager storeTransactionManager,
            FifoCalculator fifoCalculator,
            MenuManager menuManager,
            IServiceScopeFactory scopeFactory)
        {
            this.stockContext = stockContext;
            this.readOnlyStockContext = readOnlyStockContext;
            this.storeManager = storeManager;
            this.storeTransactionManager = storeTransactionManager;
            this.fifoCalculator = fifoCalculator;
            this.menuManager = menuManager;
            this.scopeFactory = scopeFactory;
        }

        public async Task<TheoreticalSalesReportResponse> GetTheoreticalSalesReportAsync(string storeId, int itemId, int fromStockTakeId, int toStockTakeId, bool useModel)
        {
            if (fromStockTakeId == toStockTakeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.BadRequest, "You cannot specify the same stock take for both the start and end of the report.");
            }

            storeId = storeId.ToUpperInvariant();

            var openingStockTake = await readOnlyStockContext.StockTakes.AsNoTracking().FirstOrDefaultAsync(x => x.Id == fromStockTakeId);
            if (openingStockTake == null || openingStockTake.StoreId.ToUpperInvariant() != storeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Opening stock take {fromStockTakeId} was not found for store id {storeId}");
            }
            var closingStockTake = await readOnlyStockContext.StockTakes.AsNoTracking().FirstOrDefaultAsync(x => x.Id == toStockTakeId);
            if (closingStockTake == null || closingStockTake.StoreId.ToUpperInvariant() != storeId)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Closing stock take {toStockTakeId} was not found for store id {storeId}");
            }

            var store = await storeManager.ViewStoreSummaryAsync(storeId);

            var usage = await GetTheoreticalUsageAsync(store.BrandId, storeId, openingStockTake, closingStockTake.DateTime, new List<int>() { itemId }, useModel);

            if (usage == null)
            {
                return new TheoreticalSalesReportResponse()
                {
                    Sales = [],
                };
            }

            return await GenerateTheoreticalSalesReportAsync(usage, openingStockTake, itemId, store, useModel);
        }

        private async Task<TheoreticalSalesReportResponse> GenerateTheoreticalSalesReportAsync(TheoreticalUsageInfo usage, StockTake openingStockTake, int itemId, AuraCloudInterface.Stores.StoreSummary store, bool useModel)
        {
            var response = new TheoreticalSalesReportResponse()
            {
                Sales = new List<TheoreticalSale>(),
            };

            var stockItem = await readOnlyStockContext.Items.FindAsync(itemId);


            foreach (var periodItem in usage.Periods)
            {
                var period = await readOnlyStockContext.TheoreticalUsagePeriods.FindAsync(periodItem.Key);
                foreach (var saleInfo in periodItem.Value.SalesByMenuItemId)
                {
                    var sales = new List<TheoreticalSale>();
                    var sale = saleInfo.Value;

                    var eatIn = sale.EatInQuantitySold;// + sale.EatInQuantityWasted;

                    if (eatIn > 0)
                    {
                        var stockItems = readOnlyStockContext.MenuItemStockItemMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && x.StockItemId == itemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatIn));
                        foreach (var item in stockItems)
                        {
                            sales.Add(new TheoreticalSale()
                            {
                                EatInOrEatOut = "Sit down",
                                BasketItemQuantity = eatIn,
                                StockItemId = item.StockItemId,
                                StockItemName = item.StockItem.Name,
                                UoM = item.StockItem.UnitOfMeasure.Name,
                                NamedRecipeName = "n/a",
                                SitDownRecipe = item.Quantity ?? 0,
                                TheoreticalUsage = item.Quantity * eatIn ?? 0M,
                            });
                        }

                        var recipes = readOnlyStockContext.MenuItemRecipeMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatIn));
                        foreach (var recipe in recipes)
                        {
                            foreach (var item in recipe.Recipe.RecipeItems.Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.ItemId == itemId))
                            {
                                sales.Add(new TheoreticalSale()
                                {
                                    EatInOrEatOut = "Sit down",
                                    BasketItemQuantity = eatIn,
                                    StockItemId = item.ItemId,
                                    StockItemName = item.Item.Name,
                                    UoM = item.Item.UnitOfMeasure.Name,
                                    NamedRecipeName = item.Recipe.Name,
                                    SitDownRecipe = item.Quantity ?? 0,
                                    TheoreticalUsage = item.Quantity * eatIn ?? 0M,
                                });
                            }
                        }

                    }

                    var takeAway = sale.TakeAwayQuantitySold;// + sale.TakeAwayQuantityWasted;

                    if (takeAway > 0)
                    {
                        var stockItems = readOnlyStockContext.MenuItemStockItemMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && x.StockItemId == itemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatOut));
                        foreach (var item in stockItems)
                        {
                            sales.Add(new TheoreticalSale()
                            {
                                EatInOrEatOut = "Take away",
                                BasketItemQuantity = takeAway,
                                StockItemId = item.StockItemId,
                                StockItemName = item.StockItem.Name,
                                UoM = item.StockItem.UnitOfMeasure.Name,
                                NamedRecipeName = "n/a",
                                TakeAwayRecipe = item.Quantity ?? 0,
                                TheoreticalUsage = item.Quantity * takeAway ?? 0M,
                            });
                        }

                        var recipes = readOnlyStockContext.MenuItemRecipeMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == sale.MenuItemId && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatOut));
                        foreach (var recipe in recipes)
                        {
                            foreach (var item in recipe.Recipe.RecipeItems.Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.ItemId == itemId))
                            {
                                sales.Add(new TheoreticalSale()
                                {
                                    EatInOrEatOut = "Take away",
                                    BasketItemQuantity = takeAway,
                                    StockItemId = item.ItemId,
                                    StockItemName = item.Item.Name,
                                    UoM = item.Item.UnitOfMeasure.Name,
                                    NamedRecipeName = item.Recipe.Name,
                                    TakeAwayRecipe = item.Quantity ?? 0,
                                    TheoreticalUsage = item.Quantity * takeAway ?? 0M,
                                });
                            }
                        }
                    }

                    var wastagePercentage = stockItem.WastagePercentage;
                    if (!useModel)
                    {
                        var storeOverride = stockItem.ItemDetailStoreOverrides.FirstOrDefault(x => x.StoreId == openingStockTake.StoreId);
                        if (storeOverride?.WastagePercentage.HasValue ?? false)
                        {
                            wastagePercentage = storeOverride.WastagePercentage.Value;
                        }
                    }

                    foreach (var theoreticalSale in sales)
                    {
                        theoreticalSale.RecipeRevisionId = period.RecipeRevisionId;
                        theoreticalSale.RevisionStartDate = period.FromTimestamp.AddMinutes(store.TimeZoneOffsetMinutes).ToString("ddd dd MMM yyyy") + " at " + period.FromTimestamp.AddMinutes(store.TimeZoneOffsetMinutes).ToString("HH:mm");
                        theoreticalSale.RevisionEndDate = period.ToTimestamp.AddMinutes(store.TimeZoneOffsetMinutes).ToString("ddd dd MMM yyyy") + " at " + period.ToTimestamp.AddMinutes(store.TimeZoneOffsetMinutes).ToString("HH:mm");
                        theoreticalSale.TheoreticalFifoValue = useModel ? stockItem.ModelPreppedCost :
                            await fifoCalculator.GetFifoCostForItemAsync(
                                readOnlyStockContext,
                                stockItem,
                                theoreticalSale.TheoreticalUsage,
                                openingStockTake,
                                await readOnlyStockContext.Grvs.Where(x => x.StoreId == openingStockTake.StoreId && x.GrvDate >= openingStockTake.DateTime && x.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized).ToListAsync(),
                                null,
                                wastagePercentage);
                        theoreticalSale.MenuItemId = sale.MenuItemId;

                        var menuItem = await GetMenuItemAsync(stockItem.BrandId, sale.MenuItemId);

                        theoreticalSale.MenuItemPLUCode = menuItem?.PluCode;
                        theoreticalSale.MenuItemCategory = menuItem?.CategoryName;
                        theoreticalSale.MenuItemName = menuItem?.Name;
                    }

                    response.Sales.AddRange(sales);
                }
            }

            return response;
        }

        static Dictionary<int, MenuItemSummary> menuItemCache = new Dictionary<int, MenuItemSummary>();

        private async Task<MenuItemSummary> GetMenuItemAsync(int brandId, int menuItemId)
        {
            if (menuItemCache.TryGetValue(menuItemId, out MenuItemSummary? menuItem))
            {
                return menuItem;
            }
            menuItemCache[menuItemId] = await menuManager.ViewMenuItemAsync(null, brandId, menuItemId);
            return menuItemCache[menuItemId];
        }

        internal async Task<TheoreticalUsageInfo> GetTheoreticalUsageAsync(int brandId, string storeId, StockTake openingStockTake, DateTime toTimestamp, ICollection<int> itemIds, bool useModel)
        {
            var window = new TheoreticalUsage()
            {
                StoreId = storeId,
                FromStockTakeId = openingStockTake.Id,
                ToTimestamp = toTimestamp,
            };
            await stockContext.TheoreticalUsages.AddAsync(window);
            await stockContext.SaveChangesAsync();
            stockContext.TheoreticalUsages.Entry(window).State = EntityState.Detached;
            readOnlyStockContext.TheoreticalUsages.Attach(window);

            var windowPeriods = new List<TheoreticalUsagePeriod>();

            var recipeRevisions = readOnlyStockContext.RecipeRevisions.OrderByDescending(x => x.EffectiveDate).Where(x => x.BrandId == brandId && x.ApprovedAt.HasValue);
            if (!await recipeRevisions.AnyAsync())
            {
                // no approved recipe revision
                var revision = await readOnlyStockContext.RecipeRevisions.Where(x => x.BrandId == brandId).OrderByDescending(x => x.Id).FirstOrDefaultAsync();
                if (revision == null)
                {
                    // no recipe revisions, so can't calculate theoretical usage
                    return null;
                }
                windowPeriods.Add(new TheoreticalUsagePeriod()
                {
                    TheoreticalUsageId = window.Id,
                    RecipeRevisionId = revision.Id,
                    FromTimestamp = openingStockTake.DateTime,
                    ToTimestamp = toTimestamp,
                });
            }
            else
            {
                TheoreticalUsagePeriod? period = null;

                var openingRevision = await recipeRevisions.FirstOrDefaultAsync(x => x.EffectiveDate < openingStockTake.DateTime);
                if (openingRevision != null)
                {
                    period = new TheoreticalUsagePeriod()
                    {
                        TheoreticalUsageId = window.Id,
                        RecipeRevisionId = openingRevision.Id,
                        FromTimestamp = openingStockTake.DateTime,
                    };
                    windowPeriods.Add(period);

                    recipeRevisions = readOnlyStockContext.RecipeRevisions
                        .OrderBy(x => x.EffectiveDate)
                        .Where(x => x.BrandId == brandId && x.ApprovedAt.HasValue && x.EffectiveDate > openingRevision.EffectiveDate);
                }
                else
                {
                    recipeRevisions = readOnlyStockContext.RecipeRevisions
                        .OrderBy(x => x.EffectiveDate)
                        .Where(x => x.BrandId == brandId && x.ApprovedAt.HasValue && x.EffectiveDate > openingStockTake.DateTime);
                }

                foreach (var revision in recipeRevisions)
                {
                    if (revision.EffectiveDate > toTimestamp && windowPeriods.Any())
                    {
                        break;
                    }

                    if (period != null)
                    {
                        period.ToTimestamp = revision.EffectiveDate;
                    }

                    period = new TheoreticalUsagePeriod()
                    {
                        TheoreticalUsageId = window.Id,
                        RecipeRevisionId = revision.Id,
                        FromTimestamp = period == null ? openingStockTake.DateTime : revision.EffectiveDate,
                    };
                    windowPeriods.Add(period);
                }

                if (period == null)
                {
                    // no recipe revisions, so can't calculate theoretical usage
                    return null;
                }

                // last period ends at the close of the reporting period.
                period.ToTimestamp = toTimestamp;
            }

            await stockContext.TheoreticalUsagePeriods.AddRangeAsync(windowPeriods);
            await stockContext.SaveChangesAsync();

            var info = new TheoreticalUsageInfo()
            {
                Id = window.Id,
            };

            await Parallel.ForEachAsync(windowPeriods, async (period, cancel) =>
            {
                var scope = scopeFactory.CreateScope();
                var stockContext = scope.ServiceProvider.GetRequiredService<StockContext>();
                var readOnlyStockContext = scope.ServiceProvider.GetRequiredService<ReadOnlyStockContext>();
                var fifoCalculator = scope.ServiceProvider.GetRequiredService<FifoCalculator>();
                var storeTransactionManager = scope.ServiceProvider.GetRequiredService<StoreTransactionManager>();

                var periodInfo = await CalculateTheoreticalUsageForWindowPeriodAsync(
                    stockContext, 
                    readOnlyStockContext, 
                    fifoCalculator, 
                    storeTransactionManager, 
                    brandId, 
                    storeId, 
                    period, 
                    openingStockTake, 
                    itemIds, 
                    period.RecipeRevisionId, 
                    useModel);
                periodInfo.PeriodId = period.Id;
                info.Periods[period.Id] = periodInfo;
            });

            return info;
        }

        private static async Task<TheoreticalUsagePeriodInfo> CalculateTheoreticalUsageForWindowPeriodAsync(
            StockContext stockContext, 
            ReadOnlyStockContext readOnlyStockContext,
            FifoCalculator fifoCalculator,
            StoreTransactionManager storeTransactionManager,
            int brandId, 
            string storeId, 
            TheoreticalUsagePeriod period, 
            StockTake openingStockTake, 
            ICollection<int> itemIds, 
            int recipeRevisionId,
            bool useModel)
        {
            var menuItems = await readOnlyStockContext.MenuItemStockItemMaps.Where(x => x.RecipeRevisionId == recipeRevisionId && itemIds.Contains(x.StockItemId)).Select(x => x.MenuItemId).Distinct().ToListAsync();
            var recipeIds = await readOnlyStockContext.RecipeItems.Where(x => x.RecipeRevisionId == recipeRevisionId && itemIds.Contains(x.ItemId)).Select(x => x.RecipeId).Distinct().ToListAsync();
            var itemsWithRecipes = await readOnlyStockContext.MenuItemRecipeMaps.Where(x => x.RecipeRevisionId == recipeRevisionId && recipeIds.Contains(x.RecipeId)).Select(x => x.MenuItemId).Distinct().ToListAsync();

            var menuItemIds = menuItems.Union(itemsWithRecipes).ToList();

            var menuItemTotals = await storeTransactionManager.GetMenuItemTotalsAsync(brandId, storeId, period.FromTimestamp, period.ToTimestamp, menuItemIds);

            var itemSales = new Dictionary<int, TheoreticalUsagePeriodSale>();
            foreach (var item in menuItemTotals.TotalQuantitySoldByMenuItemIdEatIn)
            {
                itemSales[item.Key] = new TheoreticalUsagePeriodSale()
                {
                    MenuItemId = item.Key,
                    EatInQuantitySold = item.Value, 
                };
            }
            foreach (var item in menuItemTotals.TotalQuantityWastedByMenuItemIdEatIn)
            {
                if (!itemSales.TryGetValue(item.Key, out TheoreticalUsagePeriodSale? value))
                {
                    itemSales[item.Key] = new TheoreticalUsagePeriodSale()
                    {
                        TheoreticalUsagePeriodId = period.Id,
                        MenuItemId = item.Key,
                        EatInQuantityWasted = item.Value,
                    };
                }
                else
                {
                    value.EatInQuantityWasted = item.Value;
                }
            }
            foreach (var item in menuItemTotals.TotalQuantitySoldByMenuItemIdTakeAway)
            {
                if (!itemSales.TryGetValue(item.Key, out TheoreticalUsagePeriodSale? value))
                {
                    itemSales[item.Key] = new TheoreticalUsagePeriodSale()
                    {
                        TheoreticalUsagePeriodId = period.Id,
                        MenuItemId = item.Key,
                        TakeAwayQuantitySold = item.Value,
                    };
                }
                else
                {
                    value.TakeAwayQuantitySold = item.Value;
                }
            }
            foreach (var item in menuItemTotals.TotalQuantityWastedByMenuItemIdTakeAway)
            {
                if (!itemSales.TryGetValue(item.Key, out TheoreticalUsagePeriodSale? value))
                {
                    itemSales[item.Key] = new TheoreticalUsagePeriodSale()
                    {
                        TheoreticalUsagePeriodId = period.Id,
                        MenuItemId = item.Key,
                        TakeAwayQuantityWasted = item.Value,
                    };
                }
                else
                {
                    value.TakeAwayQuantityWasted = item.Value;
                }
            }

            var totalQuantitySold = menuItemTotals.TotalQuantitySoldByMenuItemIdEatIn.ToDictionary(x => x.Key, x => x.Value);
            foreach (var record in menuItemTotals.TotalQuantitySoldByMenuItemIdTakeAway)
            {
                if (!totalQuantitySold.ContainsKey(record.Key))
                {
                    totalQuantitySold[record.Key] = record.Value;
                }
                else
                {
                    totalQuantitySold[record.Key] += record.Value;
                }
            }

            await stockContext.SaveChangesAsync();

            var itemUsage = new Dictionary<int, TheoreticalUsagePeriodStockUsage>();

            foreach (var record in menuItemTotals.TotalQuantitySoldByMenuItemIdEatIn)
            {
                var stockItems = readOnlyStockContext.MenuItemStockItemMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == record.Key && itemIds.Contains(x.StockItemId) && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatIn));
                foreach (var item in stockItems)
                {
                    TheoreticalUsagePeriodStockUsage stockUsage = itemUsage.TryGetValue(item.StockItemId, out TheoreticalUsagePeriodStockUsage? value) ? value : new TheoreticalUsagePeriodStockUsage()
                    {
                        TheoreticalUsagePeriodId = period.Id,
                        ItemId = item.StockItemId,
                    };

                    stockUsage.UnitQuantityUsed += (item.Quantity ?? 0) * record.Value;

                    itemUsage[item.StockItemId] = stockUsage;
                }

                var recipes = readOnlyStockContext.MenuItemRecipeMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == record.Key && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatIn));
                foreach (var recipe in recipes)
                {
                    foreach (var item in recipe.Recipe.RecipeItems.Where(x => x.RecipeRevisionId == period.RecipeRevisionId))
                    {
                        TheoreticalUsagePeriodStockUsage stockUsage = itemUsage.TryGetValue(item.ItemId, out TheoreticalUsagePeriodStockUsage? value) ? value : new TheoreticalUsagePeriodStockUsage()
                        {
                            TheoreticalUsagePeriodId = period.Id,
                            ItemId = item.ItemId,
                        };

                        stockUsage.UnitQuantityUsed += (item.Quantity ?? 0) * record.Value;

                        itemUsage[item.ItemId] = stockUsage;
                    }
                }
            }

            foreach (var record in menuItemTotals.TotalQuantitySoldByMenuItemIdTakeAway)
            {
                var stockItems = readOnlyStockContext.MenuItemStockItemMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == record.Key && itemIds.Contains(x.StockItemId) && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatOut));
                foreach (var item in stockItems)
                {
                    TheoreticalUsagePeriodStockUsage stockUsage = itemUsage.TryGetValue(item.StockItemId, out TheoreticalUsagePeriodStockUsage? value) ? value : new TheoreticalUsagePeriodStockUsage()
                    {
                        TheoreticalUsagePeriodId = period.Id,
                        ItemId = item.StockItemId,
                    };

                    stockUsage.UnitQuantityUsed += (item.Quantity ?? 0) * record.Value;

                    itemUsage[item.StockItemId] = stockUsage;
                }

                var recipes = readOnlyStockContext.MenuItemRecipeMaps.AsNoTracking().Where(x => x.RecipeRevisionId == period.RecipeRevisionId && x.MenuItemId == record.Key && (x.MapType == (int)MenuItemRecipeType.Always || x.MapType == (int)MenuItemRecipeType.EatOut));
                foreach (var recipe in recipes)
                {
                    foreach (var item in recipe.Recipe.RecipeItems.Where(x => x.RecipeRevisionId == period.RecipeRevisionId))
                    {
                        TheoreticalUsagePeriodStockUsage stockUsage = itemUsage.TryGetValue(item.ItemId, out TheoreticalUsagePeriodStockUsage? value) ? value : new TheoreticalUsagePeriodStockUsage()
                        {
                            TheoreticalUsagePeriodId = period.Id,
                            ItemId = item.ItemId,
                        };

                        stockUsage.UnitQuantityUsed += (item.Quantity ?? 0) * record.Value;

                        itemUsage[item.ItemId] = stockUsage;
                    }
                }
            }

            foreach (var item in itemUsage)
            {
                var stockItem = await readOnlyStockContext.Items.FindAsync(item.Key);
                if (useModel)
                {
                    item.Value.Fifocost = item.Value.UnitQuantityUsed * stockItem.ModelPreppedCost;
                }
                else
                {
                    var wastagePercentage = stockItem.WastagePercentage;
                    var storeOverride = stockItem.ItemDetailStoreOverrides.FirstOrDefault(x => x.StoreId == storeId);
                    if (storeOverride?.WastagePercentage.HasValue ?? false)
                    {
                        wastagePercentage = storeOverride.WastagePercentage.Value;
                    }

                    item.Value.Fifocost = await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, stockItem, item.Value.UnitQuantityUsed, openingStockTake, [], null, wastagePercentage);
                }
            }

            return new TheoreticalUsagePeriodInfo()
            {
                RecipeRevisionId = period.RecipeRevisionId,
                SalesByMenuItemId = itemSales,
                UsageByStockItemId = itemUsage,
            };
        }
    }
}

