﻿using Aura.Common.Extensions;
using Aura.Database.Context.Stock;
using Microsoft.EntityFrameworkCore;
using System.Collections.Generic;
using System.Linq;

namespace Aura.Manager.Stock.Reports
{
    public class FifoCalculator
    {
        internal async Task<decimal?> GetFifoCostForItemAsync(
            ReadOnlyStockContext readOnlyStockContext,
            Item item, 
            decimal usage, 
            StockTake openingStockTake, 
            List<Grv> grvs, 
            GrossProfitReport? report, 
            decimal wastagePercentage)
        {
            if (usage == 0)
            {
                return 0;
            }

            var cost = 0M;

            var stockToAccountFor = usage;

            var openingWindow = await readOnlyStockContext.StockTakeWindows.AsNoTracking().OrderByDescending(x => x.Id).FirstOrDefaultAsync(x => (report == null || x.GrossProfitReportId == report.Id) && x.StockTakeId == openingStockTake.Id && x.ItemId == item.Id);

            if (openingWindow == null)
            {
                return null;
            }

            foreach (var grv in openingWindow.StockTakeWindowGrvBalances.OrderByDescending(x => x.Id).Where(x => x.RemainingUnitQuantity > 0))
            {
                var stockAccounted = Math.Min(stockToAccountFor, grv.RemainingUnitQuantity);
                cost += stockAccounted * grv.UnitCost;
                stockToAccountFor -= stockAccounted;

                if (stockToAccountFor <= 0)
                {
                    break;
                }
            }

            if (stockToAccountFor > 0)
            {
                foreach (var grv in grvs)
                {
                    var grvItem = grv.GrvItems.FirstOrDefault(x => x.ItemId == item.Id);

                    if (grvItem != null)
                    {
                        var actualReceivedQuantity = grvItem.StockMovements.Sum(sm => sm.Quantity);
                        var actualTotalCost = grvItem.TotalCostExcl.ProRata(grvItem.QuantityGrv, actualReceivedQuantity);
                        var quantityWithWastage = actualReceivedQuantity * (100M - wastagePercentage) / 100M;
                        var costPerUnit = quantityWithWastage > 0 ? actualTotalCost / quantityWithWastage : 0M;

                        if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
                        {
                            var stockAccounted = Math.Min(stockToAccountFor, quantityWithWastage);
                            cost += stockAccounted * costPerUnit;
                            stockToAccountFor -= stockAccounted;
                        }
                        else
                        {
                            var stockReturned = Math.Min(stockToAccountFor, quantityWithWastage);
                            cost += stockReturned * costPerUnit;
                            stockToAccountFor -= stockReturned;
                        }

                        if (stockToAccountFor <= 0)
                        {
                            break;
                        }
                    }
                }
            }

            return cost;
        }


    }
}

