     1→using Aura.CloudBusinessLogic;
     2→using Aura.Common;
     3→using Aura.Common.Database.Extensions;
     4→using Aura.Database.Context.Stock;
     5→using Aura.Manager.Stock.Extensions;
     6→using AuraCloudInterface.Base;
     7→using AuraCloudInterface.Stock.Processed;
     8→using Microsoft.EntityFrameworkCore;
     9→using System.Linq.Expressions;
    10→using ProcessedStockType = AuraCloudInterface.Stock.Processed.ProcessedStockType;
    11→using ProcessedStockStatus = AuraCloudInterface.Stock.Processed.ProcessedStockStatus;
    12→using Aura.Common.Extensions;
    13→
    14→namespace Aura.Manager.Stock
    15→{
    16→    public class ProcessedStockManager(StockContext dbContext, ReadOnlyStockContext readOnlyContext, StoreManager storeManager)
    17→    {
    18→        public async Task<CreateProcessedStockResponse> CreateProcessedStockAsync(int brandId, string? storeId, ProcessedStockType processedStockType, ProcessedStockRequest request, int userId)
    19→        {            
    20→            if (request.Status == ProcessedStockStatus.Posted)
    21→            {
    22→                ApiException.ThrowIfTrue(!request.Ingredients?.Any() ?? false, "Cannot create a posted batch without ingredients");                
    23→            }
    24→            
    25→            var stockItem = await readOnlyContext.GetOrExceptStockItemAsync(brandId, request.ItemId, storeId);
    26→
    27→            ValidateStockItemProcessedStockType(stockItem, processedStockType, "create");            
    28→            ValidateNumberOfBatches(processedStockType, request.NumberOfBatches);
    29→            await ValidateStoreAsync(brandId, storeId);
    30→
    31→            // Check for existing record created within 1 minute of the request's Timestamp
    32→            var requestTimeUtc = request.Timestamp.FromUnixTimestamp();
    33→            var startTime = requestTimeUtc.AddMinutes(-1);
    34→            var endTime = requestTimeUtc.AddMinutes(1);
    35→
    36→           var recentRecordExists = await readOnlyContext.ProcessedStocks
    37→                .Where(b => b.BrandId == brandId 
    38→                         && b.StoreId == storeId 
    39→                         && b.ItemId == request.ItemId
    40→                         && b.ProcessedStockTypeId == (int)processedStockType
    41→                         && b.Timestamp >= startTime 
    42→                         && b.Timestamp <= endTime)
    43→                .AnyAsync();
    44→
    45→#if !DEBUG
    46→            ApiException.ThrowIfTrue(recentRecordExists, "A record for this stock item was already created within 1 minute of the specified time.");
    47→#endif
    48→
    49→            const int maxRetries = 3;
    50→
    51→            var retryCount = 0;
    52→
    53→            while (retryCount < maxRetries)
    54→            {
    55→                try
    56→                {
    57→                    var strategy = dbContext.Database.CreateExecutionStrategy();
    58→                    return await strategy.ExecuteAsync(async () =>
    59→                    {
    60→                        await using var transaction = await dbContext.Database.BeginTransactionAsync();
    61→
    62→                        var datePart = request.Timestamp.FromUnixTimestamp().ToString("yyyy-MM-dd");
    63→                        var searchString = $"{stockItem.Code}-{datePart}-";
    64→
    65→                        var latestForToday = await dbContext.ProcessedStocks
    66→                            .Where(x => x.BrandId == brandId && x.StoreId == storeId && x.BatchReference.StartsWith(searchString))
    67→                            .OrderByDescending(x => x.BatchReference)
    68→                            .FirstOrDefaultAsync();
    69→
    70→                        var sequence = 1;
    71→                        if (latestForToday != null)
    72→                        {
    73→                            var lastSequence = int.Parse(latestForToday.BatchReference.Split('-').Last());
    74→                            sequence = lastSequence + 1;
    75→                        }
    76→
    77→                        var processedStock = new ProcessedStock();
    78→                        request.MapTo(brandId, storeId, processedStock);
    79→                        processedStock.ProcessedStockTypeId = (int)processedStockType;
    80→                        processedStock.BatchReference = $"{searchString}{sequence:D3}";
    81→                                                
    82→                        if (request.Status == ProcessedStockStatus.Posted)
    83→                        {                            
    84→                            processedStock.PostedByUserId = userId;
    85→                            processedStock.PostedAt = DateTime.UtcNow;
    86→                        }
    87→                        
    88→                        dbContext.ProcessedStocks.Add(processedStock);
    89→                        await dbContext.SaveChangesAsync();
    90→
    91→                        // Add ingredients if provided
    92→                        if (request.Ingredients != null && request.Ingredients.Length > 0)
    93→                        {
    94→                            foreach (var ingredient in request.Ingredients)
    95→                            {
    96→                                var processedStockItem = new ProcessedStockItem
    97→                                {
    98→                                    ProcessedStockId = processedStock.Id,
    99→                                    ItemId = ingredient.ItemId,
   100→                                    ExpectedQuantity = ingredient.ExpectedQuantity,
   101→                                    ActualQuantity = ingredient.ActualQuantity
   102→                                };
   103→                                dbContext.ProcessedStockItems.Add(processedStockItem);
   104→                            }
   105→                            await dbContext.SaveChangesAsync();
   106→                        }
   107→
   108→                        await transaction.CommitAsync();
   109→
   110→                        return new CreateProcessedStockResponse(processedStock.Id, processedStock.BatchReference, request.Status, processedStock.PostedAt?.ToUnixTimestamp());
   111→                    });
   112→                }
   113→                catch (DbUpdateException ex)
   114→                {
   115→                    retryCount++;
   116→
   117→                    if (retryCount == maxRetries)
   118→                    {
   119→                        throw new ApiException(System.Net.HttpStatusCode.BadRequest, $"Unable to generate unique batch number after multiple attempts. Error: {ex.Message}");
   120→                    }
   121→                }
   122→                catch (Exception ex)
   123→                {
   124→                    throw new ApiException(System.Net.HttpStatusCode.InternalServerError, $"Error creating batch: {ex.Message}");
   125→                }
   126→            }
   127→
   128→            throw new ApiException(System.Net.HttpStatusCode.InternalServerError, "Unexpected error creating batch");
   129→        }
   130→
   131→        public async Task<ListResponse<ProcessedStockSummary>> ListProcessedStockAsync(int brandId, ListProcessedStockArgs args)
   132→        {
   133→            var sourceFilter = args.Source switch
   134→            {
   135→                ProcessedStockSource.Manual => (Expression<Func<ProcessedStock, bool>>)(x => x.RecipeId == null && x.ProcessedStockId == null),
   136→                ProcessedStockSource.Batch => (Expression<Func<ProcessedStock, bool>>)(x => x.ProcessedStockId != null),
   137→                ProcessedStockSource.Recipe => (Expression<Func<ProcessedStock, bool>>)(x => x.RecipeId != null),
   138→                _ => null
   139→            };
   140→
   141→            var query = readOnlyContext.ProcessedStocks
   142→                .Include(x => x.Item).ThenInclude(item => item.Category)
   143→                .Include(x => x.Item).ThenInclude(item => item.UnitOfMeasure)
   144→                .Include(x => x.ProcessedStockStatus)
   145→                .Include(x => x.ProcessedStockItems)
   146→                .WhereIfElse(!string.IsNullOrEmpty(args.StoreId), 
   147→                    x => x.BrandId == brandId && x.StoreId == args.StoreId, 
   148→                    x => x.BrandId == brandId && x.StoreId == null)
   149→                .Where(x => x.ProcessedStockTypeId == (int)args.ProcessedStockType)
   150→                .WhereIf(args.IsArchived.HasValue, x => x.IsArchived == args.IsArchived!.Value)
   151→                .WhereIf(args.FromTimestamp.HasValue, x => x.Timestamp >= args.FromTimestamp!.Value.FromUnixTimestamp())
   152→                .WhereIf(args.ToTimestamp.HasValue, x => x.Timestamp <= args.ToTimestamp!.Value.FromUnixTimestamp())
   153→                .WhereIf(args.StockListId is > 0, x => x.Item.StockListItems.Any(sli => sli.StockListId == args.StockListId!.Value))
   154→                .WhereIf(args.StockCategoryId is > 0, x => x.Item.CategoryId == args.StockCategoryId!.Value)
   155→                .WhereIf(args.ItemId is > 0, x => x.ItemId == args.ItemId!.Value)
   156→                .WhereIf(args.Status.HasValue, x => x.ProcessedStockStatusId == (int)args.Status!.Value)
   157→                .WhereIf(sourceFilter != null, sourceFilter!)
   158→                .WhereIf(!string.IsNullOrWhiteSpace(args.Search), x =>
   159→                    x.Item.Name.Contains(args.Search!) ||
   160→                    x.Item.Category.Name.Contains(args.Search!) ||
   161→                    (x.Item.Code != null && x.Item.Code.Contains(args.Search!)));
   162→
   163→            var orderFunctions = new Dictionary<string, Expression<Func<ProcessedStock, object>>>
   164→            {
   165→                { nameof(ProcessedStockSummary.BatchReference), x => x.BatchReference },
   166→                { "LocalTimestamp", x => x.Timestamp },
   167→                { nameof(ProcessedStockSummary.Timestamp), x => x.Timestamp },
   168→                { nameof(ProcessedStockSummary.ItemCode), x => x.Item.Code ?? string.Empty },
   169→                { nameof(ProcessedStockSummary.ItemName), x => x.Item.Name },
   170→                { nameof(ProcessedStockSummary.ItemCategoryName), x => x.Item.Category.Name },
   171→                { nameof(ProcessedStockSummary.ItemUnitOfMeasureName), x => x.Item.UnitOfMeasure.Name },
   172→                { nameof(ProcessedStockSummary.NumberOfBatches), x => x.NumberOfBatches },
   173→                { nameof(ProcessedStockSummary.QuantityPerBatch), x => x.QuantityPerBatch },
   174→                { nameof(ProcessedStockSummary.Notes), x => x.Notes ?? string.Empty },                
   175→                { nameof(ProcessedStockSummary.Status), x => x.ProcessedStockStatus.Name },
   176→                { nameof(ProcessedStockSummary.IsArchived), x => x.IsArchived },
   177→                { nameof(ProcessedStockSummary.IsMasterRecipe), x => x.IsMasterRecipe }
   178→            };
   179→            query = query.AddOrderBy(args.Sort, orderFunctions, defaultOrder: x => x.Timestamp, isDefaultOrderDescending: true);
   180→
   181→            var paged = await query.PaginateAsync(args.Page ?? 1, args.PageSize ?? 15);
   182→
   183→            return new ListResponse<ProcessedStockSummary>
   184→            {
   185→                Items = paged.Page.Select(x => x.ToSummary(includeIngredients: false)).ToList(),
   186→                Pagination = paged.Pagination
   187→            };
   188→        }
   189→
   190→        public async Task<ProcessedStockSummary?> ViewProcessedStockAsync(int brandId, string? storeId, int id)
   191→        {
   192→            var query = readOnlyContext.ProcessedStocks
   193→                .Include(b => b.Item).ThenInclude(item => item.Category)
   194→                .Include(b => b.Item).ThenInclude(item => item.UnitOfMeasure)
   195→                .Include(b => b.ProcessedStockStatus)                                
   196→                .Include(b => b.ProcessedStockItems)
   197→                .Where(b => b.Item.BrandId == brandId && b.Id == id);
   198→
   199→            if (!string.IsNullOrEmpty(storeId))
   200→            {
   201→                query = query.Where(b => b.StoreId == storeId);
   202→            }
   203→
   204→            var result = await query.FirstOrDefaultAsync();
   205→            return result?.ToSummary(includeIngredients: true);
   206→        }
   207→
   208→        private void ValidateNotPosted(ProcessedStock processedStock, string operation)
   209→        {
   210→            ApiException.ThrowIfTrue(processedStock.ProcessedStockStatusId == (int)ProcessedStockStatus.Posted, $"Cannot {operation} a batch that has already been posted.");
   211→        }
   212→
   213→        private static void ValidateNumberOfBatches(ProcessedStockType processedStockType, decimal numberOfBatches)
   214→        {
   215→            switch (processedStockType)
   216→            {
   217→                case ProcessedStockType.Manufactured:
   218→                    ApiException.ThrowIfTrue(numberOfBatches <= 0, "Number of batches must be greater than 0 for manufacturing.");
   219→                    break;
   220→                    
   221→                case ProcessedStockType.Apportioned:
   222→                    ApiException.ThrowIfTrue(numberOfBatches != 1, "Number of batches must be exactly 1 for apportioning.");
   223→                    break;
   224→                    
   225→                default:
   226→                    throw new ApiException(System.Net.HttpStatusCode.BadRequest, $"Unknown ProcessedStockType: {processedStockType}");
   227→            }
   228→        }
   229→
   230→
   231→
   232→        public async Task<UpdateProcessedStockResponse> UpdateProcessedStockAsync(int brandId, string? storeId, int id, int userId, ProcessedStockRequest request)
   233→        {
   234→            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, id);
   235→
   236→            // Only allow updates if the current batch is in Draft status
   237→            ValidateNotPosted(processedStock, "update");
   238→
   239→            var stockItem = await readOnlyContext.GetOrExceptStockItemAsync(brandId, request.ItemId, storeId);
   240→
   241→            // If the item ID is changing, validate that the new item has the same ProcessedStockType
   242→            if (processedStock.ItemId != request.ItemId)
   243→            {
   244→                ValidateStockItemProcessedStockType(stockItem, (ProcessedStockType)processedStock.ProcessedStockTypeId, "update to a different item");
   245→            }
   246→            
   247→            ValidateNumberOfBatches((ProcessedStockType)processedStock.ProcessedStockTypeId, request.NumberOfBatches);
   248→            await ValidateStoreAsync(brandId, storeId);            
   249→
   250→            request.MapTo(brandId, storeId, processedStock);
   251→            
   252→            // If the request status is Posted, set the posted timestamp and user
   253→            if (request.Status == ProcessedStockStatus.Posted)
   254→            {
   255→                processedStock.PostedByUserId = userId;
   256→                processedStock.PostedAt = DateTime.UtcNow;
   257→            }
   258→
   259→            await dbContext.SaveChangesAsync();
   260→
   261→            return new UpdateProcessedStockResponse(request.Status, processedStock.PostedAt?.ToUnixTimestamp());
   262→        }
   263→
   264→        public async Task<PostProcessedStockResponse> PostProcessedStockAsync(int brandId, string? storeId, int id, int userId, PostProcessedStockRequest request)
   265→        {
   266→            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, id);
   267→            
   268→            ValidateNotPosted(processedStock, "post");
   269→            
   270→            processedStock.Timestamp = request.Timestamp?.FromUnixTimestamp() ?? DateTime.UtcNow;
   271→            processedStock.ProcessedStockStatusId = (int)AuraCloudInterface.Stock.Processed.ProcessedStockStatus.Posted;            
   272→            processedStock.PostedByUserId = userId;
   273→            processedStock.PostedAt = DateTime.UtcNow;            
   274→
   275→            await dbContext.SaveChangesAsync();
   276→              
   277→            return new PostProcessedStockResponse
   278→            (                
   279→                PostedByUserId: processedStock.PostedByUserId.Value,
   280→                PostedTimestamp: processedStock.PostedAt!.Value.ToUnixTimestamp()
   281→            );
   282→        }
   283→
   284→        public async Task<UpdateResponse> ArchiveProcessedStockAsync(int brandId, string? storeId, int id, ArchiveProcessedStockRequest request)
   285→        {
   286→            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, id);
   287→
   288→            if (!processedStock.IsArchived)
   289→            {
   290→                processedStock.IsArchived = true;
   291→                processedStock.ArchivedReason = request.ArchivedReason;
   292→                await dbContext.SaveChangesAsync();
   293→            }
   294→
   295→            return new UpdateResponse();
   296→        }
   297→
   298→        public async Task<UpdateResponse> LoadMasterTemplateAsync(int brandId, string? storeId, int id, int standardTemplateProcessedStockId)
   299→        {
   300→            var processedStocks = await dbContext.ProcessedStocks
   301→                .Include(ps => ps.ProcessedStockItems)
   302→                .Where(ps => ps.BrandId == brandId && ps.StoreId == storeId && (ps.Id == id || ps.Id == standardTemplateProcessedStockId))
   303→                .ToDictionaryAsync(ps => ps.Id);
   304→
   305→            var processedStock = processedStocks.GetValueOrDefault(id);
   306→            ApiException.ThrowIfTrue(processedStock == null, $"Processed stock with id {id} not found.");
   307→
   308→            var standardTemplateProcessedStock = processedStocks.GetValueOrDefault(standardTemplateProcessedStockId);
   309→            ApiException.ThrowIfTrue(standardTemplateProcessedStock == null, $"Standard template with id {standardTemplateProcessedStockId} not found.");
   310→
   311→            ValidateNotPosted(processedStock, "load standard template");
   312→
   313→            ApiException.ThrowIfTrue(!standardTemplateProcessedStock.IsMasterRecipe, $"The specified processed stock id {standardTemplateProcessedStockId} is not marked as a master recipe.");
