using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.Database.Extensions;
using Aura.Database.Context.Stock;
using Aura.Manager.Stock.Extensions;
using AuraCloudInterface.Base;
using AuraCloudInterface.Stock.Processed;
using Microsoft.EntityFrameworkCore;
using System.Linq.Expressions;
using ProcessedStockType = AuraCloudInterface.Stock.Processed.ProcessedStockType;
using ProcessedStockStatus = AuraCloudInterface.Stock.Processed.ProcessedStockStatus;
using Aura.Common.Extensions;

namespace Aura.Manager.Stock
{
    public class ProcessedStockManager(StockContext dbContext, ReadOnlyStockContext readOnlyContext, StoreManager storeManager)
    {
        public async Task<CreateProcessedStockResponse> CreateProcessedStockAsync(int brandId, string? storeId, ProcessedStockType processedStockType, ProcessedStockRequest request, int userId)
        {            
            if (request.Status == ProcessedStockStatus.Posted)
            {
                ApiException.ThrowIfTrue(!request.Ingredients?.Any() ?? false, "Cannot create a posted batch without ingredients");                
            }

            var stockItem = await readOnlyContext.GetOrExceptStockItemAsync(brandId, request.ItemId, storeId);

            ValidateStockItemProcessedStockType(stockItem, processedStockType, "create");            
            ValidateNumberOfBatches(processedStockType, request.NumberOfBatches);
            await ValidateStoreAsync(brandId, storeId);

            // Check for existing record created within 1 minute of the request's Timestamp
            var requestTimeUtc = request.Timestamp.FromUnixTimestamp();
            var startTime = requestTimeUtc.AddMinutes(-1);
            var endTime = requestTimeUtc.AddMinutes(1);

           var recentRecordExists = await readOnlyContext.ProcessedStocks
                .Where(b => b.BrandId == brandId 
                         && b.StoreId == storeId 
                         && b.ItemId == request.ItemId
                         && b.ProcessedStockTypeId == (int)processedStockType
                         && b.Timestamp >= startTime 
                         && b.Timestamp <= endTime)
                .AnyAsync();

#if !DEBUG
            ApiException.ThrowIfTrue(recentRecordExists, "A record for this stock item was already created within 1 minute of the specified time.");
#endif

            const int maxRetries = 3;

            var retryCount = 0;

            while (retryCount < maxRetries)
            {
                try
                {
                    var strategy = dbContext.Database.CreateExecutionStrategy();
                    return await strategy.ExecuteAsync(async () =>
                    {
                        await using var transaction = await dbContext.Database.BeginTransactionAsync();

                        var datePart = request.Timestamp.FromUnixTimestamp().ToString("yyyy-MM-dd");
                        var searchString = $"{stockItem.Code}-{datePart}-";

                        var latestForToday = await dbContext.ProcessedStocks
                            .Where(x => x.BrandId == brandId && x.StoreId == storeId && x.BatchReference.StartsWith(searchString))
                            .OrderByDescending(x => x.BatchReference)
                            .FirstOrDefaultAsync();

                        var sequence = 1;
                        if (latestForToday != null)
                        {
                            var lastSequence = int.Parse(latestForToday.BatchReference.Split('-').Last());
                            sequence = lastSequence + 1;
                        }

                        var processedStock = new ProcessedStock();
                        request.MapTo(brandId, storeId, processedStock);
                        processedStock.ProcessedStockTypeId = (int)processedStockType;
                        processedStock.BatchReference = $"{searchString}{sequence:D3}";

                        if (request.Status == ProcessedStockStatus.Posted)
                        {                            
                            processedStock.PostedByUserId = userId;
                            processedStock.PostedAt = DateTime.UtcNow;
                        }

                        dbContext.ProcessedStocks.Add(processedStock);
                        await dbContext.SaveChangesAsync();

                        // Add ingredients if provided
                        if (request.Ingredients != null && request.Ingredients.Length > 0)
                        {
                            foreach (var ingredient in request.Ingredients)
                            {
                                var processedStockItem = new ProcessedStockItem
                                {
                                    ProcessedStockId = processedStock.Id,
                                    ItemId = ingredient.ItemId,
                                    ExpectedQuantity = ingredient.ExpectedQuantity,
                                    ActualQuantity = ingredient.ActualQuantity
                                };
                                dbContext.ProcessedStockItems.Add(processedStockItem);
                            }
                            await dbContext.SaveChangesAsync();
                        }

                        await transaction.CommitAsync();

                        return new CreateProcessedStockResponse(processedStock.Id, processedStock.BatchReference, request.Status, processedStock.PostedAt?.ToUnixTimestamp());
                    });
                }
                catch (DbUpdateException ex)
                {
                    retryCount++;

                    if (retryCount == maxRetries)
                    {
                        throw new ApiException(System.Net.HttpStatusCode.BadRequest, $"Unable to generate unique batch number after multiple attempts. Error: {ex.Message}");
                    }
                }
                catch (Exception ex)
                {
                    throw new ApiException(System.Net.HttpStatusCode.InternalServerError, $"Error creating batch: {ex.Message}");
                }
            }

            throw new ApiException(System.Net.HttpStatusCode.InternalServerError, "Unexpected error creating batch");
        }

        public async Task<ListResponse<ProcessedStockSummary>> ListProcessedStockAsync(int brandId, ListProcessedStockArgs args)
        {
            var sourceFilter = args.Source switch
            {
                ProcessedStockSource.Manual => (Expression<Func<ProcessedStock, bool>>)(x => x.RecipeId == null && x.ProcessedStockId == null),
                ProcessedStockSource.Batch => (Expression<Func<ProcessedStock, bool>>)(x => x.ProcessedStockId != null),
                ProcessedStockSource.Recipe => (Expression<Func<ProcessedStock, bool>>)(x => x.RecipeId != null),
                _ => null
            };

            var query = readOnlyContext.ProcessedStocks
                .Include(x => x.Item).ThenInclude(item => item.Category)
                .Include(x => x.Item).ThenInclude(item => item.UnitOfMeasure)
                .Include(x => x.ProcessedStockStatus)
                .Include(x => x.ProcessedStockItems)
                .WhereIfElse(!string.IsNullOrEmpty(args.StoreId), 
                    x => x.BrandId == brandId && x.StoreId == args.StoreId, 
                    x => x.BrandId == brandId && x.StoreId == null)
                .Where(x => x.ProcessedStockTypeId == (int)args.ProcessedStockType)
                .WhereIf(args.IsArchived.HasValue, x => x.IsArchived == args.IsArchived!.Value)
                .WhereIf(args.FromTimestamp.HasValue, x => x.Timestamp >= args.FromTimestamp!.Value.FromUnixTimestamp())
                .WhereIf(args.ToTimestamp.HasValue, x => x.Timestamp <= args.ToTimestamp!.Value.FromUnixTimestamp())
                .WhereIf(args.StockListId is > 0, x => x.Item.StockListItems.Any(sli => sli.StockListId == args.StockListId!.Value))
                .WhereIf(args.StockCategoryId is > 0, x => x.Item.CategoryId == args.StockCategoryId!.Value)
                .WhereIf(args.ItemId is > 0, x => x.ItemId == args.ItemId!.Value)
                .WhereIf(args.Status.HasValue, x => x.ProcessedStockStatusId == (int)args.Status!.Value)
                .WhereIf(sourceFilter != null, sourceFilter!)
                .WhereIf(!string.IsNullOrWhiteSpace(args.Search), x =>
                    x.Item.Name.Contains(args.Search!) ||
                    x.Item.Category.Name.Contains(args.Search!) ||
                    (x.Item.Code != null && x.Item.Code.Contains(args.Search!)));

            var orderFunctions = new Dictionary<string, Expression<Func<ProcessedStock, object>>>
            {
                { nameof(ProcessedStockSummary.BatchReference), x => x.BatchReference },
                { "LocalTimestamp", x => x.Timestamp },
                { nameof(ProcessedStockSummary.Timestamp), x => x.Timestamp },
                { nameof(ProcessedStockSummary.ItemCode), x => x.Item.Code ?? string.Empty },
                { nameof(ProcessedStockSummary.ItemName), x => x.Item.Name },
                { nameof(ProcessedStockSummary.ItemCategoryName), x => x.Item.Category.Name },
                { nameof(ProcessedStockSummary.ItemUnitOfMeasureName), x => x.Item.UnitOfMeasure.Name },
                { nameof(ProcessedStockSummary.NumberOfBatches), x => x.NumberOfBatches },
                { nameof(ProcessedStockSummary.QuantityPerBatch), x => x.QuantityPerBatch },
                { nameof(ProcessedStockSummary.Notes), x => x.Notes ?? string.Empty },                
                { nameof(ProcessedStockSummary.Status), x => x.ProcessedStockStatus.Name },
                { nameof(ProcessedStockSummary.IsArchived), x => x.IsArchived },
                { nameof(ProcessedStockSummary.IsMasterRecipe), x => x.IsMasterRecipe }
            };
            query = query.AddOrderBy(args.Sort, orderFunctions, defaultOrder: x => x.Timestamp, isDefaultOrderDescending: true);

            var paged = await query.PaginateAsync(args.Page ?? 1, args.PageSize ?? 15);

            return new ListResponse<ProcessedStockSummary>
            {
                Items = paged.Page.Select(x => x.ToSummary(includeIngredients: false)).ToList(),
                Pagination = paged.Pagination
            };
        }

        public async Task<ProcessedStockSummary?> ViewProcessedStockAsync(int brandId, string? storeId, int id)
        {
            var query = readOnlyContext.ProcessedStocks
                .Include(b => b.Item).ThenInclude(item => item.Category)
                .Include(b => b.Item).ThenInclude(item => item.UnitOfMeasure)
                .Include(b => b.ProcessedStockStatus)                                
                .Include(b => b.ProcessedStockItems)
                .Where(b => b.Item.BrandId == brandId && b.Id == id);

            if (!string.IsNullOrEmpty(storeId))
            {
                query = query.Where(b => b.StoreId == storeId);
            }

            var result = await query.FirstOrDefaultAsync();
            return result?.ToSummary(includeIngredients: true);
        }

        private void ValidateNotPosted(ProcessedStock processedStock, string operation)
        {
            ApiException.ThrowIfTrue(processedStock.ProcessedStockStatusId == (int)ProcessedStockStatus.Posted, $"Cannot {operation} a batch that has already been posted.");
        }

        private static void ValidateNumberOfBatches(ProcessedStockType processedStockType, decimal numberOfBatches)
        {
            switch (processedStockType)
            {
                case ProcessedStockType.Manufactured:
                    ApiException.ThrowIfTrue(numberOfBatches <= 0, "Number of batches must be greater than 0 for manufacturing.");
                    break;

                case ProcessedStockType.Apportioned:
                    ApiException.ThrowIfTrue(numberOfBatches != 1, "Number of batches must be exactly 1 for apportioning.");
                    break;

                default:
                    throw new ApiException(System.Net.HttpStatusCode.BadRequest, $"Unknown ProcessedStockType: {processedStockType}");
            }
        }



        public async Task<UpdateProcessedStockResponse> UpdateProcessedStockAsync(int brandId, string? storeId, int id, int userId, ProcessedStockRequest request)
        {
            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, id);

            // Only allow updates if the current batch is in Draft status
            ValidateNotPosted(processedStock, "update");

            var stockItem = await readOnlyContext.GetOrExceptStockItemAsync(brandId, request.ItemId, storeId);

            // If the item ID is changing, validate that the new item has the same ProcessedStockType
            if (processedStock.ItemId != request.ItemId)
            {
                ValidateStockItemProcessedStockType(stockItem, (ProcessedStockType)processedStock.ProcessedStockTypeId, "update to a different item");
            }

            ValidateNumberOfBatches((ProcessedStockType)processedStock.ProcessedStockTypeId, request.NumberOfBatches);
            await ValidateStoreAsync(brandId, storeId);            

            request.MapTo(brandId, storeId, processedStock);

            // If the request status is Posted, set the posted timestamp and user
            if (request.Status == ProcessedStockStatus.Posted)
            {
                processedStock.PostedByUserId = userId;
                processedStock.PostedAt = DateTime.UtcNow;
            }

            await dbContext.SaveChangesAsync();

            return new UpdateProcessedStockResponse(request.Status, processedStock.PostedAt?.ToUnixTimestamp());
        }

        public async Task<PostProcessedStockResponse> PostProcessedStockAsync(int brandId, string? storeId, int id, int userId, PostProcessedStockRequest request)
        {
            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, id);

            ValidateNotPosted(processedStock, "post");

            processedStock.Timestamp = request.Timestamp?.FromUnixTimestamp() ?? DateTime.UtcNow;
            processedStock.ProcessedStockStatusId = (int)AuraCloudInterface.Stock.Processed.ProcessedStockStatus.Posted;            
            processedStock.PostedByUserId = userId;
            processedStock.PostedAt = DateTime.UtcNow;            

            await dbContext.SaveChangesAsync();

            return new PostProcessedStockResponse
            (                
                PostedByUserId: processedStock.PostedByUserId.Value,
                PostedTimestamp: processedStock.PostedAt!.Value.ToUnixTimestamp()
            );
        }

        public async Task<UpdateResponse> ArchiveProcessedStockAsync(int brandId, string? storeId, int id, ArchiveProcessedStockRequest request)
        {
            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, id);

            if (!processedStock.IsArchived)
            {
                processedStock.IsArchived = true;
                processedStock.ArchivedReason = request.ArchivedReason;
                await dbContext.SaveChangesAsync();
            }

            return new UpdateResponse();
        }

        public async Task<UpdateResponse> LoadMasterTemplateAsync(int brandId, string? storeId, int id, int standardTemplateProcessedStockId)
        {
            var processedStocks = await dbContext.ProcessedStocks
                .Include(ps => ps.ProcessedStockItems)
                .Where(ps => ps.BrandId == brandId && ps.StoreId == storeId && (ps.Id == id || ps.Id == standardTemplateProcessedStockId))
                .ToDictionaryAsync(ps => ps.Id);

            var processedStock = processedStocks.GetValueOrDefault(id);
            ApiException.ThrowIfTrue(processedStock == null, $"Processed stock with id {id} not found.");

            var standardTemplateProcessedStock = processedStocks.GetValueOrDefault(standardTemplateProcessedStockId);
            ApiException.ThrowIfTrue(standardTemplateProcessedStock == null, $"Standard template with id {standardTemplateProcessedStockId} not found.");

            ValidateNotPosted(processedStock, "load standard template");

            ApiException.ThrowIfTrue(!standardTemplateProcessedStock.IsMasterRecipe, $"The specified processed stock id {standardTemplateProcessedStockId} is not marked as a master recipe.");
            ApiException.ThrowIfTrue(standardTemplateProcessedStock.ItemId != processedStock.ItemId, "The standard template is for a different stock item.");
            ApiException.ThrowIfTrue(standardTemplateProcessedStock.ProcessedStockTypeId != processedStock.ProcessedStockTypeId,
                $"The standard template is for {(ProcessedStockType)standardTemplateProcessedStock.ProcessedStockTypeId} but this processed stock record is for {(ProcessedStockType)processedStock.ProcessedStockTypeId}.");

            // Clear existing items
            var existingItems = processedStock.ProcessedStockItems.ToList();
            if (existingItems.Any())
            {
                dbContext.ProcessedStockItems.RemoveRange(existingItems);
            }

            // Copy ingredients from the standard template
            var standardTemplateItems = standardTemplateProcessedStock.ProcessedStockItems;
            foreach (var ingredient in standardTemplateItems)
            {
                var newItem = new ProcessedStockItem
                {
                    ProcessedStockId = id,
                    ItemId = ingredient.ItemId,
                    ExpectedQuantity = ingredient.ExpectedQuantity,
                    ActualQuantity = ingredient.ActualQuantity,
                };
                dbContext.ProcessedStockItems.Add(newItem);
            }

            await dbContext.SaveChangesAsync();
            return new UpdateResponse();
        }

        public async Task<CreateResponse> CreateProcessedStockItemAsync(int brandId, string? storeId, int processedStockId, int stockItemId, ProcessedStockItemRequest request)
        {
            var processedStock = await dbContext.GetOrExceptProcessedStockAsync(brandId, storeId, processedStockId);
            ValidateNotPosted(processedStock, "add item to");

            var stockItem = await readOnlyContext.GetOrExceptStockItemAsync(brandId, stockItemId, storeId);

            var newItem = new ProcessedStockItem
            {
                ProcessedStockId = processedStockId,
                ItemId = stockItemId,
                ExpectedQuantity = request.ExpectedQuantity,
                ActualQuantity = request.ActualQuantity                
            };

            try
            {
                dbContext.ProcessedStockItems.Add(newItem);
                await dbContext.SaveChangesAsync();

                return new CreateResponse(newItem.Id);
            }
            catch (DbUpdateException ex) when (ex.Message.ToLower().Contains("unique constraint"))
            {
                ApiException.Throw("Item already exists");
                return null!;
            }
            catch (DbUpdateException ex)
            {
                throw new ApiException(System.Net.HttpStatusCode.InternalServerError, $"Error adding item: {ex.Message}");
            }
        }

        public async Task<UpdateResponse> UpdateProcessedStockItemAsync(int brandId, string? storeId, int processedStockId, int processedStockItemId, ProcessedStockItemRequest request)
        {
            var processedStockItem = await dbContext.GetOrExceptProcessedStockItemAsync(brandId, storeId, processedStockId, processedStockItemId);

            ValidateNotPosted(processedStockItem.ProcessedStock, "update item for");

            processedStockItem.ExpectedQuantity = request.ExpectedQuantity;
            processedStockItem.ActualQuantity = request.ActualQuantity;            

            await dbContext.SaveChangesAsync();
            return new UpdateResponse();
        }

        public async Task<ProcessedStockItemSummary?> ViewProcessedStockItemAsync(int brandId, string? storeId, int processedStockId, int processedStockItemId)
        {
            var processedStockItem = await readOnlyContext.GetOrExceptProcessedStockItemAsync(brandId, storeId, processedStockId, processedStockItemId);
            return processedStockItem.ToSummary();
        }

        public async Task<List<ProcessedStockItemSummary>> ListProcessedStockItemsAsync(int brandId, string? storeId, int processedStockId)
        {
            var processedStock = await readOnlyContext.GetOrExceptProcessedStockAsync(brandId, storeId, processedStockId);
            return processedStock.ProcessedStockItems.Select(x => x.ToSummary()).ToList();
        }

        public async Task<UpdateResponse> DeleteProcessedStockItemAsync(int brandId, string? storeId, int processedStockId, int processedStockItemId)
        {
            var processedStockItem = await dbContext.GetOrExceptProcessedStockItemAsync(brandId, storeId, processedStockId, processedStockItemId);

            ValidateNotPosted(processedStockItem.ProcessedStock, "delete item from");

            dbContext.ProcessedStockItems.Remove(processedStockItem);
            await dbContext.SaveChangesAsync();

            return new UpdateResponse();
        }

        public async Task<int?> GetProcessedStockIdWithMasterRecipeTemplateForStockItemAsync(int brandId, string? storeId, int stockItemId)
        {
            return await readOnlyContext.ProcessedStocks
                .Where(ps => ps.BrandId == brandId
                          && ps.StoreId == storeId
                          && ps.ItemId == stockItemId
                          && ps.IsMasterRecipe == true
                          && ps.ProcessedStockStatusId == (int)AuraCloudInterface.Stock.Processed.ProcessedStockStatus.Posted)
                .OrderByDescending(ps => ps.Timestamp)
                .ThenByDescending(ps => ps.Id)
                .Select(ps => (int?)ps.Id)
                .FirstOrDefaultAsync();            
        }

        private void ValidateStockItemProcessedStockType(Item stockItem, ProcessedStockType expectedProcessedStockType, string operation)
        {
            ApiException.ThrowIfTrue(stockItem.ProcessedStockTypeId == null, "The selected stock item does not have a ProcessedStockType configured and cannot be used in processed stock operations.");
            ApiException.ThrowIfTrue((ProcessedStockType)stockItem.ProcessedStockTypeId != expectedProcessedStockType, $"The selected stock item is configured for {(AuraCloudInterface.Stock.Processed.ProcessedStockType?)stockItem.ProcessedStockTypeId} but you are trying to {operation} a {expectedProcessedStockType} record.");
        }

        private async Task ValidateStoreAsync(int brandId, string? storeId)
        {
            if (!string.IsNullOrEmpty(storeId))
            {
                var store = await storeManager.ViewStoreSummaryAsync(storeId);
                ApiException.ThrowIfTrue(store == null, $"Store with id {storeId} not found.");
                ApiException.ThrowIfTrue(store.BrandId != brandId, $"Store with id {storeId} does not belong to brand with id {brandId}.");
            }
        }
    }
