#nullable enable

using System.Threading.Tasks;
using Aura.CloudBusinessLogic;
using Aura.Manager.Stock;
using AuraCloudInterface.Base;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Authorization;
using Aura.Common.WebService.Authorization;
using AuraCloudInterface.Constants;
using System.Collections.Generic;
using AuraCloudInterface.Stock.Processed;
using AuraCloudInterface.Attributes;
using Aura.Common;

namespace CloudGatewayApi.Controllers;

[ApiController]
[Route("brands/{brandId}/stock/processed")]
[Authorize]
public class ProcessedStockController(ProcessedStockManager processedStockManager, UserManager userManager, Aura.Manager.Permissions.PermissionsManager permissionsManager) : AuthenticatedControllerWithUser(userManager)
{
    [HttpPost("{processedStockType}")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<CreateProcessedStockResponse>> CreateProcessedStock([ValidId] int brandId, ProcessedStockType processedStockType, [FromQuery] string? storeId, [FromBody] ProcessedStockRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.CreateProcessedStockAsync(brandId, storeId, processedStockType, request, await GetAuthenticatedUserIdAsync()));
    }

    [HttpPut("{id}")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<UpdateResponse>> UpdateProcessedStock([ValidId] int brandId, [ValidId] int id, [FromQuery] string? storeId, [FromBody] ProcessedStockRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.UpdateProcessedStockAsync(brandId, storeId, id, await GetAuthenticatedUserIdAsync(), request));
    }

    [HttpGet]
    [AuthorizedRoles(PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<ListResponse<ProcessedStockSummary>>> ListProcessedStock([ValidId] int brandId, [FromQuery] ListProcessedStockArgs args)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, args.StoreId, PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.ListProcessedStockAsync(brandId, args));
    }

    [HttpGet("{id}")]
    [AuthorizedRoles(PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<ProcessedStockSummary>> ViewProcessedStock([ValidId] int brandId, [ValidId] int id, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.ViewProcessedStockAsync(brandId, storeId, id));
    }

    [HttpPut("{id}/post")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<PostProcessedStockResponse>> PostProcessedStock([ValidId] int brandId, [ValidId] int id, [FromBody] PostProcessedStockRequest request, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.PostProcessedStockAsync(brandId, storeId, id, await GetAuthenticatedUserIdAsync(), request));
    }

    [HttpPut("{id}/archive")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<UpdateResponse>> ArchiveProcessedStock([ValidId] int brandId, [ValidId] int id, [FromBody] ArchiveProcessedStockRequest request, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.ArchiveProcessedStockAsync(brandId, storeId, id, request));
    }

    [HttpPost("{id}/items/stock-item/{stockItemId}")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<CreateResponse>> CreateProcessedStockItem([ValidId] int brandId, [ValidId] int id, [ValidId] int stockItemId, [FromQuery] string? storeId, [FromBody] ProcessedStockItemRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.CreateProcessedStockItemAsync(brandId, storeId, id, stockItemId, request));
    }

    [HttpPut("{id}/items/{processedStockItemId}")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<UpdateResponse>> UpdateProcessedStockItem([ValidId] int brandId, [ValidId] int id, [ValidId] int processedStockItemId, [FromQuery] string? storeId, [FromBody] ProcessedStockItemRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.UpdateProcessedStockItemAsync(brandId, storeId, id, processedStockItemId, request));
    }

    [HttpGet("{id}/items/{processedStockItemId}")]
    [AuthorizedRoles(PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<ProcessedStockItemSummary>> ViewProcessedStockItem([ValidId] int brandId, [ValidId] int id, [ValidId] int processedStockItemId, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.ViewProcessedStockItemAsync(brandId, storeId, id, processedStockItemId));
    }

    [HttpGet("{id}/items")]
    [AuthorizedRoles(PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<List<ProcessedStockItemSummary>>> ListProcessedStockItems([ValidId] int brandId, [ValidId] int id, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.ListProcessedStockItemsAsync(brandId, storeId, id));
    }

    [HttpDelete("{id}/items/{processedStockItemId}")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<UpdateResponse>> DeleteProcessedStockItem([ValidId] int brandId, [ValidId] int id, [ValidId] int processedStockItemId, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.DeleteProcessedStockItemAsync(brandId, storeId, id, processedStockItemId));
    }

    [HttpPut("{id}/load-standard-template/{processedStockId}")]
    [AuthorizedRoles(PortalPermission.CanManageProcessedStock)]
    public async Task<ActionResult<UpdateResponse>> LoadStandardTemplate([ValidId] int brandId, [ValidId] int id, [ValidId] int processedStockId, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageProcessedStock);

        return Ok(await processedStockManager.LoadMasterTemplateAsync(brandId, storeId, id, processedStockId));
    }

    [HttpGet("standard-template-lookup/{stockItemId}")]
    [AuthorizedRoles(PortalPermission.CanViewProcessedStock)]
    public async Task<ActionResult<StandardTemplateIdResponse>> GetProcessedStockIdWithStandardTemplateForStockItem([ValidId] int brandId, [ValidId] int stockItemId, [FromQuery] string? storeId = null)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewProcessedStock, PortalPermission.CanManageProcessedStock);

        return Ok(new StandardTemplateIdResponse(await processedStockManager.GetProcessedStockIdWithMasterRecipeTemplateForStockItemAsync(brandId, storeId, stockItemId)));
    }
}

