﻿using Aura.Common.Database.Extensions;
using Aura.Database.Context.Stock;
using Aura.Manager.Stock.Extensions;
using AuraCloudInterface.Stock.Items;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using static Aura.Common.Database.Extensions.QueryableExtensions;

namespace Aura.Manager.Stock;

public class StockItemsManager(ILogger<StockItemsManager> logger, StockContext readWriteStockContext, ReadOnlyStockContext readOnlyStockContext)
{
    public async Task<IEnumerable<StockItemLookupSummary>> ListStockItemsForLookupAsync(int brandId, string? storeId, int? grvId, string? search, AuraCloudInterface.Stock.Processed.ProcessedStockType? processedStockType)
    {
        var resultQuery = readOnlyStockContext
            .StockItemFilter(brandId, storeId, search, false)
            .WhereIf(grvId is > 0, x => x.GrvItems.Any(g => g.GrvId == grvId!.Value))
            .WhereIf(processedStockType.HasValue, x => x.ProcessedStockTypeId == (int)processedStockType!)
            .OrderBy(x => x.Code).ThenBy(x => x.Name).ThenBy(x => x.UnitOfMeasure.Name)
            .Select(x => x.ToStockItemLookupSummary());

        return await (grvId is > 0 ? resultQuery : resultQuery.Take(20))
            .ToListAsync();
    }

    public async Task<LastPurchaseResponse> GetLastStockItemPurchaseAsync(int brandId, string storeId, int stockItemId, DateTime? forDate = null)
    {
        var query = readOnlyStockContext
            .GrvItems
            .Where(x => x.ItemId == stockItemId
                        && x.Item.BrandId == brandId
                        && x.Grv.StoreId == storeId
                        && x.Grv.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized
                        && x.Grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv
                        && x.QuantityGrv > 0)
            .WhereIf(forDate.HasValue, x => x.Grv.GrvDate <= forDate!.Value);

        return await query
            .OrderByDescending(x => x.Grv.GrvDate).ThenByDescending(x => x.Grv.Id)
            .Select(x => new LastPurchaseResponse(
                x.QuantityGrv.GetValueOrDefault(),
                x.StockMovements.Sum(y => y.Quantity) > 0 ? x.TotalCostExcl.GetValueOrDefault() / x.StockMovements.Sum(y => y.Quantity) : 0,
                x.Grv.SupplierId ?? 0,
                x.Grv.GrvDate,
                x.Grv.Id))
            .FirstOrDefaultAsync() ?? new LastPurchaseResponse(0, 0, 0, DateTime.MinValue, 0);
    }

    public async Task<StockItemsCountResponse> GetStockItemsCountAsync(int brandId, string? storeId = null, bool? isArchived = null)
    {

