﻿using Aura.Common.Extensions;
using Aura.Manager.Stock.Reports;
using Microsoft.AspNetCore.Mvc;
using System;
using System.Threading.Tasks;

namespace CloudGatewayApi.Controllers
{
    [Route("[controller]")]
    [ApiController]
    public class ReportsController(
        GrossProfitReportManager grossProfitReportManager,
        TheoreticalSalesReportManager theoreticalSalesReportManager,
        TheoreticalStockOnHandManager theoreticalStockOnHandManager,
        StockLossReportManager stockLossReportManager
    ) : ControllerBase
    {
        [HttpGet]
        [Route("/stores/{storeId}/reports/gross-profit/{fromStockTakeId}/{toStockTakeId}")]
        public async Task<ActionResult> GrossProfitReport(string storeId, int fromStockTakeId, int toStockTakeId, bool useModel, int[]? stockListIds)
        {
            return Ok(await grossProfitReportManager.GetGrossProfitReportAsync(storeId, fromStockTakeId, toStockTakeId, useModel, stockListIds));
        }

        [HttpGet]
        [Route("/stores/{storeId}/reports/theoreticaldrilldown/{fromStockTakeId}/{toStockTakeId}/{itemId}")]
        public async Task<ActionResult> TheoreticalSalesDrillDownReport(string storeId, int fromStockTakeId, int toStockTakeId, int itemId)
        {
            return Ok(await theoreticalSalesReportManager.GetTheoreticalSalesReportAsync(storeId, itemId, fromStockTakeId, toStockTakeId, false));
        }

        [HttpGet]
        [Route("/stores/{storeId}/reports/theoretical-stockonhand")]
        public async Task<ActionResult> TheoreticalStockOnHandReport(string storeId, int[]? items, long? timestamp)
        {
            var effectiveDate = timestamp.HasValue ? timestamp.Value.FromUnixTimestampMilliseconds() : DateTime.UtcNow;
            return Ok(await theoreticalStockOnHandManager.GetTheoreticalStockOnHandReportAsync(storeId, items, effectiveDate));
        }

        [HttpGet]
        [Route("/stores/{storeId}/reports/stock-losses/{fromStockTakeId}/{toStockTakeId}")]
        public async Task<ActionResult> StockLossReport(string storeId, int fromStockTakeId, int toStockTakeId)
        {
            return Ok(await stockLossReportManager.GetStockLossReportAsync(storeId, fromStockTakeId, toStockTakeId));
        }
    }
}

