﻿using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.Database.Extensions;
using Aura.Common.Extensions;
using Aura.Database.Context.Stock;
using AuraCloudInterface.Stock.TheoreticalStockOnHandReport;
using Microsoft.EntityFrameworkCore;
using System.Collections.Generic;
using System.Linq;

namespace Aura.Manager.Stock.Reports
{
    public class TheoreticalStockOnHandManager
    {
        private StockContext stockContext;
        private ReadOnlyStockContext readOnlyStockContext;
        private StoreManager storeManager;
        private StoreTransactionManager storeTransactionManager;
        private TheoreticalSalesReportManager theoreticalSalesReportManager;
        private FifoCalculator fifoCalculator;

        public TheoreticalStockOnHandManager(
            StockContext stockContext,
            ReadOnlyStockContext readOnlyStockContext,
            StoreManager storeManager,
            StoreTransactionManager storeTransactionManager,
            TheoreticalSalesReportManager theoreticalSalesReportManager,
            FifoCalculator fifoCalculator)
        {
            this.stockContext = stockContext;
            this.readOnlyStockContext = readOnlyStockContext;
            this.storeManager = storeManager;
            this.storeTransactionManager = storeTransactionManager;
            this.theoreticalSalesReportManager = theoreticalSalesReportManager;
            this.fifoCalculator = fifoCalculator;
        }


        public async Task<TheoreticalStockOnHandReportResponse> GetTheoreticalStockOnHandReportAsync(string storeId, int[]? itemIds, DateTime effectiveDate)
        {
            var store = await storeManager.ViewStoreSummaryAsync(storeId);
            if (store == null)
            {
                throw new ApiException(System.Net.HttpStatusCode.NotFound, $"Store {storeId} not found");
            }

            var startDate = DateTime.Now.Date.AddDays(-45);
            var stockTakes = await readOnlyStockContext.StockTakes
                .Where(x => x.StoreId == storeId && x.DateTime >= startDate && !x.IsArchived)
                .WhereIf(itemIds.OrEmptyIfNull().Any(), x => x.StockTakeItems.Any(i => itemIds!.Contains(i.ItemId)))
                .OrderByDescending(x => x.DateTime)
                .ToListAsync();

            var itemCounts = new Dictionary<int, StockTake>();
            foreach (var stockTake in stockTakes)
            {
                var stockTakeItems = await readOnlyStockContext.StockTakeItems.AsNoTracking().Where(x => x.StockTakeId == stockTake.Id).WhereIf(itemIds.Any(), x => itemIds.Contains(x.ItemId)).ToListAsync();

                foreach (var stockTakeItem in stockTakeItems)
                {
                    if (!itemCounts.ContainsKey(stockTakeItem.ItemId))
                    {
                        itemCounts[stockTakeItem.ItemId] = stockTake;
                    }
                }
            }

            return await GenerateStockOnHandReportAsync(store.BrandId, storeId, itemCounts, effectiveDate);
        }

        private async Task<TheoreticalStockOnHandReportResponse> GenerateStockOnHandReportAsync(
            int brandId, 
            string storeId, 
            Dictionary<int, StockTake> itemCounts, 
            DateTime effectiveDate)
        {
            var response = new TheoreticalStockOnHandReportResponse()
            {
                TheoreticalSOH = new List<TheoreticalSOH>(),
            };

            foreach (var itemCount in itemCounts)
            {
                var item = await readOnlyStockContext.Items.FindAsync(itemCount.Key);
                var openingStockTake = itemCount.Value;
                var usage = await theoreticalSalesReportManager.GetTheoreticalUsageAsync(brandId, storeId, openingStockTake, effectiveDate, [item.Id], false);

                var wastagePercentage = item.WastagePercentage;
                var storeOverride = item.ItemDetailStoreOverrides.FirstOrDefault(x => x.StoreId == storeId);
                if (storeOverride?.WastagePercentage.HasValue ?? false)
                {
                    wastagePercentage = storeOverride.WastagePercentage.Value;
                }

                var openingBulk = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.BulkCounted ?? 0m));
                var openingPrepped = Convert.ToDecimal(openingStockTake.StockTakeItems.First(x => x.ItemId == item.Id).StockTakeItemSubCounts.Sum(x => x.PreppedCounted ?? 0m));
                var openingStockOnHand = openingPrepped + openingBulk * (100 - wastagePercentage) / 100M;

                var grvs = await readOnlyStockContext.Grvs
                    .Include(x => x.GrvItems)
                    .ThenInclude(i => i.StockMovements)
                    .OrderBy(x => x.StockMovementAt)
                    .Where(x =>
                        x.StoreId == storeId &&
                        x.GrvItems.Any(i => i.ItemId == item.Id) &&
                        x.GrvStatusId == (int)AuraCloudInterface.Stock.Grv.GrvStatus.Finalized &&
                        (x.StockMovementAt >= openingStockTake.DateTime && x.StockMovementAt <= effectiveDate))
                    .ToListAsync();

                var balance = openingStockOnHand;
                var purchasesUnits = 0M;

                foreach (var grv in grvs)
                {
                    var grvItem = grv.GrvItems.First(x => x.ItemId == item.Id);

                    var actualReceivedQuantity = grvItem.StockMovements.Sum(sm => sm.Quantity);
                    var quantityWithWastage = actualReceivedQuantity * (100M - wastagePercentage) / 100M;

                    if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
                    {
                        balance += quantityWithWastage;
                        purchasesUnits += actualReceivedQuantity;
                    }
                    else
                    {
                        balance -= quantityWithWastage;
                        purchasesUnits -= actualReceivedQuantity;
                    }
                }

                var theoreticalUsage = 0M;
                foreach (var period in usage.Periods)
                {
                    foreach (var stockUsage in period.Value.UsageByStockItemId.Where(x => x.Key == item.Id))
                    {
                        theoreticalUsage += stockUsage.Value.UnitQuantityUsed;
                    }
                }

                response.TheoreticalSOH.Add(new TheoreticalSOH()
                {
                    ItemId = item.Id,
                    CategoryName = item.Category?.Name,
                    ItemName = item.Name,
                    ItemCode = item.Code,
                    StockType = item.StockType?.Name,
                    ItemStatus = item.IsArchived ? "Inactive" : "Active",
                    ItemDescription = item.Description,
                    UnitOfMeasure = item.UnitOfMeasure?.Name,
                    StockTakeId = openingStockTake.Id,
                    StockTakeDateTime = openingStockTake.DateTime,
                    OpeningUnits = openingStockOnHand,
                    PurchasesUnits = purchasesUnits,
                    ManufacturingUnits = 0M,
                    TransfersUnits = 0M,
                    TheoreticalUnitsSOH = theoreticalUsage,
                    TheoreticalFIFOValue = await fifoCalculator.GetFifoCostForItemAsync(readOnlyStockContext, item, openingStockOnHand + purchasesUnits - theoreticalUsage, openingStockTake, grvs, null, wastagePercentage),
                });
            }

            return response;
        }
    }
}

