    public async Task<ListResponse<EntityNameSummary>> ListStockTakesForReportParamAsync(int brandId, string storeId, StockTakeLookupTypes stockTakeLookupType)
    {
        await ValidateStoreAndBrandAsync(brandId, storeId);

        var parameters = new List<object>
        {
            new MySqlParameter("@StoreId", storeId)
        };

        if (stockTakeLookupType == StockTakeLookupTypes.Opening)
        {
            return new ListResponse<EntityNameSummary>
            {
                Items = (await readonlyContext
                        .Database
                        .SqlQueryRaw<SpGetStockVarianceOpeningIdResult>("CALL aura_cloud_brand.spGetStockVarianceOpeningId(@StoreId)", parameters.ToArray())
                        .ToListAsync())
                    .Select(x => new EntityNameSummary
                    {
                        Id = x.OpeningStockTakeId,
                        Name = x.OpeningStockTakeList
                    })
            };
        }

        if (stockTakeLookupType == StockTakeLookupTypes.Closing)
        {
            return new ListResponse<EntityNameSummary>
            {
                Items = (await readonlyContext
                        .Database
                        .SqlQueryRaw<SpGetStockVarianceClosingIdResult>("CALL aura_cloud_brand.spGetStockVarianceClosingId(@StoreId)", parameters.ToArray())
                        .ToListAsync())
                    .Select(x => new EntityNameSummary
                    {
                        Id = x.ClosingStockTakeId,
                        Name = x.ClosingStockTakeList
                    })
            };
        }

        return new ListResponse<EntityNameSummary>
        {
            Items = (await readonlyContext
                .Database
                .SqlQueryRaw<SpGetStockTakeIdResult>("CALL aura_cloud_brand.spGetStockTakeId(@StoreId)", parameters.ToArray())
                .ToListAsync())
                .Select(x => new EntityNameSummary
                {
                    Id = x.StockTakeId,
                    Name = x.StockTakeList
                })
        };
    }

    private async Task<int> GetAvailableStockItemCountAsync(int brandId, string storeId, int? stockTakeId = null)
    {
        var availableItemsQuery = readonlyContext.Items
            .Where(x => x.BrandId == brandId && (x.StoreId == null || x.StoreId == storeId) && !x.IsArchived)
            .Select(x => x.Id);

        if (stockTakeId.HasValue)
        {
            var stockTakeItemsQuery = readonlyContext.StockTakeItems
                .Where(x => x.StockTakeId == stockTakeId.Value)
                .Select(x => x.ItemId);

            return await availableItemsQuery.Union(stockTakeItemsQuery).CountAsync();
        }

        return await availableItemsQuery.CountAsync();
    }
}

