private static StockTakeSubCountError? ValidateUpdate(StockTakeSubCountUpdate update, HashSet<int> existingStockItems, HashSet<int> validSubCounts)
{
    if (!existingStockItems.Contains(update.StockItemId))
    {
        return new StockTakeSubCountError
        {
            StockItemId = update.StockItemId,
            Error = "Stock item not found"
        };
    }

    if (!validSubCounts.Contains(update.SubcountId))
    {
        return new StockTakeSubCountError
        {
            StockItemId = update.StockItemId,
            Error = "Subcount not found or archived"
        };
    }

    if (update.Values.Bulk.HasValue && update.Values.Bulk < 0)
    {
        return new StockTakeSubCountError
        {
            StockItemId = update.StockItemId,
            Error = "Invalid bulk value"
        };
    }

    if (update.Values.Prepped.HasValue && update.Values.Prepped < 0)
    {
        return new StockTakeSubCountError
        {
            StockItemId = update.StockItemId,
            Error = "Invalid prepped value"
        };
    }

    return null;
}

private void ProcessSingleUpdate(StockTake stockTake, StockTakeSubCountUpdate update, Dictionary<int, StockTakeItem> existingStockTakeItems)
{
    var stockTakeItem = existingStockTakeItems.GetValueOrDefault(update.StockItemId);
    var stockTakeItemSubCount = stockTakeItem?.StockTakeItemSubCounts.SingleOrDefault(x => x.SubCountId == update.SubcountId);

    if (update.Values.Bulk == null && update.Values.Prepped == null)
    {
        // Remove subcount if both values are null
        if (stockTakeItemSubCount != null)
        {
            dbContext.Remove(stockTakeItemSubCount);

            if (stockTakeItem != null)
            {
                stockTakeItem.StockTakeItemSubCounts.Remove(stockTakeItemSubCount);

                // Remove the stocktake item if no subcounts remain
                if (!stockTakeItem.StockTakeItemSubCounts.Any())
                {
                    dbContext.Remove(stockTakeItem);
                    existingStockTakeItems.Remove(update.StockItemId);
                }
            }
        }
        return;
    }

    // Create or update subcount
    if (stockTakeItem == null)
    {
        stockTakeItem = new StockTakeItem
        {
            StockTakeId = stockTake.Id,
            ItemId = update.StockItemId,
            StockTakeItemSubCounts = new List<StockTakeItemSubCount>()
        };

        dbContext.Add(stockTakeItem);

