using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.Database.Extensions;
using Aura.Common.Extensions;
using Aura.Database.Context.Stock;
using Aura.Manager.Stock.Extensions;
using AuraCloudInterface.Base;
using AuraCloudInterface.Patching;
using AuraCloudInterface.Stock.Grv;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using System.Linq.Expressions;
using System.Net;
using GrvStatus = AuraCloudInterface.Stock.Grv.GrvStatus;

namespace Aura.Manager.Stock;

public class GrvManager(ILogger<GrvManager> logger, StockContext dbContext, ReadOnlyStockContext readonlyContext, BrandManager brandManager, StoreManager storeManager, UserManager userManager, TaxRateManager taxRateManager, SupplierManager supplierManager, StockManager stockManager)
{
    private const decimal DifferenceTolerance = 0.01M;

    public async Task<CreateGrvResponse> CreateGrvAsync(int brandId, string storeId, int userId, CreateGrvRequest request)
    {
        ApiException.ThrowIfTrue(request.TransactionType == AuraCloudInterface.Stock.Grv.GrvTransactionType.ShortDelivered, "GRV transaction type 'ShortDelivered' cannot be created directly.");

        const int maxRetries = 3;
        var retryCount = 0;

        while (retryCount < maxRetries)
        {
            try
            {
                var strategy = dbContext.Database.CreateExecutionStrategy();
                return await strategy.ExecuteAsync(async () =>
                {
                    await using var transaction = await dbContext.Database.BeginTransactionAsync();

                    var grvNumber = (await dbContext.Grvs
                        .Where(x => x.StoreId == storeId && x.GrvTransactionTypeId == (int)request.TransactionType)
                        .MaxAsync(x => (int?)x.GrvNumber) ?? 0) + 1;

                    int? supplierId = null;
                    if (request.ParentGrvId is > 0)
                    {
                        var parentGrv = await ViewGrvAsync(brandId, storeId, request.ParentGrvId.Value);

                        ApiException.ThrowIfTrue(parentGrv.StoreId != storeId, $"Parent GRV with id {request.ParentGrvId} does not belong to store id {storeId}");
                        ApiException.ThrowIfTrue(parentGrv.Status != GrvStatus.Finalized, $"Cannot create a return for a GRV with a status of {parentGrv.Status}");

                        supplierId = parentGrv.Supplier?.Id;
                    }

                    var grv = request.CreateGrv(storeId, grvNumber, supplierId, userId);

                    await dbContext.Grvs.AddAsync(grv);

                    await dbContext.SaveChangesAsync();

                    await transaction.CommitAsync();

                    return new CreateGrvResponse
                    {
                        Id = grv.Id,
                        GrvNumber = grvNumber,
                        GrvDateTime = grv.GrvDate,
                        StockMovementAt = grv.StockMovementAt,
                    };
                });
            }
            catch (DbUpdateException ex) // Catches unique constraint violations
            {
                retryCount++;
                if (retryCount == maxRetries)
                {
                    throw new ApiException(HttpStatusCode.BadRequest, "Unable to generate unique GRV number after multiple attempts");
                }
            }
        }

        throw new ApiException(HttpStatusCode.InternalServerError, "Unexpected error creating GRV"); // Should never reach here
    }


    public async Task<GrvSummary> ViewGrvAsync(int brandId, string storeId, int grvId)
    {
        var grv = await readonlyContext.GetOrExceptGrvAsync(storeId, grvId);

        var userIds = new[] { grv.StockMovementByUserId, grv.LockedByUserId }
            .OfType<int>()
            .ToList();

        var userLookup = await userManager.GetUserNamesForUserIds(userIds);

        return grv.ToGrvSummary(userLookup);
    }

    public async Task<ListResponse<GrvSummary>> ListGrvsAsync(int brandId, string storeId, ListGrvArgs args)
    {
        var store = await storeManager.ViewStoreSummaryAsync(storeId);
        ApiException.ThrowIfTrue(store == null, $"Store with id {storeId} not found");
        ApiException.ThrowIfTrue(store.BrandId != brandId, $"Store id {storeId} does not belong to brand id {brandId}");

        var fromDateTime = args.FromDateTime ?? DateTime.MinValue;
        var toDateTime = args.ToDateTime ?? DateTime.UtcNow;        

        IQueryable<Grv> query = readonlyContext
            .Grvs
            .Include(x => x.Supplier)
            .Include(x => x.GrvItems);

        if (args.IncludeRelatedSummaries)
        {
            if (args.AreRelatedSummariesSortedDesc)
            {
                query = query.Include(x => x.InverseParentGrv
                    .Where(inv => inv.GrvTransactionTypeId == 2 || inv.GrvTransactionTypeId == 3)
                    .OrderByDescending(inv => inv.GrvDate));
            }
            else
            {
                query = query.Include(x => x.InverseParentGrv
                    .Where(inv => inv.GrvTransactionTypeId == 2 || inv.GrvTransactionTypeId == 3)
                    .OrderBy(inv => inv.GrvDate));
            }
            query = query.Include(x => x.ParentGrv);
        }

        query = query.Where(x => x.StoreId == storeId && x.GrvTransactionTypeId == (int)args.TransactionType)
            .Where(x => (x.GrvDate >= fromDateTime && x.GrvDate <= toDateTime) || (x.StockMovementAt >= fromDateTime && x.StockMovementAt <= toDateTime) || (x.LockedAt.HasValue && x.LockedAt >= fromDateTime && x.LockedAt <= toDateTime))            
            .WhereIf(args.StatusIds?.Any() ?? false, x => args.StatusIds!.Contains(x.GrvStatusId))
            .WhereIf(args.SupplierId.HasValue, x => x.SupplierId == args.SupplierId)
            .WhereIf(args.IsArchived.HasValue, x => x.IsArchived == args.IsArchived)
            .WhereIf(args.GrvNumber.HasValue, x => 
                EF.Functions.Like(x.GrvNumber.ToString(), $"{args.GrvNumber.Value}%"))
            .WhereIf(!string.IsNullOrWhiteSpace(args.PurchaseOrderNumber), x => 
                x.PurchaseOrderNumber != null && 
                EF.Functions.Like(x.PurchaseOrderNumber.ToLower(), $"%{args.PurchaseOrderNumber.ToLower()}%"))
            .WhereIf(!string.IsNullOrWhiteSpace(args.Search), x => 
                (x.SupplierReference != null && EF.Functions.Like(x.SupplierReference, $"%{args.Search}%")) ||
                EF.Functions.Like(x.GrvNumber.ToString(), $"%{args.Search}%"))
            .WhereIf(args.ItemIds?.Any() ?? false, x => x.GrvItems.Any(i => args.ItemIds.Contains(i.ItemId)));

        var orderFunctions = new Dictionary<string, Expression<Func<Grv, object>>>
        {
            { nameof(GrvSummary.GrvNumber), x => x.GrvNumber },
            { nameof(GrvSummary.Status), x => x.GrvStatus.Name },
            { nameof(GrvSummary.GrvDate), x => x.GrvDate },
            { nameof(GrvSummary.StockMovementAt), x => x.StockMovementAt },
            { nameof(GrvSummary.LockedAt), x => x.LockedAt },
            { nameof(GrvSummary.TotalCostIncl), x => x.TotalCostIncl },
            { nameof(GrvSummary.Notes), x => x.Notes },
            { $"{nameof(GrvSummary.Supplier)}.{nameof(GrvSummary.Supplier.Name)}", x => x.Supplier.Name },
            { $"{nameof(GrvSummary.Supplier)}.{nameof(GrvSummary.Supplier.Phone)}", x => x.Supplier.Phone },
        };
        query = query.AddOrderBy(args.Sort, orderFunctions, defaultOrder: x => x.GrvDate, isDefaultOrderDescending: true);

        var currentPage = await query
            .PaginateAsync(args.Page ?? -1, args.PageSize ?? 15);

        var userIds = currentPage.Page
            .SelectMany(x => new[] { x.StockMovementByUserId, x.LockedByUserId })
            .OfType<int>()
            .Distinct()
            .ToList();

        var userLookup = await userManager.GetUserNamesForUserIds(userIds);

        return new ListResponse<GrvSummary>
        {
            Items = currentPage.Page.Select(x => x.ToGrvSummary(userLookup)),
            Pagination = currentPage.Pagination,
        };
    }

    public async Task<UpdateGrvResponse> UpdateGrvAsync(int brandId, string storeId, int grvId, int userId, UpdateGrvRequest request)
    {
        var grv = await dbContext.GetOrExceptGrvAsync(storeId, grvId);

        ApiException.ThrowIfTrue(grv.IsArchived, $"Cannot modify a GRV that is archived (id: {grvId})");
        ApiException.ThrowIfTrue((GrvStatus)grv.GrvStatusId == GrvStatus.Locked, $"Cannot modify a GRV with status Locked");

        if (request.SupplierId != grv.SupplierId)
        {
            ApiException.ThrowIfTrue(grv.ParentGrvId is > 0, "Cannot change the supplier if linked to a Grv");

            if (request.SupplierId is > 0)
            {
                var supplier = await supplierManager.ViewSupplierAsync(storeId, request.SupplierId.Value);
                ApiException.ThrowIfTrue(supplier == null, $"Supplier with id {request.SupplierId} not found");
                ApiException.ThrowIfTrue(supplier.StoreId != storeId, $"Supplier id {request.SupplierId} does not belong to store id {storeId}");
            }
        }

        request.MapTo(grv, userId);        

        await dbContext.SaveChangesAsync();

        return new UpdateGrvResponse((GrvStatus)grv.GrvStatusId);
    }

    private void EnsureGrvBalances(Grv grv)
    {
        if (Math.Abs(grv.TotalCostIncl - grv.GrvItems.Sum(x => x.TotalCostIncl ?? 0)) <= DifferenceTolerance)
        {
            return;
        }

        throw new ApiException(HttpStatusCode.BadRequest, $"Total cost {grv.TotalCostIncl} does not match sum of item costs {grv.GrvItems.Sum(x => x.TotalCostIncl ?? 0)} for GRV with id {grv.Id}");
    }

    public async Task<UpdateResponse> ArchiveGrvsAsync(int brandId, string storeId, ICollection<int> grvIds, int archivedByUserId)
    {
        var strategy = dbContext.Database.CreateExecutionStrategy();
        return await strategy.ExecuteAsync(async () =>
        {
            await using var transaction = await dbContext.Database.BeginTransactionAsync();

            try
            {
                var grvs = await dbContext.Grvs
                    .Include(x => x.InverseParentGrv.Where(y => !y.IsArchived))
                    .Where(x => x.StoreId == storeId && grvIds.Contains(x.Id) && !x.IsArchived)
                    .ToListAsync();

                var shortDeliveredGrvs = grvs.Where(x => x.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.ShortDelivered).ToList();
                ApiException.ThrowIfTrue(shortDeliveredGrvs.Any(), $"Cannot archive short delivered credit notes directly. Short delivered credit notes can only be archived when their parent GRV is archived. GRV IDs: {string.Join(", ", shortDeliveredGrvs.Select(x => x.Id))}");

                foreach (var grv in grvs)
                {
                    grv.IsArchived = true;

                    foreach (var returnGrv in grv.InverseParentGrv)
                    {
                        returnGrv.IsArchived = true;
                    }
                }

                await dbContext.SaveChangesAsync();
                await transaction.CommitAsync();

                await Task.Delay(150);
                await stockManager.UpdateGrvItemPricesAsync(brandId, storeId, archivedByUserId, null);

                return new UpdateResponse();
            }
            catch
            {
                await transaction.RollbackAsync();
                throw;
            }
        });
    }

    public async Task<ReceiveGrvResponse> ReceiveGrvAsync(int brandId, string storeId, DateTime receivedDateTime, int grvId, int receivedByUserId)
    {        
        var grv = await dbContext.Grvs
            .Include(g => g.Supplier)
            .Include(g => g.GrvItems)
            .SingleOrDefaultAsync(g => g.Id == grvId && g.StoreId == storeId);

        ApiException.ThrowIfTrue(grv == null, $"GRV with id {grvId} not found for store {storeId}");

        ValidateGrvItemsAreFullyCosted(grv);

        if (grv is { GrvStatusId: (int)GrvStatus.Finalized })
        {            
            ApiException.Throw($"Grv with id {grvId} has already been received on {grv.StockMovementAt:yyyy-MM-dd HH:mm:ss}");
        }

        EnsureGrvBalances(grv);

        var invalidItems = grv.GrvItems.Where(i => i.StockMovements.Sum(m => m.Quantity) > i.QuantityGrv).ToList();
        if (invalidItems.Any())
        {
            var errorDetails = string.Join(", ", invalidItems.Select(i => $"Item ID {i.ItemId} (Received: {i.StockMovements.Sum(m => m.Quantity)} > GRV: {i.QuantityGrv})"));
            throw new ApiException(HttpStatusCode.BadRequest, $"Cannot receive GRV. Received quantity cannot be greater than GRV quantity for the following items: {errorDetails}");
        }

        ApiException.ThrowIfTrue((GrvStatus)grv.GrvStatusId is not (GrvStatus.Draft or GrvStatus.Locked), $"GRV id {grv.Id} must be in a draft or locked state to receive");
        ApiException.ThrowIfTrue(grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv, $"Grv id {grv.Id} is not a GRV");
        ApiException.ThrowIfTrue(grv.SupplierId == null, $"Supplier is required for GRV with id {grv.Id}");
        ApiException.ThrowIfTrue(grv.ParentGrv != null, "Parent grv should only be configured for Returns");

        // Validate that all GRV items have assigned stock item IDs
        var unmappedItems = grv.GrvItems.Where(i => i.ItemId == null).ToList();
        if (unmappedItems.Any())
        {
            var errorDetails = string.Join(", ", unmappedItems.Select(i => $"GRV Item ID {i.Id}"));
            throw new ApiException(HttpStatusCode.BadRequest, $"Cannot finalize GRV. All GRV items must have assigned stock item IDs. Unmapped items: {errorDetails}");
        }

        var totalGrvQuantity = grv.GrvItems.Sum(item => item.QuantityGrv.Value);
        var totalFulfilledQuantity = grv.GrvItems.Sum(item => item.QuantityFulfilled.Value);
        var needsCreditNote = totalGrvQuantity > totalFulfilledQuantity;        

        const int maxRetries = 3;
        var retryCount = 0;

        while (retryCount < maxRetries)
        {
            try
            {
                var strategy = dbContext.Database.CreateExecutionStrategy();
                return await strategy.ExecuteAsync(async () =>
                {
                    await using var transaction = await dbContext.Database.BeginTransactionAsync();

                    grv.GrvStatusId = (int)GrvStatus.Finalized;                    
                    grv.ValuesUpdatedAt = DateTime.UtcNow;

                    // Create stock movement records for each GRV item with quantity fulfilled
                    foreach (var grvItem in grv.GrvItems.Where(x => x.QuantityFulfilled.GetValueOrDefault() > 0))
                    {
                        var stockMovement = new Aura.Database.Context.Stock.StockMovement
                        {
                            GrvItemId = grvItem.Id,
                            Quantity = grvItem.QuantityFulfilled ?? 0,
                            TotalCostExcl = grvItem.TotalCostExcl.ProRata(grvItem.QuantityGrv, grvItem.QuantityFulfilled ?? 0),
                            Timestamp = grv.StockMovementAt
                        };

                        await dbContext.StockMovements.AddAsync(stockMovement);
                    }

                    Grv? returnGrv = null;
                    if (needsCreditNote)
                    {
                        returnGrv = await BuildAutoReturnGrvAsync(grv, receivedByUserId);
                        if (returnGrv != null)
                        {
                            await dbContext.Grvs.AddAsync(returnGrv);
                        }
                    }

                    await dbContext.SaveChangesAsync();

                    await transaction.CommitAsync();

                    await Task.Delay(150);
                    await stockManager.UpdateGrvItemPricesAsync(brandId, storeId, receivedByUserId, null);

                    return new ReceiveGrvResponse((GrvStatus)grv.GrvStatusId, grv.StockMovementAt, returnGrv?.Id);
                });
            }
            catch (DbUpdateException ex) // Catches unique constraint violations
            {
                retryCount++;
                if (retryCount == maxRetries)
                {
                    throw new ApiException(HttpStatusCode.BadRequest, "Unable to generate unique return GRV number after multiple attempts");
                }
            }
        }

        throw new ApiException(HttpStatusCode.InternalServerError, "Unexpected error receiving GRV"); // Should never reach here
    }

    private async Task<Grv?> BuildAutoReturnGrvAsync(Grv originalGrv, int createdByUserId)
    {
        var timestamp = originalGrv.StockMovementAt;        

        var returnGrvItems = originalGrv
            .GrvItems
            .Where(grvItem => grvItem.ItemId != null && grvItem.QuantityGrv.GetValueOrDefault() > grvItem.QuantityFulfilled.GetValueOrDefault())
            .Select(grvItem =>
            {
                var shortDeliveredQuantity = grvItem.QuantityGrv.GetValueOrDefault() - grvItem.QuantityFulfilled.GetValueOrDefault();

                var returnGrvItem = new Aura.Database.Context.Stock.GrvItem
                {
                    ItemId = grvItem.ItemId,
                    QuantityGrv = shortDeliveredQuantity,                    
                    TotalCostExcl = grvItem.TotalCostExcl.ProRata(grvItem.QuantityGrv, shortDeliveredQuantity),
                    TotalCostIncl = grvItem.TotalCostIncl.ProRata(grvItem.QuantityGrv, shortDeliveredQuantity),
                    TotalTaxes = grvItem.TotalTaxes.ProRata(grvItem.QuantityGrv, shortDeliveredQuantity),
                    Notes = grvItem.Notes                    
                };

                return returnGrvItem;
            })
            .ToList();

        if (!returnGrvItems.Any())
        {
            return null;
        }

        return new Grv
        {
            StoreId = originalGrv.StoreId,
            SupplierId = originalGrv.SupplierId,
            GrvDate = originalGrv.GrvDate,
            SupplierReference = originalGrv.SupplierReference,
            GrvNumber = (await dbContext.Grvs
                .Where(x => x.StoreId == originalGrv.StoreId && x.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.ShortDelivered)
                .MaxAsync(x => (int?)x.GrvNumber) ?? 0) + 1,
            GrvStatusId = (int)GrvStatus.Finalized,
            GrvTransactionTypeId = (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.ShortDelivered,
            ParentGrvId = originalGrv.Id,
            ValuesUpdatedAt = originalGrv.ValuesUpdatedAt,
            GrvItems = returnGrvItems,
            TotalCostIncl = returnGrvItems.Sum(x => x.TotalCostIncl.GetValueOrDefault()),
            StockMovementAt = timestamp,
            StockMovementByUserId = createdByUserId
        };
    }

    public async Task<ReturnGrvResponse> ReturnGrvAsync(int brandId, string storeId, DateTime returnedDateTime, int grvId, int returnedByUserId)
    {
        var grv = await dbContext.Grvs
            .Include(g => g.Supplier)
            .Include(g => g.GrvItems)
            .SingleOrDefaultAsync(g => g.Id == grvId && g.StoreId == storeId);

        ApiException.ThrowIfTrue(grv == null, $"GRV with id {grvId} not found for store {storeId}");

        ValidateGrvItemsAreFullyCosted(grv);

        if (grv is { GrvStatusId: (int)GrvStatus.Finalized })
        {
            return new ReturnGrvResponse((GrvStatus)grv.GrvStatusId, grv.StockMovementAt);
        }

        ApiException.ThrowIfTrue((GrvStatus)grv.GrvStatusId is not (GrvStatus.Draft or GrvStatus.Locked), $"GRV id {grv.Id} must be in a draft or locked state to return");
        ApiException.ThrowIfTrue(grv.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Return, $"Grv id {grv.Id} is not a Return");        
        ApiException.ThrowIfTrue(grv.SupplierId == null, $"Supplier is required for GRV with id {grv.Id}");

        // Validate that all GRV items have assigned stock item IDs
        var unmappedItems = grv.GrvItems.Where(i => i.ItemId == null).ToList();
        if (unmappedItems.Any())
        {
            var errorDetails = string.Join(", ", unmappedItems.Select(i => $"GRV Item ID {i.Id}"));
            throw new ApiException(HttpStatusCode.BadRequest, $"Cannot finalize return GRV. All GRV items must have assigned stock item IDs. Unmapped items: {errorDetails}");
        }

        if (grv.ParentGrv != null)
        {
            var parentGrvItems = await readonlyContext.GrvItems
                .Where(x => x.GrvId == grv.ParentGrvId)
                .GroupBy(x => x.ItemId)
                .Select(x => new { ItemId = x.Key, Quantity = x.Sum(i => i.QuantityGrv) })
                .ToListAsync();

            var returnGrvItems = await readonlyContext.GrvItems
                .Where(x => x.Grv.ParentGrvId == grv.ParentGrvId && x.GrvId != grv.Id)
                .GroupBy(x => x.ItemId)
                .Select(x => new { ItemId = x.Key, Quantity = x.Sum(i => i.QuantityGrv) })
                .ToListAsync();

            var currentReturnItems = grv.GrvItems
                .GroupBy(x => x.ItemId)
                .Select(x => new { ItemId = x.Key, Quantity = x.Sum(i => i.QuantityGrv) });

            foreach (var currentItem in currentReturnItems)
            {
                var parentTotal = parentGrvItems.FirstOrDefault(x => x.ItemId == currentItem.ItemId)?.Quantity ?? 0;
                var returnedTotal = returnGrvItems.FirstOrDefault(x => x.ItemId == currentItem.ItemId)?.Quantity ?? 0;

                ApiException.ThrowIfTrue(currentItem.Quantity > parentTotal - returnedTotal, $"Return quantity for item id {currentItem.ItemId} is greater than the quantity in the parent GRV.");
            }
        }

        grv.GrvStatusId = (int)GrvStatus.Finalized;

        // Create stock movement records for each GRV item with quantity fulfilled
        foreach (var grvItem in grv.GrvItems.Where(x => x.QuantityFulfilled.GetValueOrDefault() > 0))
        {
            var stockMovement = new Aura.Database.Context.Stock.StockMovement
            {
                GrvItemId = grvItem.Id,
                Quantity = grvItem.QuantityFulfilled ?? 0,
                TotalCostExcl = grvItem.TotalCostExcl.ProRata(grvItem.QuantityGrv, grvItem.QuantityFulfilled ?? 0),
                Timestamp = grv.StockMovementAt
            };

            await dbContext.StockMovements.AddAsync(stockMovement);
        }

        await dbContext.SaveChangesAsync();

        await Task.Delay(150);
        await stockManager.UpdateGrvItemPricesAsync(brandId, storeId, returnedByUserId, null);

        return new ReturnGrvResponse((GrvStatus)grv.GrvStatusId, grv.StockMovementAt);
    }

    private void ValidateGrvItemsAreFullyCosted(Aura.Database.Context.Stock.Grv grv)
    {
        var isTaxable = grv.Supplier?.IsRegisteredForTax ?? true;

        foreach (var item in grv.GrvItems)
        {
            var itemIdentifier = $"Stock Item ID {item.ItemId?.ToString() ?? "N/A"} (GRV Item ID: {item.Id})";

            if (!item.QuantityGrv.HasValue ||
                !item.TotalCostExcl.HasValue ||
                !item.TotalCostIncl.HasValue ||
                !item.TotalTaxes.HasValue ||
                !item.QuantityFulfilled.HasValue)
            {
                ApiException.Throw($"Cannot process GRV with id {grv.Id}. Item {itemIdentifier} has incomplete quantity or cost information.");
            }

            var totalCostIncl = item.TotalCostIncl!.Value;
            var totalCostExcl = item.TotalCostExcl!.Value;
            var totalTaxes = item.TotalTaxes!.Value;

            if (!isTaxable)
            {
                if (totalTaxes != 0)
                {
                    ApiException.Throw($"Cannot process GRV with id {grv.Id}. Supplier is not registered for tax, but item {itemIdentifier} has a tax value.");
                }

                if (totalCostIncl != totalCostExcl)
                {
                    ApiException.Throw($"Cannot process GRV with id {grv.Id}. Supplier is not registered for tax, but item {itemIdentifier} has different inclusive and exclusive costs.");
                }
            }
            else if (totalCostIncl == totalCostExcl)
            {
                if (totalTaxes != 0)
                {
                    ApiException.Throw($"Cannot process GRV with id {grv.Id}. Item {itemIdentifier} has identical inclusive and exclusive costs, but taxes are not zero.");
                }
            }
            else
            {
                var expectedTaxes = totalCostIncl - totalCostExcl;
                if (expectedTaxes != totalTaxes)
                {
                    ApiException.Throw($"Cannot process GRV with id {grv.Id}. Item {itemIdentifier} has incorrect tax calculation. The tax value ({totalTaxes}) does not match the difference between inclusive ({totalCostIncl}) and exclusive ({totalCostExcl}) costs.");
                }
            }
        }
    }

    public async Task<LockGrvResponse> LockGrvAsync(int brandId, string storeId, DateTime lockedDateTime, int grvId, int lockedByUserId)
    {
        var grv = await dbContext.Grvs
            .Include(g => g.Supplier)
            .Include(g => g.GrvItems)
            .SingleOrDefaultAsync(g => g.Id == grvId && g.StoreId == storeId);

        ApiException.ThrowIfTrue(grv == null, $"GRV with id {grvId} not found for store {storeId}");

        ValidateGrvItemsAreFullyCosted(grv);

        if (grv is { GrvStatusId: (int)GrvStatus.Locked, LockedByUserId: not null, LockedAt: not null })
        {
            return new LockGrvResponse((GrvStatus)grv.GrvStatusId, grv.LockedAt.Value);
        }

        EnsureGrvBalances(grv);

        ApiException.ThrowIfTrue((GrvStatus)grv.GrvStatusId is not GrvStatus.Draft, $"GRV id {grv.Id} must be in a draft state to lock");
        ApiException.ThrowIfTrue(grv.SupplierId == null, $"Supplier is required for GRV with id {grv.Id}");

        // Validate that all GRV items have assigned stock item IDs
        var unmappedItems = grv.GrvItems.Where(i => i.ItemId == null).ToList();
        if (unmappedItems.Any())
        {
            var errorDetails = string.Join(", ", unmappedItems.Select(i => $"GRV Item ID {i.Id}"));
            throw new ApiException(HttpStatusCode.BadRequest, $"Cannot lock GRV. All GRV items must have assigned stock item IDs. Unmapped items: {errorDetails}");
        }

        grv.GrvStatusId = (int)GrvStatus.Locked;
        grv.LockedByUserId = lockedByUserId;
        grv.LockedAt = lockedDateTime;

        await dbContext.SaveChangesAsync();

        await Task.Delay(150);
        await stockManager.UpdateGrvItemPricesAsync(brandId, storeId, lockedByUserId, null);

        return new LockGrvResponse((GrvStatus)grv.GrvStatusId, grv.LockedAt.Value);
    }

    private void ValidateGrvItemRequest(BaseGrvItemRequest request)
    {
        ApiException.ThrowIfTrue(request.QuantityGRV < 0, "GRV quantity cannot be negative.");
        ApiException.ThrowIfTrue(request.TotalCostExcl < 0, "Total cost (exclusive) cannot be negative.");
        ApiException.ThrowIfTrue(request.TotalCostIncl < 0, "Total cost (inclusive) cannot be negative.");
        ApiException.ThrowIfTrue(request.TotalTaxes < 0, "Total taxes cannot be negative.");
        ApiException.ThrowIfTrue(request.QuantityFulfilled < 0, "Quantity fulfilled cannot be negative.");

        ApiException.ThrowIfTrue(request.QuantityFulfilled > request.QuantityGRV, $"Quantity fulfilled ({request.QuantityFulfilled}) cannot be greater than the GRV quantity ({request.QuantityGRV}).");
    }

    private async Task ValidateReturnGrvQuantityLimitsAsync(int brandId, string storeId, int grvId, int? itemId, decimal quantityGRV)
    {
        if (!itemId.HasValue) return;

        var grv = await readonlyContext.Grvs
            .Where(x => x.Id == grvId)
            .Select(x => new { x.GrvTransactionTypeId, x.ParentGrvId })
            .SingleOrDefaultAsync();

        if (grv?.GrvTransactionTypeId != (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Return || grv.ParentGrvId is not > 0)
        {
            return; // Not a return GRV or no parent GRV
        }

        var parentGrvItems = await readonlyContext.GrvItems
            .Where(x => x.GrvId == grv.ParentGrvId && x.ItemId == itemId)
            .ToListAsync();

        var returnedGrvItems = await readonlyContext.GrvItems
            .Where(x => x.Grv.ParentGrvId == grv.ParentGrvId && x.ItemId == itemId && x.GrvId != grvId)
            .ToListAsync();

        var parentTotal = parentGrvItems.Sum(x => x.QuantityGrv) ?? 0;
        var returnedTotal = returnedGrvItems.Sum(x => x.QuantityGrv) ?? 0;
        var maxQuantity = parentTotal - returnedTotal;

        ApiException.ThrowIfTrue(quantityGRV > maxQuantity, $"Return quantity ({quantityGRV}) cannot exceed available quantity ({maxQuantity}) in parent GRV.");
    }

    public async Task<CreateResponse> CreateGrvItemAsync(int brandId, string storeId, int grvId, CreateGrvItemRequest request)
    {
        ValidateGrvItemRequest(request);

        var grv = await dbContext.GetOrExceptGrvAsync(storeId, grvId);
        ApiException.ThrowIfTrue(grv.IsArchived, $"Cannot add items to a GRV that is archived (id: {grvId})");

        var grvStatus = (GrvStatus)grv.GrvStatusId;
        ApiException.ThrowIfTrue(grvStatus is not GrvStatus.Draft, $"New items can only be added to a GRV in a draft state. Current status is {grvStatus}.");

        Aura.Database.Context.Stock.Item? stockItem = null;
        if (request.ItemId.HasValue)
        {
            stockItem = grv.ParentGrvId is > 0 
                ? await readonlyContext
                    .GrvItems
                    .Include(x => x.Item).ThenInclude(x => x.UnitOfMeasure)
                    .Where(x => x.GrvId == grv.ParentGrvId.Value && x.ItemId == request.ItemId.Value)
                    .Select(x => x.Item)
                    .SingleOrDefaultAsync()
                : await readonlyContext
                    .Items
                    .Include(x => x.UnitOfMeasure)
                    .SingleOrDefaultAsync(x => x.Id == request.ItemId.Value);

            if (stockItem == null)
            {
                if (grv.ParentGrvId.HasValue)
                {
                    ApiException.Throw($"Stock item with id {request.ItemId} not found under parent grv id {grv.ParentGrvId.Value}");
                }

                ApiException.Throw($"Stock item with id {request.ItemId} not found");
            }

            ApiException.ThrowIfTrue(stockItem.BrandId != brandId, $"Stock item with id {request.ItemId} does not belong to brand id {brandId}");
        }

        // Validate return GRV quantity limits only if this is a return GRV
        if (grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Return && grv.ParentGrvId is > 0)
        {
            await ValidateReturnGrvQuantityLimitsAsync(brandId, storeId, grvId, request.ItemId, request.QuantityGRV);
        }

        var grvItem = new Aura.Database.Context.Stock.GrvItem
        {
            GrvId = grvId,
            ItemId = request.ItemId,
            QuantityGrv = request.QuantityGRV,
            TotalCostExcl = request.TotalCostExcl,
            TotalCostIncl = request.TotalCostIncl,
            TotalTaxes = request.TotalTaxes,
            Notes = request.Notes,
            Order = request.Order,
            QuantityFulfilled = request.QuantityFulfilled,
            AiReceiptLine = request.AiReceiptLine,
        };

        await dbContext.GrvItems.AddAsync(grvItem);

        await dbContext.SaveChangesAsync();

        return new CreateResponse
        {
            Id = grvItem.Id
        };
    }

    public async Task<UpdateResponse> UpdateGrvItemAsync(int brandId, string storeId, int grvId, int grvItemId, UpdateGrvItemRequest request, int userId)
    {
        ValidateGrvItemRequest(request);

        var grvItem = await dbContext.GrvItems
            .Include(x => x.Grv)
            .Include(x => x.Item).ThenInclude(x => x.UnitOfMeasure)            
            .SingleOrDefaultAsync(x => x.Id == grvItemId && x.GrvId == grvId);

        ApiException.ThrowIfTrue(grvItem == null, $"GRV Item with id {grvItemId} not found");
        ApiException.ThrowIfTrue(grvItem.Grv.StoreId != storeId, $"GRV with id {grvId} does not belong to store {storeId}");

        var grvStatus = (GrvStatus)grvItem.Grv.GrvStatusId;

        if (grvStatus == GrvStatus.Finalized)
        {
            ApiException.Throw($"GRV items cannot be updated when the GRV is finalized.");
        }

        if (request.ItemId.HasValue)
        {
            var stockItem = await readonlyContext.Items
                .Include(x => x.UnitOfMeasure)
                .SingleOrDefaultAsync(x => x.Id == request.ItemId.Value);

            ApiException.ThrowIfTrue(stockItem == null, $"Stock item with id {request.ItemId.Value} not found");
            ApiException.ThrowIfTrue(stockItem.BrandId != brandId, $"Stock item with id {request.ItemId.Value} does not belong to brand id {brandId}");

            grvItem.ItemId = stockItem.Id;            
        }

        // Validate return GRV quantity limits only if this is a return GRV
        if (grvItem.Grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Return && grvItem.Grv.ParentGrvId is > 0)
        {
            await ValidateReturnGrvQuantityLimitsAsync(brandId, storeId, grvId, request.ItemId, request.QuantityGRV);
        }

        if (grvStatus == GrvStatus.Draft)
        {
            // Note: ItemId and UnitOfMeasure are intentionally not set here.
            // They are assigned earlier in this method if a valid ItemId is provided in the request.
            grvItem.QuantityGrv = request.QuantityGRV;
            grvItem.TotalCostExcl = request.TotalCostExcl;
            grvItem.TotalCostIncl = request.TotalCostIncl;
            grvItem.TotalTaxes = request.TotalTaxes;
            grvItem.Notes = request.Notes;
            grvItem.Order = request.Order;
            grvItem.QuantityFulfilled = request.QuantityFulfilled;
            grvItem.AiReceiptLine = request.AiReceiptLine;
        }
        else if (grvStatus == GrvStatus.Locked)
        {
            // For Locked status, only quantityFulfilled can be updated
            grvItem.QuantityFulfilled = request.QuantityFulfilled;
        }

        grvItem.Grv.ValuesUpdatedAt = DateTime.UtcNow;

        await dbContext.SaveChangesAsync();

        return new UpdateResponse();
    }

    public async Task DeleteGrvItemAsync(int brandId, string storeId, int grvId, int grvItemId)
    {
        var grvItem = await dbContext.GrvItems
            .Include(x => x.Grv)            
            .FirstOrDefaultAsync(x => x.Id == grvItemId && x.GrvId == grvId);

        if (grvItem == null)
        {
            return;
        }

        ApiException.ThrowIfTrue(grvItem.Grv.StoreId != storeId, $"GRV with id {grvId} does not belong to store {storeId}");

        grvItem.Grv.ValuesUpdatedAt = DateTime.UtcNow;

        dbContext.GrvItems.Remove(grvItem);
        await dbContext.SaveChangesAsync();
    }

    public async Task<ListResponse<GrvItemSummary>> ListGrvItemsAsync(int brandId, string storeId, int grvId, ListGrvItemArgs? args = null)
    {
        var store = await storeManager.ViewStoreSummaryAsync(storeId);
        ApiException.ThrowIfTrue(store == null, $"Store with id {storeId} not found");
        ApiException.ThrowIfTrue(store.BrandId != brandId, $"Store id {storeId} does not belong to brand id {brandId}");

        var grvSummary = await ViewGrvAsync(brandId, storeId, grvId);
        ApiException.ThrowIfTrue(grvSummary == null, $"A GRV with the id {grvId} could not be found");

        var grvItems = await readonlyContext
            .GrvItems
            .Include(x => x.Item).ThenInclude(x => x.ItemTaxes)
            .Where(x => x.GrvId == grvId)
            .WhereIf(!string.IsNullOrEmpty(args?.Search), x => 
                EF.Functions.Like(x.Item.Code, $"%{args.Search}%")
                || EF.Functions.Like(x.Item.Name, $"%{args.Search}%")
                || EF.Functions.Like(x.Item.Description, $"%{args.Search}%")
            )
            .AsNoTracking()
            .ToListAsync();

        var parentGrvItems = grvSummary.ParentGrvId.HasValue
            ? await readonlyContext.GrvItems
                .Where(x => x.GrvId == grvSummary.ParentGrvId)
                .GroupBy(x => x.ItemId)
                .Select(x => new { ItemId = x.Key, Quantity = x.Sum(y => y.QuantityGrv.Value) })
                .ToDictionaryAsync(x => x.ItemId, x => x.Quantity)
            : new Dictionary<int?, decimal>();

        Dictionary<int, LastPurchaseSummary> lastPurchases = new();

        var totalReturnedQuantities = grvSummary.ParentGrvId.HasValue
            ? await readonlyContext.GrvItems
                .Where(x => x.Grv.ParentGrvId == grvSummary.ParentGrvId.Value && x.Grv.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Return)
                .GroupBy(x => x.ItemId)
                .Select(x => new { ItemId = x.Key, Quantity = x.Sum(y => y.QuantityGrv.Value) })
                .ToDictionaryAsync(x => x.ItemId, x => x.Quantity)
            : new Dictionary<int?, decimal>();

        if (grvSummary.TransactionType == AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv)
        {
            var itemIds = grvItems.Where(x => x.ItemId.HasValue).Select(x => x.ItemId.Value).Distinct().ToHashSet();
            lastPurchases = await readonlyContext.GetLastItemPurchasesForGrvAsync(storeId, grvSummary.Id, grvSummary.GrvDate, itemIds);
        }

        return new ListResponse<GrvItemSummary>
        {
            Items = grvItems.Select(grvItem =>
            {
                decimal? maxUnitQuantity = null;

                if (grvSummary.TransactionType == AuraCloudInterface.Stock.Grv.GrvTransactionType.Return && grvSummary.ParentGrvId is > 0 && grvItem.ItemId.HasValue)
                {
                    if (parentGrvItems.TryGetValue(grvItem.ItemId.Value, out var grvQuantity))
                    {
                        maxUnitQuantity = grvQuantity;

                        if (totalReturnedQuantities.TryGetValue(grvItem.ItemId.Value, out var totalReturnedQuantity))
                        {
                            maxUnitQuantity -= (totalReturnedQuantity - grvItem.QuantityGrv ?? 0);
                        }
                    }
                    else
                    {
                        maxUnitQuantity = 0; // Not allowed to configure the quantity for a stock grvItem in a return that does not exist in the GRV
                    }
                }

                var lastPurchaseSummary = grvSummary.TransactionType == AuraCloudInterface.Stock.Grv.GrvTransactionType.Grv && grvItem.ItemId.HasValue
                    ? (lastPurchases.TryGetValue(grvItem.ItemId.Value, out var summary) ? summary : null)
                    : null;

                return grvItem.ToGrvItemSummary(lastPurchaseSummary, maxUnitQuantity);
            })
            .OrderBy(x => x.Order)
            .ThenBy(x => x.Code)
            .ToList()
        };
    }

    public async Task<SupplierMappingsResponse> GetSupplierMappingsAsync(int brandId, string storeId, int supplierId)
    {
        // Select latest mapping per ItemId using correlated subquery - clean and efficient
        var mappings = await readonlyContext.GrvItems
            .Where(x => x.Grv.StoreId == storeId
                        && x.Grv.SupplierId == supplierId
                        && x.Grv.GrvStatusId == (int)GrvStatus.Finalized
                        && x.ItemId != null
                        && x.AiReceiptLine != null && x.AiReceiptLine != ""
                        && x.Grv.StockMovementAt == readonlyContext.GrvItems
                            .Where(y => y.ItemId == x.ItemId
                                        && y.Grv.StoreId == storeId
                                        && y.Grv.SupplierId == supplierId
                                        && y.Grv.GrvStatusId == (int)GrvStatus.Finalized
                                        && y.AiReceiptLine != null && y.AiReceiptLine != "")
                            .Max(y => y.Grv.StockMovementAt))
            .Select(x => new SupplierMappingItem
            {
                AiReceiptLine = x.AiReceiptLine!,
                ItemId = x.ItemId!.Value.ToString(),
                ItemCode = x.Item.Code,
                ItemName = x.Item.Name,
                GrvId = x.GrvId.ToString()
            })
            .ToListAsync();

        return new SupplierMappingsResponse
        {
            SupplierId = supplierId.ToString(),
            Mappings = mappings
        };
    }

    private async Task ValidateGrvForPatching(int grvId, string storeId, int? currentSupplierId, bool isArchived, int grvStatusId, int? parentGrvId, Dictionary<string, object?> patchRequest)
    {
        ApiException.ThrowIfTrue(isArchived, $"Cannot modify a GRV that is archived (id: {grvId})");
        ApiException.ThrowIfTrue((GrvStatus)grvStatusId == GrvStatus.Locked, $"Cannot modify a GRV with status Locked");

        if (patchRequest.HasProperty<UpdateGrvRequest, int?>(x => x.SupplierId))
        {
            var newSupplierId = patchRequest.GetPatchValue<UpdateGrvRequest, int?>(x => x.SupplierId);
            if (newSupplierId != currentSupplierId)
            {
                ApiException.ThrowIfTrue(parentGrvId is > 0, "Cannot change the supplier if linked to a Grv");

                if (newSupplierId is > 0)
                {
                    var supplier = await supplierManager.ViewSupplierAsync(storeId, newSupplierId.Value);
                    ApiException.ThrowIfTrue(supplier == null, $"Supplier with id {newSupplierId} not found");
                    ApiException.ThrowIfTrue(supplier.StoreId != storeId, $"Supplier id {newSupplierId} does not belong to store id {storeId}");
                }
            }
        }
    }

    /// <summary>
    /// Assumptions...
    /// 1. propertyName is a valid property on the entity, and not any request model.
    /// 2. value will convert to the property type correctly.
    /// To-Do...
    /// 1. Optional validation. EG: Might not be able to update if entity in a certain state.
    /// 2. Dictionary of property names that cannot be updated = like Id.
    /// </summary>        
    public async Task<PatchResponse> PatchGrvAsync(int brandId, string storeId, int grvId, Dictionary<string, object?> patchRequest)
    {
        var grvInfo = await readonlyContext.Grvs
            .Where(x => x.Id == grvId && x.StoreId == storeId)
            .Select(x => new { x.SupplierId, x.IsArchived, x.GrvStatusId, x.ParentGrvId })
            .SingleOrDefaultAsync();

        ApiException.ThrowIfTrue(grvInfo == null, $"GRV with id {grvId} not found for store {storeId}");

        await ValidateGrvForPatching(grvId, storeId, grvInfo.SupplierId, grvInfo.IsArchived, grvInfo.GrvStatusId, grvInfo.ParentGrvId, patchRequest);

        var rowsAffected = await dbContext
            .Grvs
            .Where(x => x.Id == grvId)
            .BeginPatch<Grv, UpdateGrvRequest>(patchRequest)
            .Map(request => request.TotalCost, entity => entity.TotalCostIncl)
            .OnPatched(async (rowsAffected) =>
            {
                if (rowsAffected > 0)
                {
                    await dbContext.Grvs
                        .Where(g => g.Id == grvId)
                        .ExecuteUpdateAsync(s => s.SetProperty(g => g.ValuesUpdatedAt, DateTime.UtcNow));
                }
            })
            .ExecuteAsync();

        return new PatchResponse(rowsAffected);
    }

    public async Task<PatchResponse> PatchGrvItemAsync(int brandId, string storeId, int grvId, int grvItemId, Dictionary<string, object?> patchRequest)
    {
        var grvData = await readonlyContext.Grvs
            .Where(x => x.Id == grvId && x.StoreId == storeId)
            .Select(x => new { 
                x.GrvStatusId, 
                x.GrvTransactionTypeId, 
                x.ParentGrvId,
                CurrentQuantityGRV = x.GrvItems.Where(i => i.Id == grvItemId).Select(i => i.QuantityGrv).FirstOrDefault()
            })
            .SingleOrDefaultAsync();

        ApiException.ThrowIfTrue(grvData == null, $"GRV with id {grvId} not found for store {storeId}");
        ApiException.ThrowIfTrue(grvData.GrvStatusId == (int)GrvStatus.Finalized, $"Cannot patch GRV when it is finalized.");

        // Validate ItemId if present in patch request
        if (patchRequest.TryGetPatchValue<UpdateGrvItemRequest, int?>(x => x.ItemId, out var itemId) && itemId.HasValue)
        {
            var stockItem = await readonlyContext.Items
                .SingleOrDefaultAsync(x => x.Id == itemId.Value);

            ApiException.ThrowIfTrue(stockItem == null, $"Stock item with id {itemId.Value} not found");
            ApiException.ThrowIfTrue(stockItem.BrandId != brandId, $"Stock item with id {itemId.Value} does not belong to brand id {brandId}");
        }

        // Validate return GRV quantity limits if QuantityGRV is being modified
        if (patchRequest.TryGetPatchValue<UpdateGrvItemRequest, decimal>(x => x.QuantityGRV, out var quantityGRV))
        {
            if (grvData.GrvTransactionTypeId == (int)AuraCloudInterface.Stock.Grv.GrvTransactionType.Return && grvData.ParentGrvId is > 0)
            {
                await ValidateReturnGrvQuantityLimitsAsync(brandId, storeId, grvId, itemId, quantityGRV);
            }
        }

        var rowsAffected = await dbContext
            .GrvItems
            .Where(x => x.Id == grvItemId && x.GrvId == grvId && x.Grv.StoreId == storeId)
            .BeginPatch<GrvItem, UpdateGrvItemRequest>(patchRequest)
            .ExcludeAllExceptIf(grvData.GrvStatusId == (int)GrvStatus.Locked, x => x.QuantityFulfilled!)
            .ValidateIfPresent(x => x.QuantityFulfilled, fulfilled => {
                if (patchRequest.TryGetPatchValue<UpdateGrvItemRequest, decimal>(req => req.QuantityGRV, out var grvQuantity))
                {
                    return fulfilled <= grvQuantity;
                }

                // Check against stored GRV quantity when QuantityGRV is not in patch request
                return fulfilled <= grvData.CurrentQuantityGRV;
            }, "Quantity fulfilled cannot be greater than GRV quantity")
            .OnPatched(async (rowsAffected) =>
            {
                if (rowsAffected > 0)
                {
                    await dbContext.Grvs
                        .Where(g => g.Id == grvId)
                        .ExecuteUpdateAsync(s => s.SetProperty(g => g.ValuesUpdatedAt, DateTime.UtcNow));
                }
            })
            .ExecuteAsync();

        return new PatchResponse(rowsAffected);
    }
}

