    28‚Üí        if (grvItemId > 0) configuredSources.Add("GrvItemId");
    29‚Üí        if (processedStockId > 0) configuredSources.Add("ProcessedStockId");
    30‚Üí        if (itemDiscardId > 0) configuredSources.Add("ItemDiscardId");
    31‚Üí
    32‚Üí        ApiException.ThrowIfTrue(configuredSources.Count == 0, "None of GrvItemId, ProcessedStockId, or ItemDiscardId have been configured. Exactly one must be provided.");
    33‚Üí        ApiException.ThrowIfTrue(configuredSources.Count > 1, $"Multiple source IDs have been configured: {string.Join(", ", configuredSources)}. Exactly one must be provided.");
