using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.Database.Extensions;
using Aura.Database.Context.Stock;
using Aura.Manager.Stock.Extensions;
using AuraCloudInterface.Base;
using AuraCloudInterface.Stock.StockMovement;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using System.Collections.Generic;
using System.Net;

namespace Aura.Manager.Stock;

public class StockMovementManager(ILogger<StockMovementManager> logger, StockContext dbContext, ReadOnlyStockContext readonlyContext, StoreManager storeManager)
{
    private async Task ValidateArgsAsync(int brandId, string storeId)
    {
        var store = await storeManager.ViewStoreSummaryAsync(storeId);

        ApiException.ThrowIfTrue(store == null, $"Store with id {storeId} not found");
        ApiException.ThrowIfTrue(store.BrandId != brandId, $"Store with id {storeId} does not belong to brand with id {brandId}");
    }

    private static void ValidateSourceIds(int? grvItemId, int? processedStockId, int? itemDiscardId, int? processedStockItemId)
    {
        var configuredSources = new List<string>();
        if (grvItemId > 0) configuredSources.Add("GrvItemId");
        if (processedStockId > 0) configuredSources.Add("ProcessedStockId");
        if (itemDiscardId > 0) configuredSources.Add("ItemDiscardId");
        if (processedStockItemId > 0) configuredSources.Add("ProcessedStockItemId");

        ApiException.ThrowIfTrue(configuredSources.Count == 0, "None of GrvItemId, ProcessedStockId, ItemDiscardId, or ProcessedStockItemId have been configured. Exactly one must be provided.");
        ApiException.ThrowIfTrue(configuredSources.Count > 1, $"Multiple source IDs have been configured: {string.Join(", ", configuredSources)}. Exactly one must be provided.");
    }

    public async Task<CreateResponse> CreateStockMovementAsync(int brandId, string storeId, CreateStockMovementRequest request)
    {        
        await ValidateArgsAsync(brandId, storeId);

        ValidateSourceIds(request.GrvItemId, request.ProcessedStockId, request.ItemDiscardId, request.ProcessedStockItemId);

        var stockMovement = new StockMovement
        {
            Timestamp = request.Timestamp,
            Quantity = request.Quantity,
            TotalCostExcl = request.TotalCostExcl,
            GrvItemId = request.GrvItemId,
            ProcessedStockId = request.ProcessedStockId,
            ItemDiscardId = request.ItemDiscardId,
            ProcessedStockItemId = request.ProcessedStockItemId
        };

        dbContext.StockMovements.Add(stockMovement);
        await dbContext.SaveChangesAsync();

        return new CreateResponse { Id = stockMovement.Id };
    }

    public async Task<IEnumerable<StockMovementSummary>> ListStockMovementsAsync(int brandId, string storeId)
    {
        await ValidateArgsAsync(brandId, storeId);

        return await readonlyContext.StockMovements
            .Where(x => (x.GrvItem != null && x.GrvItem.Grv.StoreId == storeId) || (x.ProcessedStock != null && x.ProcessedStock.StoreId == storeId) || (x.ItemDiscard != null && x.ItemDiscard.StoreId == storeId) || (x.ProcessedStockItem != null && x.ProcessedStockItem.ProcessedStock.StoreId == storeId))
            .OrderBy(x => x.Timestamp)
            .Select(x => new StockMovementSummary { Id = x.Id, Quantity = x.Quantity, Timestamp = x.Timestamp, TotalCostExcl = x.TotalCostExcl, GrvItemId = x.GrvItemId, ProcessedStockId = x.ProcessedStockId, ItemDiscardId = x.ItemDiscardId, ProcessedStockItemId = x.ProcessedStockItemId })
            .ToListAsync();
    }

    public async Task<UpdateResponse> UpdateStockMovementAsync(int brandId, string storeId, int stockMovementId, UpdateStockMovementRequest request)
    {
        await ValidateArgsAsync(brandId, storeId);
        ValidateSourceIds(request.GrvItemId, request.ProcessedStockId, request.ItemDiscardId);

        var stockMovement = await dbContext.StockMovements
            .Include(x => x.GrvItem).ThenInclude(x => x.Grv)
            .Include(x => x.ProcessedStock)
            .Include(x => x.ItemDiscard)
            .FirstOrDefaultAsync(x => x.Id == stockMovementId);

        ApiException.ThrowIfTrue(stockMovement == null, $"Stock movement with id {stockMovementId} not found");

        stockMovement.Quantity = request.Quantity;
        stockMovement.Timestamp = request.Timestamp;
        stockMovement.TotalCostExcl = request.TotalCostExcl;
        stockMovement.GrvItemId = request.GrvItemId;
        stockMovement.ProcessedStockId = request.ProcessedStockId;
        stockMovement.ItemDiscardId = request.ItemDiscardId;

        await dbContext.SaveChangesAsync();

        return new UpdateResponse();
    }

    public async Task DeleteStockMovementAsync(int brandId, string storeId, int stockMovementId)
    {
        await ValidateArgsAsync(brandId, storeId);

        var stockMovement = await dbContext.StockMovements
            .Include(x => x.GrvItem).ThenInclude(x => x.Grv)
            .Include(x => x.ProcessedStock)
            .Include(x => x.ItemDiscard)
            .FirstOrDefaultAsync(x => x.Id == stockMovementId);

        ApiException.ThrowIfTrue(stockMovement == null, $"Stock movement with id {stockMovementId} not found");                

        dbContext.StockMovements.Remove(stockMovement);
        await dbContext.SaveChangesAsync();
    }
}

