namespace Aura.Common.Database.Extensions
{
    public static class DbContextExtensions
    {
        public static void DeleteCollection<T>(this DbContext dbContext, ICollection<T> items)
        {
            if (items == null || items.Count == 0)
            {
                return;
            }

            foreach (var item in items)
            {
                if (item != null)
                {
                    dbContext.Remove(item);
                }
            }

            items.Clear();
        }

        public static Task SyncDataItemsAsync<TSrcDataItem, TDstDataItem>(this DbContext db, ICollection<TSrcDataItem> srcDataItems, ICollection<TDstDataItem> dstDataItems, bool trimValues = false)
            where TSrcDataItem : IDataKeyValue
            where TDstDataItem : IDataKeyValue, new()
        {
            var itemsToDelete = srcDataItems.SyncDataItems(dstDataItems, trimValues);
            foreach (var itemToDelete in itemsToDelete)
            {
                db.Remove(itemToDelete);
            }

            return Task.CompletedTask;
        }

        public static async Task ApplyAuditUpdatesAsync(this DbContext db, string database, string tableName, ILogger logger)
