using Aura.Common.Database;
using Aura.Common.Database.Extensions;
using AuraCloudInterface.Stock.Grv;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;

namespace Aura.Database.Context.Stock
{
    public partial class StockContext : IReadWriteDbContext
    {
        string DatabaseName = "aura_cloud_stock";

        protected StockContext(DbContextOptions options) : base(options) { }

        public async Task InitializeAsync(ILogger<IReadWriteDbContext> logger)
        {            
            await this.RenameColumnAsync(DatabaseName, nameof(ProcessedStock), "IsStdRecipe", "IsMasterRecipe", logger);

            await this.DropConstraintAsync(DatabaseName, nameof(ProcessedStock), "FK_ProcessedStock_PostedUnitOfMeasureId", logger);
            await this.DropColumnAsync(DatabaseName, nameof(ProcessedStock), "PostedUnitOfMeasureId", logger);

            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "QuantityPerBatch", "DECIMAL(12,6) NOT NULL DEFAULT 0", logger);

            var columnType = await this.GetColumnTypeAsync(DatabaseName, nameof(ProcessedStock), nameof(ProcessedStock.NumberOfBatches));
            if (columnType.Equals("DECIMAL(18,8)", StringComparison.InvariantCultureIgnoreCase))
            {
                await this.ExecuteSqlAsync($"ALTER TABLE ProcessedStock MODIFY COLUMN NumberOfBatches DECIMAL(12,6) NOT NULL", logger);
            }

            await this.DropColumnAsync(DatabaseName, nameof(ProcessedStockItem), "UnitCost", logger);

            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "BatchReference", "VARCHAR(50) NULL", logger);

            if (await this.DoesColumnExistAsync(DatabaseName, nameof(ProcessedStock), "RecordNo"))
            {
                await this.ExecuteSqlAsync(@"
                UPDATE ProcessedStock ps
                JOIN Item i ON ps.ItemId = i.Id
                SET ps.BatchReference = CONCAT(i.Code, '-', DATE_FORMAT(ps.Timestamp, '%Y-%m-%d'), '-', ps.RecordNo)
                WHERE ps.BatchReference IS NULL OR ps.BatchReference = '';", logger);

                            await this.DropIndexAsync(DatabaseName, nameof(ProcessedStock), "UK_ProcessedStock_BrandId_StoreId_RecordNo", logger);
                 await this.DropColumnAsync(DatabaseName, nameof(ProcessedStock), "RecordNo", logger);
             }

            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "ActualTotalQuantity", "DECIMAL(12,6) NOT NULL DEFAULT 0", logger);

            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStockItem), "ExpectedQuantity", "DECIMAL(12,6) NOT NULL DEFAULT 0", logger);
            await this.RenameColumnAsync(DatabaseName, nameof(ProcessedStockItem), "UnitQuantity", "ActualQuantity", logger);                           

            if (await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "RecipeId", "INT NULL", logger))
            {
                await this.AddConstraintAsync(DatabaseName, nameof(ProcessedStock), "FK_ProcessedStock_RecipeId", "RecipeId", DatabaseName, "Recipe", "Id", logger);
            }
            if (await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "ProcessedStockId", "INT NULL", logger))
            {
                await this.AddConstraintAsync(DatabaseName, nameof(ProcessedStock), "FK_ProcessedStock_ProcessedStockId", "ProcessedStockId", DatabaseName, "ProcessedStock", "Id", logger);
            }

            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "ArchivedReason", "VARCHAR(500)", logger);

            await this.AddColumnAsync(DatabaseName, nameof(GrvItem), "AiReceiptLine", "VARCHAR(500) NULL", logger);

            await this.AddIndexAsync(DatabaseName, nameof(GrvItem), "IX_GrvItem_SupplierMappings", "ItemId, GrvId", false, logger);

            await this.AddLockingColumnsAsync(DatabaseName, nameof(ProcessedStock), logger);
            await this.AddConstraintAsync(DatabaseName, nameof(ProcessedStock), $"FK_{nameof(ProcessedStock)}_LockedByUserId", "LockedByUserId", "aura_cloud_auth", "User", "Id", logger);

            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStock), "TotalCostExcl", "DECIMAL(18,8) NOT NULL DEFAULT 0", logger);
            await this.AddColumnAsync(DatabaseName, nameof(ProcessedStockItem), "CostExcl", "DECIMAL(18,8) NOT NULL DEFAULT 0", logger);

            await this.AddColumnAsync(DatabaseName, nameof(StockMovement), "ProcessedStockItemId", "INT NULL", logger);    
            await this.AddConstraintAsync(DatabaseName, nameof(StockMovement), "FK_StockMovement_ProcessedStockItemId", "ProcessedStockItemId", DatabaseName, nameof(ProcessedStockItem), "Id", logger);

            // Add check constraint to ensure exactly one source FK is set
            await this.AddCheckConstraintAsync(DatabaseName, nameof(StockMovement), "CHK_StockMovement_ExactlyOneSource", 
                "(GrvItemId IS NOT NULL AND ProcessedStockId IS NULL AND ItemDiscardId IS NULL AND ProcessedStockItemId IS NULL) OR " +
                "(GrvItemId IS NULL AND ProcessedStockId IS NOT NULL AND ItemDiscardId IS NULL AND ProcessedStockItemId IS NULL) OR " +
                "(GrvItemId IS NULL AND ProcessedStockId IS NULL AND ItemDiscardId IS NOT NULL AND ProcessedStockItemId IS NULL) OR " +
                "(GrvItemId IS NULL AND ProcessedStockId IS NULL AND ItemDiscardId IS NULL AND ProcessedStockItemId IS NOT NULL)", logger);

            await Task.CompletedTask;
            logger.LogInformation("Initialization of {databaseContext} completed", this.GetType().Name);
        }

        partial void OnModelCreatingPartial(ModelBuilder modelBuilder)
        {
            modelBuilder.UseUtcForAllDateTimes();
        }
    }
}

