                        (GrvItemId IS NULL AND ProcessedStockId IS NULL AND ItemDiscardId IS NULL AND ProcessedStockItemId IS NOT NULL)
                    )", logger);
            }

            await Task.CompletedTask;
            logger.LogInformation("Initialization of {databaseContext} completed", this.GetType().Name);
        }

