    18‚Üí        public async Task<CreateProcessedStockResponse> CreateProcessedStockAsync(int brandId, string? storeId, ProcessedStockType processedStockType, ProcessedStockRequest request, int userId)
    19‚Üí        {            
    20‚Üí            if (request.Status == ProcessedStockStatus.Posted)
    21‚Üí            {
    22‚Üí                ApiException.ThrowIfTrue(!request.Ingredients?.Any() ?? false, "Cannot create a posted batch without ingredients");                
    23‚Üí            }
    24‚Üí
    25‚Üí            var simpleStockItem = await readOnlyContext.Items
    26‚Üí                .Where(i => i.Id == request.ItemId && i.BrandId == brandId)
    27‚Üí                .Select(i => new { i.ProcessedStockTypeId, i.Code })
    28‚Üí                .FirstOrDefaultAsync();
    29‚Üí
    30‚Üí            ApiException.ThrowIfTrue(simpleStockItem?.ProcessedStockTypeId == null, "Stock item not found or has no ProcessedStockType configured.");
    31‚Üí
    32‚Üí            ValidateStockItemProcessedStockType(simpleStockItem.ProcessedStockTypeId, processedStockType, "create");
    33‚Üí            ValidateNumberOfBatches(processedStockType, request.NumberOfBatches);
    34‚Üí            await ValidateStoreAsync(brandId, storeId);
    35‚Üí
    36‚Üí            // Check for existing record created within 1 minute of the request's Timestamp
    37‚Üí            var requestTimeUtc = request.Timestamp.FromUnixTimestamp();
    38‚Üí            var startTime = requestTimeUtc.AddMinutes(-1);
    39‚Üí            var endTime = requestTimeUtc.AddMinutes(1);
    40‚Üí
    41‚Üí           var recentRecordExists = await readOnlyContext.ProcessedStocks
    42‚Üí                .Where(b => b.BrandId == brandId 
    43‚Üí                         && b.StoreId == storeId 
    44‚Üí                         && b.ItemId == request.ItemId
    45‚Üí                         && b.ProcessedStockTypeId == (int)processedStockType
    46‚Üí                         && b.Timestamp >= startTime 
    47‚Üí                         && b.Timestamp <= endTime)
    48‚Üí                .AnyAsync();
    49‚Üí
    50‚Üí#if !DEBUG
    51‚Üí            ApiException.ThrowIfTrue(recentRecordExists, "A record for this stock item was already created within 1 minute of the specified time.");
    52‚Üí#endif
    53‚Üí
    54‚Üí            const int maxRetries = 3;
    55‚Üí
    56‚Üí            var retryCount = 0;
    57‚Üí
    58‚Üí            while (retryCount < maxRetries)
    59‚Üí            {
    60‚Üí                try
    61‚Üí                {
    62‚Üí                    var strategy = dbContext.Database.CreateExecutionStrategy();
    63‚Üí                    return await strategy.ExecuteAsync(async () =>
    64‚Üí                    {
    65‚Üí                        await using var transaction = await dbContext.Database.BeginTransactionAsync();
    66‚Üí
    67‚Üí                        var datePart = request.Timestamp.FromUnixTimestamp().ToString("yyyy-MM-dd");
    68‚Üí                        var searchString = $"{simpleStockItem.Code}-{datePart}-";
    69‚Üí
    70‚Üí                        var latestForToday = await dbContext.ProcessedStocks
    71‚Üí                            .Where(x => x.BrandId == brandId && x.StoreId == storeId && x.BatchReference.StartsWith(searchString))
    72‚Üí                            .OrderByDescending(x => x.BatchReference)
    73‚Üí                            .FirstOrDefaultAsync();
    74‚Üí
    75‚Üí                        var sequence = 1;
    76‚Üí                        if (latestForToday != null)
    77‚Üí                        {
    78‚Üí                            var lastSequence = int.Parse(latestForToday.BatchReference.Split('-').Last());
    79‚Üí                            sequence = lastSequence + 1;
    80‚Üí                        }
    81‚Üí
    82‚Üí                        var processedStock = new ProcessedStock();
    83‚Üí                        request.MapTo(brandId, storeId, processedStock);
    84‚Üí                        processedStock.ProcessedStockTypeId = (int)processedStockType;
    85‚Üí                        processedStock.BatchReference = $"{searchString}{sequence:D3}";
    86‚Üí                                                
    87‚Üí                        if (request.Status == ProcessedStockStatus.Posted)
    88‚Üí                        {                            
    89‚Üí                            processedStock.PostedByUserId = userId;
    90‚Üí                            processedStock.PostedAt = DateTime.UtcNow;
    91‚Üí                        }
    92‚Üí
    93‚Üí                        if (!string.IsNullOrEmpty(request.RequestReference))
    94‚Üí                        {
    95‚Üí                            processedStock.LockedAt = DateTime.UtcNow;
    96‚Üí                            processedStock.LockedByUserId = userId;
    97‚Üí                            processedStock.RequestReference = request.RequestReference;
    98‚Üí                        }
    99‚Üí
   100‚Üí                        dbContext.ProcessedStocks.Add(processedStock);
   101‚Üí                        await dbContext.SaveChangesAsync();
   102‚Üí
   103‚Üí                        // Add ingredients if provided
   104‚Üí                        if (request.Ingredients != null && request.Ingredients.Length > 0)
   105‚Üí                        {
   106‚Üí                            foreach (var ingredient in request.Ingredients)
   107‚Üí                            {
   108‚Üí                                var processedStockItem = new ProcessedStockItem
   109‚Üí                                {
   110‚Üí                                    ProcessedStockId = processedStock.Id,
   111‚Üí                                    ItemId = ingredient.ItemId,
   112‚Üí                                    ExpectedQuantity = ingredient.ExpectedQuantity,
   113‚Üí                                    ActualQuantity = ingredient.ActualQuantity
   114‚Üí                                };
