   116‚Üí                            }
   117‚Üí                            await dbContext.SaveChangesAsync();
   118‚Üí                        }
   119‚Üí
   120‚Üí                        await transaction.CommitAsync();
   121‚Üí
   122‚Üí                        return new CreateProcessedStockResponse(processedStock.Id, processedStock.BatchReference, request.Status, processedStock.PostedAt?.ToUnixTimestamp());
   123‚Üí                    });
   124‚Üí                }
   125‚Üí                catch (DbUpdateException ex)
   126‚Üí                {
   127‚Üí                    retryCount++;
   128‚Üí
   129‚Üí                    if (retryCount == maxRetries)
   130‚Üí                    {
   131‚Üí                        throw new ApiException(System.Net.HttpStatusCode.BadRequest, $"Unable to generate unique batch number after multiple attempts. Error: {ex.Message}");
