   143‚Üí        public async Task<ListResponse<ProcessedStockSummary>> ListProcessedStockAsync(int brandId, ListProcessedStockArgs args)
   144‚Üí        {
   145‚Üí            var sourceFilter = args.Source switch
   146‚Üí            {
   147‚Üí                ProcessedStockSource.Manual => (Expression<Func<ProcessedStock, bool>>)(x => x.RecipeId == null && x.ProcessedStockId == null),
   148‚Üí                ProcessedStockSource.Batch => (Expression<Func<ProcessedStock, bool>>)(x => x.ProcessedStockId != null),
   149‚Üí                ProcessedStockSource.Recipe => (Expression<Func<ProcessedStock, bool>>)(x => x.RecipeId != null),
   150‚Üí                _ => null
   151‚Üí            };
   152‚Üí
   153‚Üí            var query = readOnlyContext.ProcessedStocks
   154‚Üí                .Include(x => x.Item).ThenInclude(item => item.Category)
   155‚Üí                .Include(x => x.Item).ThenInclude(item => item.UnitOfMeasure)
   156‚Üí                .Include(x => x.ProcessedStockStatus)
   157‚Üí                .Include(x => x.ProcessedStockItems)
   158‚Üí                .WhereIfElse(!string.IsNullOrEmpty(args.StoreId), 
   159‚Üí                    x => x.BrandId == brandId && x.StoreId == args.StoreId, 
   160‚Üí                    x => x.BrandId == brandId && x.StoreId == null)
   161‚Üí                .Where(x => x.ProcessedStockTypeId == (int)args.ProcessedStockType)
   162‚Üí                .WhereIf(args.IsArchived.HasValue, x => x.IsArchived == args.IsArchived!.Value)
   163‚Üí                .WhereIf(args.FromTimestamp.HasValue, x => x.Timestamp >= args.FromTimestamp!.Value.FromUnixTimestamp())
   164‚Üí                .WhereIf(args.ToTimestamp.HasValue, x => x.Timestamp <= args.ToTimestamp!.Value.FromUnixTimestamp())
   165‚Üí                .WhereIf(args.StockListId is > 0, x => x.Item.StockListItems.Any(sli => sli.StockListId == args.StockListId!.Value))
   166‚Üí                .WhereIf(args.StockCategoryId is > 0, x => x.Item.CategoryId == args.StockCategoryId!.Value)
   167‚Üí                .WhereIf(args.ItemId is > 0, x => x.ItemId == args.ItemId!.Value)
   168‚Üí                .WhereIf(args.Status.HasValue, x => x.ProcessedStockStatusId == (int)args.Status!.Value)
   169‚Üí                .WhereIf(sourceFilter != null, sourceFilter!)
   170‚Üí                .WhereIf(!string.IsNullOrWhiteSpace(args.Search), x =>
   171‚Üí                    x.Item.Name.Contains(args.Search!) ||
   172‚Üí                    x.Item.Category.Name.Contains(args.Search!) ||
   173‚Üí                    (x.Item.Code != null && x.Item.Code.Contains(args.Search!)));
   174‚Üí
   175‚Üí            var orderFunctions = new Dictionary<string, Expression<Func<ProcessedStock, object>>>
   176‚Üí            {
   177‚Üí                { nameof(ProcessedStockSummary.BatchReference), x => x.BatchReference },
   178‚Üí                { "LocalTimestamp", x => x.Timestamp },
   179‚Üí                { nameof(ProcessedStockSummary.Timestamp), x => x.Timestamp },
   180‚Üí                { nameof(ProcessedStockSummary.ItemCode), x => x.Item.Code ?? string.Empty },
   181‚Üí                { nameof(ProcessedStockSummary.ItemName), x => x.Item.Name },
   182‚Üí                { nameof(ProcessedStockSummary.ItemCategoryName), x => x.Item.Category.Name },
   183‚Üí                { nameof(ProcessedStockSummary.ItemUnitOfMeasureName), x => x.Item.UnitOfMeasure.Name },
   184‚Üí                { nameof(ProcessedStockSummary.NumberOfBatches), x => x.NumberOfBatches },
   185‚Üí                { nameof(ProcessedStockSummary.QuantityPerBatch), x => x.QuantityPerBatch },
   186‚Üí                { nameof(ProcessedStockSummary.Notes), x => x.Notes ?? string.Empty },                
   187‚Üí                { nameof(ProcessedStockSummary.Status), x => x.ProcessedStockStatus.Name },
   188‚Üí                { nameof(ProcessedStockSummary.IsArchived), x => x.IsArchived },
   189‚Üí                { nameof(ProcessedStockSummary.IsMasterRecipe), x => x.IsMasterRecipe }
   190‚Üí            };
   191‚Üí            query = query.AddOrderBy(args.Sort, orderFunctions, defaultOrder: x => x.Timestamp, isDefaultOrderDescending: true);
   192‚Üí
   193‚Üí            var paged = await query.PaginateAsync(args.Page ?? 1, args.PageSize ?? 15);
   194‚Üí
   195‚Üí            return new ListResponse<ProcessedStockSummary>
