}

public class ProcessedStockSummary : EntitySummary, ILockableEntity
{
    public string BatchReference { get; set; } = string.Empty;
    public string? StoreId { get; set; }
    public int ItemId { get; set; }
    public string ItemCode { get; set; } = string.Empty;
    public string ItemName { get; set; } = string.Empty;
    public string ItemCategoryName { get; set; } = string.Empty;
    public string ItemUnitOfMeasureName { get; set; } = string.Empty;
    public long Timestamp { get; set; }
    public decimal NumberOfBatches { get; set; }
    public decimal QuantityPerBatch { get; set; }
    public decimal ActualTotalQuantity { get; set; }   
    public decimal TotalCostExcl { get; set; }
    public string? Notes { get; set; }
    public int? PostedByUserId { get; set; }
    public long? PostedTimestamp { get; set; }
    public ProcessedStockStatus Status { get; set; }
    public bool IsArchived { get; set; }
    public string? ArchivedReason { get; set; }
    public bool IsMasterRecipe { get; set; }
    public ProcessedStockType ProcessedStockType { get; set; }
    public List<ProcessedStockIngredientSummary>? Ingredients { get; set; }        
    public int? ProcessedStockId { get; set; }  // ID of source batch (null if created from scratch)
    public ProcessedStockTemplateType TemplateType { get; set; } = ProcessedStockTemplateType.Scratch;
    public DateTime? LockedAt { get; set; }
    public int? LockedByUserId { get; set; }
    public string? RequestReference { get; set; }
}

