using Aura.Database.Context.Stock;
using AuraCloudInterface.Stock.Grv;
using AuraCloudInterface.Stock.ItemDiscard;
using AuraCloudInterface.Stock.Items;
using AuraCloudInterface.Stock.Lists;
using AuraCloudInterface.Stock.Supplier;
using AuraCloudInterface.Stock.Take;
using GrvStatus = AuraCloudInterface.Stock.Grv.GrvStatus;
using StockTakeStatus = AuraCloudInterface.Stock.Take.StockTakeStatus;
using AuraCloudInterface.Stock.Processed;
using Aura.Common.Extensions;

namespace Aura.Manager.Stock.Extensions
{
    internal static class MapperExtensions
    {
        public static StockListSummary ToStockListSummary(this StockList stockList)
        {
            return new StockListSummary
            {
                Id = stockList.Id,
                Name = stockList.Name,
                IsArchived = stockList.IsArchived,
                FrequencyId = stockList.StockListFrequencyId,
                FrequencyValueId = stockList.StockListFrequencyValueId,
                StockItemCount = stockList.StockListItems.Where(x => !x.Item.IsArchived).Count(),
            };
        }

        public static StockListWithItemsSummary ToStockListWithItemsSummary(this StockList stockList)
        {
            var stockItemIds = stockList.StockListItems.Where(x => !x.Item.IsArchived).Select(x => x.ItemId).ToList();

            return new StockListWithItemsSummary
            {
                Id = stockList.Id,
                StoreId = stockList.StoreId,
                Name = stockList.Name,
                IsArchived = stockList.IsArchived,
                FrequencyId = stockList.StockListFrequencyId,
                FrequencyValueId = stockList.StockListFrequencyValueId,
                StockItemCount = stockItemIds.Count,
                StockItemIds = stockItemIds,
            };
        }

        public static StockList ToStockList(this CreateStockListRequest request, string storeId)
        {
            return new StockList
            {
                StoreId = storeId,
                Name = request.Name,
                StockListFrequencyId = request.FrequencyId,
                StockListFrequencyValueId = request.FrequencyValueId
            };
        }

        public static void MapTo(this UpdateStockListRequest request, StockList stockList)
        {
            stockList.Name = request.Name;
            stockList.IsArchived = request.IsArchived;
            stockList.StockListFrequencyId = request.FrequencyId;
            stockList.StockListFrequencyValueId = request.FrequencyValueId;
        }

        public static Supplier ToSupplier(this SupplierRequest request, string storeId)
        {
            return new Supplier
            {
                StoreId = storeId,
                Name = request.Name,
                Phone = request.Phone,
                Email = request.Email,
                ContactPerson = request.ContactPerson,
                AccountNumber = request.AccountNumber,
                PhysicalAddress = request.PhysicalAddress,
                Notes = request.Notes,
                Website = request.Website,
                IsRegisteredForTax = request.IsRegisteredForTax,
            };
        }

        public static SupplierSummary ToSupplierSummary(this Supplier supplier)
        {
            return new SupplierSummary
            {
                Id = supplier.Id,
                Name = supplier.Name,
                Phone = supplier.Phone,
                Email = supplier.Email,
                ContactPerson = supplier.ContactPerson,
                AccountNumber = supplier.AccountNumber,
                PhysicalAddress = supplier.PhysicalAddress,
                Notes = supplier.Notes,
                Website = supplier.Website,
                IsRegisteredForTax = supplier.IsRegisteredForTax,
                StoreId = supplier.StoreId,
                TaxNumber = supplier.TaxNumber,
                IsArchived = supplier.IsArchived
            };
        }

        public static void MapTo(this SupplierRequest request, Supplier supplier)
        {
            supplier.Name = request.Name;
            supplier.Phone = request.Phone;
            supplier.Email = request.Email;
            supplier.ContactPerson = request.ContactPerson;
            supplier.AccountNumber = request.AccountNumber;
            supplier.PhysicalAddress = request.PhysicalAddress;
            supplier.Notes = request.Notes;
            supplier.Website = request.Website;
            supplier.IsRegisteredForTax = request.IsRegisteredForTax;
            supplier.TaxNumber = request.TaxNumber;
        }

        public static StockTakeSummary ToStockTakeSummary(this StockTake stockTake, Dictionary<int, string> userLookup)
        {
            userLookup.TryGetValue(stockTake.CreatedByUserId, out var createdByUser);

            return new StockTakeSummary
            {
                Id = stockTake.Id,
                StoreId = stockTake.StoreId,
                DateTime = stockTake.DateTime,
                Status = (StockTakeStatus)stockTake.StockTakeStatusId,
                CreatedByUserId = stockTake.CreatedByUserId,
                CreatedByUser = createdByUser ?? "Unknown User",
                Note = stockTake.Note,
                IsArchived = stockTake.IsArchived,
                AvailableStockItemCount = stockTake.AvailableStockItemCount,
            };
        }

        public static StockTake CreateStockTake(this CreateStockTakeRequest request, string storeId, int createdByUserId)
        {
            return new StockTake
            {
                StoreId = storeId,
                DateTime = request.DateTime,
                CreatedAt = DateTime.UtcNow,
                CreatedByUserId = createdByUserId,
                StockTakeStatusId = (int)StockTakeStatus.Draft,
                Note = null,
                ValuesUpdatedAt = DateTime.UtcNow
            };
        }

        public static void MapTo(this UpdateStockTakeRequest request, StockTake stockTake)
        {
            stockTake.DateTime = request.DateTime;
            stockTake.Note = request.Note;            
        }

        public static GrvSummary ToGrvSummary(this Grv grv, Dictionary<int, string>? userLookup = null)
        {
            return new GrvSummary
            {
                Id = grv.Id,
                StoreId = grv.StoreId,

                GrvDate = grv.GrvDate,
                SupplierReference = grv.SupplierReference,
                GrvNumber = grv.GrvNumber,
                TotalCostIncl = grv.TotalCostIncl,
                Notes = grv.Notes,

                StockMovementAt = grv.StockMovementAt,
                StockMovementBy = userLookup?.GetValueOrDefault(grv.StockMovementByUserId, "Unknown user") ?? string.Empty,
                LockedAt = grv.LockedAt,
                LockedBy = grv.LockedByUserId is > 0 ? userLookup?.GetValueOrDefault(grv.LockedByUserId.Value, "Unknown user") : null,

                Status = (GrvStatus)grv.GrvStatusId,
                PurchaseOrderNumber = grv.PurchaseOrderNumber,                
                TransactionType = (AuraCloudInterface.Stock.Grv.GrvTransactionType)grv.GrvTransactionTypeId,
                Supplier = grv.Supplier?.ToSupplierSummary(),
                ItemCount = grv.GrvItems.Count,

                CashupDataId = grv.CashupDataId,
                CashupDataAmount = grv.CashupDataAmount,

                ParentGrvId = grv.ParentGrvId,
                IsArchived = grv.IsArchived,
                RelatedCreditNoteIds = grv.InverseParentGrv
                    .Where(x => x.GrvTransactionTypeId == 2 || x.GrvTransactionTypeId == 3)
                    .Select(x => x.Id)
                    .ToList(),
                RelatedCreditNotes = grv.InverseParentGrv
                    .Where(x => x.GrvTransactionTypeId == 2 || x.GrvTransactionTypeId == 3)
                    .Select(x => new GrvRelatedSummary
                    {
                        Id = x.Id,
                        GrvNumber = x.GrvNumber,
                        Status = (GrvStatus)x.GrvStatusId,
                        IsArchived = x.IsArchived,
                        TransactionType = (AuraCloudInterface.Stock.Grv.GrvTransactionType)x.GrvTransactionTypeId
                    })
                    .ToList(),
                ParentGrv = grv.ParentGrv != null 
                    ? new GrvRelatedSummary
                    {
                        Id = grv.ParentGrv.Id,
                        GrvNumber = grv.ParentGrv.GrvNumber,
                        Status = (GrvStatus)grv.ParentGrv.GrvStatusId,
                        IsArchived = grv.ParentGrv.IsArchived,
                        TransactionType = null
                    }
                    : null
            };
        }

        public static Grv CreateGrv(this CreateGrvRequest request, string storeId, int grvNumber, int? supplierId, int userId)
        {
            var currentDateTime = DateTime.UtcNow;

            return new Grv
            {
                GrvTransactionTypeId = (int)request.TransactionType,
                ParentGrvId = request.ParentGrvId,
                StoreId = storeId,
                GrvNumber = grvNumber,
                GrvStatusId = (int)GrvStatus.Draft,
                GrvDate = currentDateTime,
                ValuesUpdatedAt = currentDateTime,
                SupplierId = supplierId,                
                StockMovementAt = currentDateTime,
                StockMovementByUserId = userId,
            };
        }

        public static void MapTo(this UpdateGrvRequest request, Grv grv, int userId)
        {
            if (request.TotalCost != grv.TotalCostIncl || request.CashupDataAmount != grv.CashupDataAmount)
            {
                grv.ValuesUpdatedAt = DateTime.UtcNow;
            }

            if (request.StockMovementAt != grv.StockMovementAt)
            {                
                grv.StockMovementByUserId = userId;
            }

            grv.SupplierId = request.SupplierId;
            grv.GrvDate = request.GrvDate;
            grv.SupplierReference = request.SupplierReference;
