        public static StockItemSummary ToSummary(this Item stockItem, string storeId)
        {
            return stockItem.ToSummary(null, storeId);
        }

        public static StockItemSummary ToSummary(this Item stockItem, int? recipeRevisionId, string? storeId)
        {
            var storeOverride = !string.IsNullOrEmpty(storeId)
                ? stockItem.ItemDetailStoreOverrides.FirstOrDefault(a => a.StoreId == storeId && a.ItemId == stockItem.Id)
                : null;

            decimal? storePreppedCost = null;
            if (storeOverride?.BulkCost != null)
            {
                var wastageToUse = storeOverride.WastagePercentage ?? stockItem.WastagePercentage;
                storePreppedCost = storeOverride.BulkCost / (1 - (wastageToUse / 100));
            }

            return new StockItemSummary
            {
                Id = stockItem.Id,
                BrandId = stockItem.BrandId,
                Name = stockItem.Name,
                Description = stockItem.Description,
                UnitOfMeasureId = stockItem.UnitOfMeasureId,
                Code = stockItem.Code,
                IsArchived = stockItem.IsArchived,
                ProcessedStockType = (AuraCloudInterface.Stock.Processed.ProcessedStockType?)stockItem.ProcessedStockTypeId,
                CategoryId = stockItem.CategoryId,
                WastagePercentage = stockItem.WastagePercentage,
                ModelUnpreppedCost = stockItem.ModelUnpreppedCost,
                ModelPreppedCost = stockItem.ModelUnpreppedCost / (1 - (stockItem.WastagePercentage / 100)),
                IsGpitem = stockItem.IsGpitem,
                StoreId = stockItem.StoreId,
                Barcode = stockItem.Barcode,
                StockTypeId = stockItem.StockTypeId,
                StorePreppedCost = storePreppedCost,
                StoreUnpreppedCost = storeOverride?.BulkCost,
                StoreWastagePercentage = storeOverride?.WastagePercentage,
                StoreParLevel = storeOverride?.ParLevel,
                StockListIds = stockItem.StockListItems.Select(x => x.StockListId).ToList(),
                TaxRateIds = stockItem.ItemTaxes.Select(x => x.TaxRateId).ToList(),
                RecipeId = recipeRevisionId.HasValue ? stockItem.RecipeItems.Where(recipe => recipe.ItemId == stockItem.Id && recipe.RecipeRevisionId == recipeRevisionId).Select(x => x.RecipeId).FirstOrDefault() : null,
            };
        }

        public static StockItemLookupSummary ToStockItemLookupSummary(this Item item)
        {
            return new StockItemLookupSummary
            {
                Id = item.Id,
                Name = item.Name,
                Code = item.Code ?? string.Empty,
                UnitOfMeasure = item.UnitOfMeasure.Name
            };
        }

        public static void MapTo(this ProcessedStockRequest request, int brandId, string? storeId, ProcessedStock processedStock)
        {
            processedStock.BrandId = brandId;
            processedStock.StoreId = storeId;
            processedStock.ProcessedStockStatusId = (int)request.Status;            
            processedStock.ItemId = request.ItemId;
            processedStock.Timestamp = request.Timestamp.FromUnixTimestamp();
            processedStock.NumberOfBatches = request.NumberOfBatches;
            processedStock.QuantityPerBatch = request.QuantityPerBatch;            
            processedStock.ActualTotalQuantity = request.ActualTotalQuantity;
            processedStock.TotalCostExcl = request.TotalCostExcl;
            processedStock.Notes = request.Notes;
            processedStock.IsMasterRecipe = request.IsMasterRecipe;
            processedStock.RecipeId = request.RecipeId;
            processedStock.ProcessedStockId = request.ProcessedStockId;
        }

        public static ProcessedStockSummary ToSummary(this ProcessedStock processedStock, bool includeIngredients = false)
        {
            return new ProcessedStockSummary
            {
                Id = processedStock.Id,
                BatchReference = processedStock.BatchReference,                
                StoreId = processedStock.StoreId,
                ItemId = processedStock.ItemId,
                ItemCode = processedStock.Item?.Code ?? string.Empty,
                ItemName = processedStock.Item?.Name ?? string.Empty,
                ItemCategoryName = processedStock.Item?.Category?.Name ?? string.Empty,
                ItemUnitOfMeasureName = processedStock.Item?.UnitOfMeasure?.Name ?? string.Empty,
                Timestamp = processedStock.Timestamp.ToUnixTimestamp(),
                NumberOfBatches = processedStock.NumberOfBatches,
                QuantityPerBatch = processedStock.QuantityPerBatch,
                ActualTotalQuantity = processedStock.ActualTotalQuantity,
                TotalCostExcl = processedStock.TotalCostExcl,
                Notes = processedStock.Notes,
                PostedByUserId = processedStock.PostedByUserId,
                PostedTimestamp = processedStock.PostedAt?.ToUnixTimestamp(),
                Status = (AuraCloudInterface.Stock.Processed.ProcessedStockStatus)processedStock.ProcessedStockStatusId,
                IsArchived = processedStock.IsArchived,
                ArchivedReason = processedStock.ArchivedReason,
                IsMasterRecipe = processedStock.IsMasterRecipe,
                ProcessedStockType = (AuraCloudInterface.Stock.Processed.ProcessedStockType)processedStock.ProcessedStockTypeId,                                
                ProcessedStockId = processedStock.ProcessedStockId,

                TemplateType = processedStock.IsMasterRecipe 
                    ? ProcessedStockTemplateType.Master
                    : (processedStock.ProcessedStockId.HasValue ? ProcessedStockTemplateType.Previous : ProcessedStockTemplateType.Scratch),

                Ingredients = includeIngredients ? processedStock.ProcessedStockItems?.Select(x => new ProcessedStockIngredientSummary
                {
                    ItemId = x.ItemId,
                    ItemName = x.Item?.Name ?? string.Empty,
                    ExpectedQuantity = x.ExpectedQuantity,
                    ActualQuantity = x.ActualQuantity,
                    UnitOfMeasureName = x.Item?.UnitOfMeasure?.Name ?? string.Empty,
                    CostAtUse = null
                }).ToList() : null,
                LockedAt = processedStock.LockedAt,
                LockedByUserId = processedStock.LockedByUserId,
                RequestReference = processedStock.RequestReference
            };
        }

        public static ProcessedStockItemSummary ToSummary(this ProcessedStockItem item)
        {
            return new ProcessedStockItemSummary(
                Id: item.Id,
                ProcessedStockId: item.ProcessedStockId,
                ItemId: item.ItemId,
                ItemCode: item.Item.Code ?? string.Empty,
                ItemName: item.Item.Name,
                UnitOfMeasureId: item.Item.UnitOfMeasureId,
                UnitOfMeasure: item.Item.UnitOfMeasure.Name,
                ExpectedQuantity: item.ExpectedQuantity,
                ActualQuantity: item.ActualQuantity
            );
        }
    }
}

