using Aura.Database.Context.Stock;
using AuraCloudInterface.Stock.Grv;
using AuraCloudInterface.Stock.ItemDiscard;
using AuraCloudInterface.Stock.Items;
using AuraCloudInterface.Stock.Lists;
using AuraCloudInterface.Stock.Supplier;
using AuraCloudInterface.Stock.Take;
using GrvStatus = AuraCloudInterface.Stock.Grv.GrvStatus;
using StockTakeStatus = AuraCloudInterface.Stock.Take.StockTakeStatus;
using AuraCloudInterface.Stock.Processed;
using Aura.Common.Extensions;

namespace Aura.Manager.Stock.Extensions
{
    internal static class MapperExtensions
    {
        public static StockListSummary ToStockListSummary(this StockList stockList)
        {
            return new StockListSummary
            {
                Id = stockList.Id,
                Name = stockList.Name,
                IsArchived = stockList.IsArchived,
                FrequencyId = stockList.StockListFrequencyId,
                FrequencyValueId = stockList.StockListFrequencyValueId,
                StockItemCount = stockList.StockListItems.Where(x => !x.Item.IsArchived).Count(),
            };
        }

        public static StockListWithItemsSummary ToStockListWithItemsSummary(this StockList stockList)
        {
            var stockItemIds = stockList.StockListItems.Where(x => !x.Item.IsArchived).Select(x => x.ItemId).ToList();

            return new StockListWithItemsSummary
            {
                Id = stockList.Id,
                StoreId = stockList.StoreId,
                Name = stockList.Name,
                IsArchived = stockList.IsArchived,
