        }

        public static GrvSummary ToGrvSummary(this Grv grv, Dictionary<int, string>? userLookup = null)
        {
            return new GrvSummary
            {
                Id = grv.Id,
                StoreId = grv.StoreId,

                GrvDate = grv.GrvDate,
                SupplierReference = grv.SupplierReference,
                GrvNumber = grv.GrvNumber,
                TotalCostIncl = grv.TotalCostIncl,
                Notes = grv.Notes,

                StockMovementAt = grv.StockMovementAt,
                StockMovementBy = userLookup?.GetValueOrDefault(grv.StockMovementByUserId, "Unknown user") ?? string.Empty,
                LockedAt = grv.LockedAt,
                LockedBy = grv.LockedByUserId is > 0 ? userLookup?.GetValueOrDefault(grv.LockedByUserId.Value, "Unknown user") : null,

                Status = (GrvStatus)grv.GrvStatusId,
