    49â†’                || x.Item.Name.Contains(args.Search!) 
    50â†’                || (x.Item.Description != null && x.Item.Description.Contains(args.Search!)));
    51â†’
    52â†’        var orderFunctions = new Dictionary<string, Expression<Func<ItemDiscard, object>>>
    53â†’        {
    54â†’            { nameof(StockItemDiscardSummary.Timestamp), x => x.Timestamp },
