ï»¿using Aura.CloudBusinessLogic;
using Aura.Common.WebService.Authorization;
using Aura.Manager.Stock;
using AuraCloudInterface.Base;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using System.Threading.Tasks;
using AuraCloudInterface.Constants;
using AuraCloudInterface.Stock.ItemDiscard;
using System;

namespace CloudGatewayApi.Controllers;

[ApiController]
[Route("[controller]")]
[Authorize]
public class StockItemDiscardsController(StockItemDiscardManager manager, UserManager userManager, Aura.Manager.Permissions.PermissionsManager permissionsManager) : AuthenticatedControllerWithUser(userManager: userManager)
{
    [HttpPost("/brands/{brandId}/stores/{storeId}/stock/items/{itemId}/discards")]
    [AuthorizedRoles(PortalPermission.CanManageStockItemDiscards)]
    public async Task<ActionResult<CreateResponse>> CreateStockItemDiscard(int brandId, string storeId, int itemId, CreateStockItemDiscardRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageStockItemDiscards);

        return Ok(await manager.CreateStockItemDiscardAsync(brandId, storeId, itemId, request));
    }

    [HttpGet("/brands/{brandId}/stores/{storeId}/stock/items/discards")]
    [AuthorizedRoles(PortalPermission.CanViewStockItemDiscards, PortalPermission.CanManageStockItemDiscards)]
    public async Task<ActionResult<ListResponse<StockItemDiscardSummary>>> ListStockItemDiscards(int brandId, string storeId, [FromQuery] StockItemDiscardListArgs args)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewStockItemDiscards, PortalPermission.CanManageStockItemDiscards);

        return Ok(await manager.ListStockItemDiscardsAsync(brandId, storeId, args));
    }

    [HttpGet("/brands/{brandId}/stores/{storeId}/stock/items/{itemId}/discards/{itemDiscardId}")]
    [AuthorizedRoles(PortalPermission.CanViewStockItemDiscards, PortalPermission.CanManageStockItemDiscards)]
    public async Task<ActionResult<StockItemDiscardSummary>> ViewStockItemDiscard(int brandId, string storeId, int itemId, int itemDiscardId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewStockItemDiscards, PortalPermission.CanManageStockItemDiscards);

        return Ok(await manager.ViewStockItemDiscardAsync(brandId, storeId, itemId, itemDiscardId));
    }

    [HttpDelete("/brands/{brandId}/stores/{storeId}/stock/items/{itemId}/discards/{itemDiscardId}")]
    [AuthorizedRoles(PortalPermission.CanManageStockItemDiscards)]
    public async Task<ActionResult> DeleteStockItemDiscard(int brandId, string storeId, int itemId, int itemDiscardId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageStockItemDiscards);

        await manager.DeleteStockItemDiscardAsync(brandId, storeId, itemId, itemDiscardId);
        return Ok();
    }
}

