using Aura.Database.Context.Stock;
using Aura.Database.Context.Stock.Extensions;
using Aura.Manager.Companion.Extensions;
using Aura.Manager.Discounts.Extensions;
using Aura.Manager.ExternalDataQuery.Extensions;
