            builder.Services.AddTransient<PaymentManager, PaymentManager>();
            builder.Services.AddTransient<DeviceManager, DeviceManager>();
            builder.Services.AddTransient<IntegrationManager, IntegrationManager>();
            builder.Services.AddTransient<ReceiptManager, ReceiptManager>();
            builder.Services.AddTransient<TillManager>();
            builder.Services.AddTransient<InvoiceManager>();
            builder.Services.AddTransient<LegalEntitiesManager>();
            builder.Services.AddTransient<StoreGroupManager>();
            builder.Services.AddTransient<MediatorManager>();
            builder.Services.AddTransient<TestManager>();
