            builder.Services.AddTransient<ProfilesManager>();
            builder.Services.AddTransient<PosUserManager>();
            builder.Services.AddTransient<CashupManager>();
            builder.Services.AddTransient<DelivereeManager>();
            builder.Services.AddTransient<PermissionsManager>();
            builder.Services.AddTransient<DeviceTypeManager>();
            builder.Services.AddTransient<EmailManager>();
            builder.Services.AddTransient<VoidReasonsManager>();
            builder.Services.AddTransient<CustomerManager>();
            builder.Services.AddTransient<UserManager>();
            builder.Services.AddTransient<BrandImagesManager>();
            builder.Services.AddTransient<StoreNetworkPrinterManager>();
            builder.Services.AddTransient<FiscalPrinterManager>();
            builder.Services.AddTransient<ProductionRoleManager>();
            builder.Services.AddTransient<VirtualDevicesManager>();
            builder.Services.AddTransient<PrinterTypeManager>();
            builder.Services.AddTransient<KitchenWorkflowsManager>();
            builder.Services.AddTransient<KitchenWorkflowStepsManager>();
            builder.Services.AddTransient<LanguageManager>();
            builder.Services.AddTransient<DashboardManager>();
            builder.Services.AddTransient<ThemeManager>();
            builder.Services.AddTransient<PosPermissionsManager>();
            builder.Services.AddTransient<RoleManager>();
            builder.Services.AddTransient<StockManager>();
            builder.Services.AddTransient<StockListManager>();
