            builder.Services.AddTransient<GrvManager>();
            builder.Services.AddTransient<StockItemsManager>();
            builder.Services.AddTransient<StockItemDiscardManager>();
            builder.Services.AddTransient<StoreTransactionManager>();
            builder.Services.AddTransient<GrossProfitReportManager>();
            builder.Services.AddTransient<FifoCalculator>();
            builder.Services.AddTransient<TheoreticalSalesReportManager>();
            builder.Services.AddSingleton<IBackgroundTaskQueue<StockContext, ReadOnlyStockContext>, BackgroundTaskQueue<StockContext, ReadOnlyStockContext>>();
            builder.Services.AddHostedService<QueuedBackgroundTaskService<StockContext, ReadOnlyStockContext>>();
            builder.Services.AddTransient<TheoreticalStockOnHandManager>();
            builder.Services.AddTransient<StockLossReportManager>();
            builder.Services.AddTransient<ProcessedStockManager>();
            builder.Services.AddTransient<StockMovementManager>();

            builder.Services.RegisterGrvImportManager(builder.Configuration);
            builder.Services.RegisterDiscountManager(builder.Configuration);
            builder.Services.RegisterMenuManagers(builder.Configuration);
            builder.Services.RegisterPermissionManager(builder.Configuration);
            builder.Services.RegisterExternalDataQueryManagers(builder.Configuration);
            builder.Services.RegisterGalleryManager(builder.Configuration);
            builder.Services.RegisterMediaManager(builder.Configuration);

#if DEBUG
            IdentityModelEventSource.ShowPII = true;
#endif
            var key = Encoding.ASCII.GetBytes(builder.Configuration["UserAuth:JwtKey"]);

            builder.Services.AddAuthentication(auth =>
            {
                auth.DefaultAuthenticateScheme = JwtBearerDefaults.AuthenticationScheme;
                auth.DefaultChallengeScheme = JwtBearerDefaults.AuthenticationScheme;
            })
            .AddJwtBearer(token =>
            {
#if DEBUG
                token.IncludeErrorDetails = true;
                token.RequireHttpsMetadata = false;
#else
                token.RequireHttpsMetadata = true;
#endif
                token.SaveToken = true;
                token.TokenValidationParameters = new TokenValidationParameters
                {
                    ValidateIssuerSigningKey = true,
                    IssuerSigningKey = new SymmetricSecurityKey(key),
                    ValidateIssuer = true,
                    ValidIssuer = builder.Configuration["UserAuth:Issuer"],
                    ValidateAudience = true,
                    ValidAudience = builder.Configuration["UserAuth:Audience"],
                    ValidateLifetime = true,
                };
            });

            builder.Services.AddControllers(options =>
            {
                options.ModelBinderProviders.Insert(0, new CommaSeparatedIntArrayModelBinderProvider());
            })
            .AddJsonOptions(options =>
            {
                options.JsonSerializerOptions.Converters.Add(new AssumeUtcDateTimeConverter());
                options.JsonSerializerOptions.PropertyNameCaseInsensitive = true;
            });

            builder.Services.AddCors();
            builder.Services.AddSwaggerGen(options => {
                options.CustomSchemaIds(type => type.FullName);
            });
            builder.Services.AddHttpContextAccessor();

            builder.Services.Configure<KestrelServerOptions>(options => options.AllowSynchronousIO = true);

            builder.Services.Configure<FormOptions>(options =>
            {
                options.KeyLengthLimit = 1024 * 16;
            });

            builder.Services.AddAuthenticatedUser();

            var app = builder.Build();

            app.UseExceptionHandling();
            if (app.Environment.IsDevelopment() || app.Environment.IsStaging())
            {
                app.UseSwagger();
                app.UseSwaggerUI();
            }
            app.UseRouting();
            app.UseCors(x => x
                            .AllowAnyMethod()
                            .AllowAnyHeader()
                            .SetIsOriginAllowed(origin => true) // allow any origin
                            .AllowCredentials()); // allow credentials
            app.UseAuthentication();
            app.UseAuthorization();

            app.UseEndpoints(endpoints =>
            {
                endpoints.MapControllers();
            });

            using (var scope = app.Services.CreateScope())
            {
                var logger = scope.ServiceProvider.GetRequiredService<ILogger<Program>>();
                logger.LogInformation("Initializing db contexts");
                try
                {
                    await app.InitializeDbContextAsync<StockContext>();
                }
                catch (Exception ex)
                {
                    Console.WriteLine("Error initializing db context");
                    Console.WriteLine(ex.ToString());
                    logger.LogError(ex, "Error initializing db context");
                    throw;
                }
            }

            await app.RunAsync();
        }
    }           
}

