        return new CreateResponse(itemDiscard.Id);
    }

    public async Task<ListResponse<StockItemDiscardSummary>> ListStockItemDiscardsAsync(int brandId, string storeId, StockItemDiscardListArgs args)
    {
        var itemDiscards = readonlyContext
            .ItemDiscards
            .Include(x => x.Item).ThenInclude(item => item.UnitOfMeasure)
            .Where(x => x.StoreId == storeId)
            .WhereIf(args.FromDateTime.HasValue, x => x.Timestamp >= args.FromDateTime!.Value)
            .WhereIf(args.ToDateTime.HasValue, x => x.Timestamp <= args.ToDateTime!.Value)
            .WhereIf(!string.IsNullOrWhiteSpace(args.Search), x => 
                (x.Item.Code != null && x.Item.Code.Contains(args.Search!)) 
                || x.Item.Name.Contains(args.Search!) 
                || (x.Item.Description != null && x.Item.Description.Contains(args.Search!)));

        var orderFunctions = new Dictionary<string, Expression<Func<ItemDiscard, object>>>
        {
            { nameof(StockItemDiscardSummary.Timestamp), x => x.Timestamp },
            { nameof(StockItemDiscardSummary.ItemCode), x => x.Item.Code },
            { nameof(StockItemDiscardSummary.ItemName), x => x.Item.Name },
            { nameof(StockItemDiscardSummary.ItemUoM), x => x.Item.UnitOfMeasure.Name },
            { nameof(StockItemDiscardSummary.PreppedQuantity), x => x.PreppedQuantity },
            { nameof(StockItemDiscardSummary.BulkQuantity), x => x.BulkQuantity },
            { nameof(StockItemDiscardSummary.Notes), x => x.Notes },
        };
        itemDiscards = itemDiscards.AddOrderBy(args.Sort, orderFunctions, defaultOrder: x => x.Timestamp, isDefaultOrderDescending :true);

        var currentPage = await itemDiscards.PaginateAsync(args.Page ?? -1, args.PageSize ?? 15);

        return new ListResponse<StockItemDiscardSummary>()
        {
            Items = currentPage.Page.Select(x => x.ToStockItemDiscardSummary()),
            Pagination = currentPage.Pagination,
        };
    }

    public async Task<StockItemDiscardSummary> ViewStockItemDiscardAsync(int brandId, string storeId, int itemId, int itemDiscardId)
    {
        var discardItem = await readonlyContext.GetOrExceptStockItemDiscardAsync(brandId, storeId, itemId, itemDiscardId);

        return discardItem.ToStockItemDiscardSummary();
    }

    public async Task DeleteStockItemDiscardAsync(int brandId, string storeId, int itemId, int itemDiscardId)
    {
        var discardItem = await dbContext.GetOrExceptStockItemDiscardAsync(brandId, storeId, itemId, itemDiscardId);

        // Delete related stock movements first
        await dbContext.StockMovements
            .Where(sm => sm.ItemDiscardId == itemDiscardId)
            .ExecuteDeleteAsync();

        dbContext.Remove(discardItem);
        await dbContext.SaveChangesAsync();
    }
}
