using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.WebService.Authorization;
using Aura.Manager.Stock;
using AuraCloudInterface;
using AuraCloudInterface.Base;
using AuraCloudInterface.Constants;
using AuraCloudInterface.Patching;
using AuraCloudInterface.Stock.Grv;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using System;
using System.Collections.Generic;
using System.Globalization;
using System.Linq;
using System.Reflection;
using System.Text.Json;
using System.Threading.Tasks;

namespace CloudGatewayApi.Controllers;

[ApiController]
[Route("[controller]")]
[Authorize]
public class StockGrvsController(GrvManager grvManager, UserManager userManager, Aura.Manager.Permissions.PermissionsManager permissionsManager) : AuthenticatedControllerWithUser(userManager: userManager)
{
    [HttpPost("/brands/{brandId}/stores/{storeId}/grvs")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<CreateGrvResponse>> CreateGrv(int brandId, string storeId, CreateGrvRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        return Ok(await grvManager.CreateGrvAsync(brandId, storeId, userId, request));
    }

    [HttpGet("/brands/{brandId}/stores/{storeId}/grvs/{grvId}")]
    [AuthorizedRoles(PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<GrvSummary>> ViewGrv(int brandId, string storeId, int grvId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs);

        return Ok(await grvManager.ViewGrvAsync(brandId, storeId, grvId));
    }

    [HttpGet("/brands/{brandId}/stores/{storeId}/grvs")]
    [AuthorizedRoles(PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<ListResponse<GrvSummary>>> ListGrvs(int brandId, string storeId, [FromQuery] ListGrvArgs args)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs);

        return Ok(await grvManager.ListGrvsAsync(brandId, storeId, args));
    }

    [HttpPut("/brands/{brandId}/stores/{storeId}/grvs/{grvId}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<UpdateResponse>> UpdateGrv(int brandId, string storeId, int grvId, UpdateGrvRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        return Ok(await grvManager.UpdateGrvAsync(brandId, storeId, grvId, userId, request));
    }

    [HttpPut("/brands/{brandId}/stores/{storeId}/grvs/archive")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<UpdateResponse>> ArchiveGrvs(int brandId, string storeId, ArchiveGrvsRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        return Ok(await grvManager.ArchiveGrvsAsync(brandId, storeId, request.GrvIds, userId));
    }

    [HttpPut("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/receive/{receivedDateTimeStr}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<ReceiveGrvResponse>> ReceiveGrv(int brandId, string storeId, string receivedDateTimeStr, int grvId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        var receivedDateTime = DateTime.SpecifyKind(DateTime.ParseExact(receivedDateTimeStr, "yyyyMMddHHmmss", CultureInfo.InvariantCulture), DateTimeKind.Utc);

        return Ok(await grvManager.ReceiveGrvAsync(brandId, storeId, receivedDateTime, grvId, userId));
    }

    [HttpPut("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/return/{returnedDateTimeStr}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<ReturnGrvResponse>> ReturnGrv(int brandId, string storeId, string returnedDateTimeStr, int grvId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        var returnedDateTime = DateTime.SpecifyKind(DateTime.ParseExact(returnedDateTimeStr, "yyyyMMddHHmmss", CultureInfo.InvariantCulture), DateTimeKind.Utc);

        return Ok(await grvManager.ReturnGrvAsync(brandId, storeId, returnedDateTime, grvId, userId));
    }

    [HttpPut("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/lock/{lockedDateTimeStr}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<LockGrvResponse>> LockGrv(int brandId, string storeId, string lockedDateTimeStr, int grvId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        var lockedDateTime = DateTime.SpecifyKind(DateTime.ParseExact(lockedDateTimeStr, "yyyyMMddHHmmss", CultureInfo.InvariantCulture), DateTimeKind.Utc);

        return Ok(await grvManager.LockGrvAsync(brandId, storeId, lockedDateTime, grvId, userId));
    }

    [HttpPost("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/items")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<CreateResponse>> CreateGrvItem(int brandId, string storeId, int grvId, CreateGrvItemRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        return Ok(await grvManager.CreateGrvItemAsync(brandId, storeId, grvId, request));
    }

    [HttpPut("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/items/{grvItemId}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<UpdateResponse>> UpdateGrvItem(int brandId, string storeId, int grvId, int grvItemId, UpdateGrvItemRequest request)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        var userId = await GetAuthenticatedUserIdAsync();

        return Ok(await grvManager.UpdateGrvItemAsync(brandId, storeId, grvId, grvItemId, request, userId));
    }

    [HttpGet("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/items")]
    [AuthorizedRoles(PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<ListResponse<GrvItemSummary>>> ListGrvItems(int brandId, string storeId, int grvId, ListGrvItemArgs args)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs);

        return Ok(await grvManager.ListGrvItemsAsync(brandId, storeId, grvId, args));
    }

    [HttpDelete("/brands/{brandId}/stores/{storeId}/grvs/{grvId}/items/{grvItemId}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult> DeleteGrvItem(int brandId, string storeId, int grvId, int grvItemId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);

        await grvManager.DeleteGrvItemAsync(brandId, storeId, grvId, grvItemId);

        return Ok();
    }

    [HttpGet("/grvs/statuses")]
    [AuthorizedRoles(PortalPermission.CanViewGrvs)]
    public ActionResult<IEnumerable<EntityNameSummary>> GetGrvStatuses()
    {
        return Ok(Enum.GetValues<GrvStatus>().Select(x => new EntityNameSummary
        {
            Id = (int)x,
            Name = x.ToString()
        }));
    }

    [HttpGet("/v1/brands/{brandId:int}/stores/{storeId}/suppliers/{supplierId:int}/mappings")]
    [AuthorizedRoles(PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<SupplierMappingsResponse>> GetSupplierMappings(int brandId, string storeId, int supplierId)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanViewGrvs, PortalPermission.CanManageGrvs);

        return Ok(await grvManager.GetSupplierMappingsAsync(brandId, storeId, supplierId));
    }

    [HttpPatch("/v1/brands/{brandId}/stores/{storeId}/grvs/{grvId}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<PatchResponse>> PatchGrv(int brandId, string storeId, int grvId, [FromBody] PatchRequest patchRequest)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);
        return Ok(await grvManager.PatchGrvAsync(brandId, storeId, grvId, patchRequest));
    }

    [HttpPatch("/v1/brands/{brandId}/stores/{storeId}/grvs/{grvId}/items/{grvItemId}")]
    [AuthorizedRoles(PortalPermission.CanManageGrvs)]
    public async Task<ActionResult<PatchResponse>> PatchGrvItem(int brandId, string storeId, int grvId, int grvItemId, [FromBody] PatchRequest patchRequest)
    {
        await permissionsManager.ValidatePortalUserHasAnyPermissionAsync(AuthenticatedUser.LoginId, brandId, storeId, PortalPermission.CanManageGrvs);
        return Ok(await grvManager.PatchGrvItemAsync(brandId, storeId, grvId, grvItemId, patchRequest));
    }
}

