using System;
using System.Collections.Generic;
using AuraCloudInterface.Base;
using AuraCloudInterface.Stock.Supplier;

namespace AuraCloudInterface.Stock.Grv;

public class GrvSummary : EntitySummary
{
    public string StoreId { get; set; } = string.Empty;
    public DateTime GrvDate { get; set; }
    public string? SupplierReference { get; set; }
    public int GrvNumber { get; set; }    
    public decimal TotalCostIncl { get; set; }
    public string? Notes { get; set; }
    public DateTime StockMovementAt { get; set; }
    public string StockMovementBy { get; set; } = string.Empty;
    public DateTime? LockedAt { get; set; }
    public string? LockedBy { get; set; }
