                CreatedAt = DateTime.UtcNow,
                CreatedByUserId = createdByUserId,
                StockTakeStatusId = (int)StockTakeStatus.Draft,
                Note = null,
                ValuesUpdatedAt = DateTime.UtcNow
            };
        }

        public static void MapTo(this UpdateStockTakeRequest request, StockTake stockTake)
        {
            stockTake.DateTime = request.DateTime;
            stockTake.Note = request.Note;            
        }

        public static GrvSummary ToGrvSummary(this Grv grv, Dictionary<int, string>? userLookup = null)
        {
            return new GrvSummary
            {
                Id = grv.Id,
                StoreId = grv.StoreId,

                GrvDate = grv.GrvDate,
                SupplierReference = grv.SupplierReference,
                GrvNumber = grv.GrvNumber,
                TotalCostIncl = grv.TotalCostIncl,
                Notes = grv.Notes,

                StockMovementAt = grv.StockMovementAt,
                StockMovementBy = userLookup?.GetValueOrDefault(grv.StockMovementByUserId, "Unknown user") ?? string.Empty,
                LockedAt = grv.LockedAt,
                LockedBy = grv.LockedByUserId is > 0 ? userLookup?.GetValueOrDefault(grv.LockedByUserId.Value, "Unknown user") : null,

                Status = (GrvStatus)grv.GrvStatusId,
                PurchaseOrderNumber = grv.PurchaseOrderNumber,                
                TransactionType = (AuraCloudInterface.Stock.Grv.GrvTransactionType)grv.GrvTransactionTypeId,
                Supplier = grv.Supplier?.ToSupplierSummary(),
                ItemCount = grv.GrvItems.Count,

                CashupDataId = grv.CashupDataId,
                CashupDataAmount = grv.CashupDataAmount,

                ParentGrvId = grv.ParentGrvId,
                IsArchived = grv.IsArchived,
                RelatedCreditNoteIds = grv.InverseParentGrv
                    .Where(x => x.GrvTransactionTypeId == 2 || x.GrvTransactionTypeId == 3)
                    .Select(x => x.Id)
                    .ToList(),
                RelatedCreditNotes = (areRelatedSummariesSortedDesc 
                    ? grv.InverseParentGrv
                        .Where(x => x.GrvTransactionTypeId == 2 || x.GrvTransactionTypeId == 3)
                        .OrderByDescending(x => x.GrvDate)
                    : grv.InverseParentGrv
                        .Where(x => x.GrvTransactionTypeId == 2 || x.GrvTransactionTypeId == 3)
                        .OrderBy(x => x.GrvDate))
                    .Select(x => new GrvRelatedSummary
                    {
                        Id = x.Id,
                        GrvNumber = x.GrvNumber,
                        Status = (GrvStatus)x.GrvStatusId,
                        IsArchived = x.IsArchived,
                        TransactionType = (GrvTransactionType)x.GrvTransactionTypeId
                    })
                    .ToList(),
                ParentGrv = grv.ParentGrv != null 
                    ? new GrvRelatedSummary
                    {
                        Id = grv.ParentGrv.Id,
                        GrvNumber = grv.ParentGrv.GrvNumber,
                        Status = (GrvStatus)grv.ParentGrv.GrvStatusId,
                        IsArchived = grv.ParentGrv.IsArchived,
                        TransactionType = null
                    }
                    : null
            };
        }

        public static Grv CreateGrv(this CreateGrvRequest request, string storeId, int grvNumber, int? supplierId, int userId)
        {
            var currentDateTime = DateTime.UtcNow;

            return new Grv
            {
                GrvTransactionTypeId = (int)request.TransactionType,
                ParentGrvId = request.ParentGrvId,
                StoreId = storeId,
                GrvNumber = grvNumber,
                GrvStatusId = (int)GrvStatus.Draft,
                GrvDate = currentDateTime,
                ValuesUpdatedAt = currentDateTime,
                SupplierId = supplierId,                
                StockMovementAt = currentDateTime,
                StockMovementByUserId = userId,
            };
        }

        public static void MapTo(this UpdateGrvRequest request, Grv grv, int userId)
        {
            if (request.TotalCost != grv.TotalCostIncl || request.CashupDataAmount != grv.CashupDataAmount)
            {
                grv.ValuesUpdatedAt = DateTime.UtcNow;
            }

            if (request.StockMovementAt != grv.StockMovementAt)
            {                
                grv.StockMovementByUserId = userId;
            }

            grv.SupplierId = request.SupplierId;
            grv.GrvDate = request.GrvDate;
            grv.SupplierReference = request.SupplierReference;
            grv.TotalCostIncl = request.TotalCost;
            grv.Notes = request.Notes;            
            grv.PurchaseOrderNumber = request.PurchaseOrderNumber;
            grv.CashupDataId = request.CashupDataId;
            grv.CashupDataAmount = request.CashupDataAmount;
            grv.StockMovementAt = request.StockMovementAt;
        }

