        public static StockItemSummary ToSummary(this Item stockItem, string storeId)
        {
            return stockItem.ToSummary(null, storeId);
        }

        public static StockItemSummary ToSummary(this Item stockItem, int? recipeRevisionId, string? storeId)
        {
            var storeOverride = !string.IsNullOrEmpty(storeId)
                ? stockItem.ItemDetailStoreOverrides.FirstOrDefault(a => a.StoreId == storeId && a.ItemId == stockItem.Id)
                : null;

            decimal? storePreppedCost = null;
            if (storeOverride?.BulkCost != null)
            {
                var wastageToUse = storeOverride.WastagePercentage ?? stockItem.WastagePercentage;
                storePreppedCost = storeOverride.BulkCost / (1 - (wastageToUse / 100));
            }

            return new StockItemSummary
            {
                Id = stockItem.Id,
                BrandId = stockItem.BrandId,
                Name = stockItem.Name,
                Description = stockItem.Description,
                UnitOfMeasureId = stockItem.UnitOfMeasureId,
                Code = stockItem.Code,
                IsArchived = stockItem.IsArchived,
                ProcessedStockType = (AuraCloudInterface.Stock.Processed.ProcessedStockType?)stockItem.ProcessedStockTypeId,
                CategoryId = stockItem.CategoryId,
                WastagePercentage = stockItem.WastagePercentage,
                ModelUnpreppedCost = stockItem.ModelUnpreppedCost,
                ModelPreppedCost = stockItem.ModelUnpreppedCost / (1 - (stockItem.WastagePercentage / 100)),
                IsGpitem = stockItem.IsGpitem,
                StoreId = stockItem.StoreId,
                Barcode = stockItem.Barcode,
                StockTypeId = stockItem.StockTypeId,
                StorePreppedCost = storePreppedCost,
                StoreUnpreppedCost = storeOverride?.BulkCost,
                StoreWastagePercentage = storeOverride?.WastagePercentage,
                StoreParLevel = storeOverride?.ParLevel,
                StockListIds = stockItem.StockListItems.Select(x => x.StockListId).ToList(),
                TaxRateIds = stockItem.ItemTaxes.Select(x => x.TaxRateId).ToList(),
                RecipeId = recipeRevisionId.HasValue ? stockItem.RecipeItems.Where(recipe => recipe.ItemId == stockItem.Id && recipe.RecipeRevisionId == recipeRevisionId).Select(x => x.RecipeId).FirstOrDefault() : null,
            };
        }

