using Aura.CloudBusinessLogic;
using Aura.Common;
using Aura.Common.Database.Extensions;
using Aura.Common.Extensions;
using Aura.Database.Context.Stock;
using Aura.Manager.Stock.Extensions;
using AuraCloudInterface.Base;
using AuraCloudInterface.Patching;
using AuraCloudInterface.Stock.Grv;
using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using System.Linq.Expressions;
using System.Net;
using GrvStatus = AuraCloudInterface.Stock.Grv.GrvStatus;

namespace Aura.Manager.Stock;

public class GrvManager(ILogger<GrvManager> logger, StockContext dbContext, ReadOnlyStockContext readonlyContext, BrandManager brandManager, StoreManager storeManager, UserManager userManager, TaxRateManager taxRateManager, SupplierManager supplierManager, StockManager stockManager)
{
    private const decimal DifferenceTolerance = 0.01M;

    public async Task<CreateGrvResponse> CreateGrvAsync(int brandId, string storeId, int userId, CreateGrvRequest request)
    {
        ApiException.ThrowIfTrue(request.TransactionType == AuraCloudInterface.Stock.Grv.GrvTransactionType.ShortDelivered, "GRV transaction type 'ShortDelivered' cannot be created directly.");

        const int maxRetries = 3;
        var retryCount = 0;

        while (retryCount < maxRetries)
        {
            try
            {
                var strategy = dbContext.Database.CreateExecutionStrategy();
                return await strategy.ExecuteAsync(async () =>
                {
                    await using var transaction = await dbContext.Database.BeginTransactionAsync();

                    var grvNumber = (await dbContext.Grvs
                        .Where(x => x.StoreId == storeId && x.GrvTransactionTypeId == (int)request.TransactionType)
                        .MaxAsync(x => (int?)x.GrvNumber) ?? 0) + 1;

                    int? supplierId = null;
                    if (request.ParentGrvId is > 0)
                    {
                        var parentGrv = await ViewGrvAsync(brandId, storeId, request.ParentGrvId.Value);

                        ApiException.ThrowIfTrue(parentGrv.StoreId != storeId, $"Parent GRV with id {request.ParentGrvId} does not belong to store id {storeId}");
                        ApiException.ThrowIfTrue(parentGrv.Status != GrvStatus.Finalized, $"Cannot create a return for a GRV with a status of {parentGrv.Status}");

                        supplierId = parentGrv.Supplier?.Id;
                    }

                    var grv = request.CreateGrv(storeId, grvNumber, supplierId, userId);

                    await dbContext.Grvs.AddAsync(grv);

                    await dbContext.SaveChangesAsync();

                    await transaction.CommitAsync();

                    return new CreateGrvResponse
                    {
                        Id = grv.Id,
                        GrvNumber = grvNumber,
                        GrvDateTime = grv.GrvDate,
                        StockMovementAt = grv.StockMovementAt,
                    };
                });
            }
            catch (DbUpdateException ex) // Catches unique constraint violations
            {
                retryCount++;
                if (retryCount == maxRetries)
                {
                    throw new ApiException(HttpStatusCode.BadRequest, "Unable to generate unique GRV number after multiple attempts");
                }
            }
        }

        throw new ApiException(HttpStatusCode.InternalServerError, "Unexpected error creating GRV"); // Should never reach here
    }


